INSERT INTO `kc_course` VALUES ('11163', '数词', '1021', '1003', '132162', '赵春艳', '0', '本节课主要为你讲解数词的相关知识点，包括数词的定义和分类、基数词的写法和读音、序数词的写法和读音、数词的9大用法以及介绍了含有数词的相关习语等知识内容。课上Kathy老师帮助你清晰地逐条归纳你日常生活中用到的所有关于数词的使用方法，以及基数词和序数词的易混辨析，同时还有一些有意思的习语介绍。同时通过实战演练的方式，帮助你巩固加深课上所学内容。快乐学习英语，快乐初中生活。快来和Kathy老师一起学习吧！&#13;&#10;', '2939', '/data/default/kecheng/6af/46a/vkm8iq9pehpsp0pv8q4492knt41358147560.jpg', '          /data/default/kecheng/1f6/9b3/g5ecqa47c15a6dd3n2gdmqllf61356075235.doc', '0', '', '1358147562', null, '0', '0', '1', null, null, '14', '0', '0', '0', '1037');
