INSERT INTO `kc_course` VALUES ('11382', '初二物理期末复习', '1021', '1004', '24320', '牛颖', '0', '初二同学刚开始学习物理，面对物理第一次期末考试，可能会感到焦虑紧张，不知所措。本节课牛老师将带你一起复习初二上学期物理课程，梳理重要知识点，精选典型题目进行讲解，总结归纳解题技巧，让大家掌握正确的学习方法，轻松面对物理考试，不再紧张，胜券在握。', '3716', '/data/default/kecheng/88d/60d/irutbfdl4cohvria64ndodagq41354684309.jpg', ' /data/default/kecheng/88d/60d/irutbfdl4cohvria64ndodagq41354673873.doc', '0', '', '1354684313', null, '0', '0', '1', null, null, '31', '2', '0', '0', '1038');
