INSERT INTO `kc_course` VALUES ('11428', '决胜中考之说明文阅读', '1021', '1001', '148218', '周可', '0', '新课标对说明文阅读的要求是：阅读说明性文章，能抓住要点，了解文章的基本说明方法。能把握文章的基本观点，获取主要信；阅读科技作品，还应该注意领会作品中所体现的科学精神和科学思想方法。所以本节课我们将带你从梳理说明文知识入手，探究中考题型，明确说明文考查的热门素材和考点，以读懂文本为宗旨，培养你结合相关材料，解决实际问题的能力。', '4097', '/data/default/kecheng/488/d60/nsvt5475cdk3hghaklpgcvlc761358821042.jpg', '     /data/default/kecheng/03f/d8b/9v7m363gjaoan0pjr3j644ni541358753216.doc', '0', '', '1358911020', null, '0', '0', '1', null, null, '7', '0', '0', '0', '1039,1038');
