INSERT INTO `kc_course` VALUES ('11444', '常见的烃（甲烷、乙烯和苯）', '1022', '1005', '10103', '袁朝乐', '0', '烃是学习有机化学的基础部分，重点是烷烃、烯烃、苯和苯的同系物的结构特点，物理性质，化学性质，制备等，最关键的部分是它们的化学性质。课堂上详细介绍了烃的性质，精选例题和习题，力争使同学们在较短时间内掌握有机化合物的学习方法和技巧，并能熟练运用。', '3430', '/data/default/kecheng/9b6/687/6t0lghqladukr3dnoej6r91gd11356690509.png', '  /data/default/kecheng/98a/acb/iqaocdivfmeu1ed0vmbc5nsrg51356757393.doc', '0', '', '1356939812', null, '0', '0', '1', null, null, '3', '0', '0', '0', '1040');
INSERT INTO `kc_course` VALUES ('11445', '烃的含氧衍生物（乙醇和乙酸）', '1022', '1005', '10103', '袁朝乐', '0', '本节课的主要内容是乙醇和乙酸，重点是他们的结构特点、物理性质、化学性质以及它们相互之间的转化关系。课堂上将主要的知识点详细讲解、归纳，以便同学们掌握，并精选例题、习题，尽力使同学们快速理解、记忆并加以运用。', '1393', '/data/default/kecheng/8bf/1e8/2rv9h5qrh4bbt4bvbrbvtbhf851357622528.png', ' /data/default/kecheng/8bf/1e8/2rv9h5qrh4bbt4bvbrbvtbhf851357622552.doc', '0', '', '1357624570', null, '0', '0', '1', null, null, '1', '0', '0', '0', '1040');
