INSERT INTO `kc_course` VALUES ('11452', '力学冲刺（一）', '1022', '1004', '916', '张国', '0', '距离高考越来越近了，大家都在想怎样在较短的时间内提高我们的高考成绩。近几年的高考题不是很难，几乎没有完全不会做的题。但是我们要做的是怎样提高解题正确率和解题速度。因此我们需要回归物理的实质，那就是注重物理情景的分析，灵活运用所学的基本概念和基本规律来提高解题能力。', '3020', '/data/default/kecheng/488/d60/bh6ccb2h07vi5ooqo726mre8201358824472.png', ' /data/default/kecheng/488/d60/bh6ccb2h07vi5ooqo726mre8201358824484.doc', '0', '', '1358827252', null, '0', '0', '1', null, null, '10', '0', '0', '0', '1042');
