INSERT INTO `kc_course` VALUES ('11454', '力学冲刺（二）', '1022', '1004', '916', '张国', '0', '虽然高考临近，但我们不能乱了手脚，正所谓“万变不离其宗”，所以我们要利用冲刺的短暂时间，把解物理题的根本抓住。这节课的根本就是运用动能定理、能量守恒、动量定理和动量守恒等规律解题，这是历年高考的重难点，掌握了这节课，你就可以胸有成竹地迎战高考综合题了！', '3179', '/data/default/kecheng/f3b/c17/vdm2nio77rtkejv0eaavkv8vc61358924818.png', ' /data/default/kecheng/f3b/c17/vdm2nio77rtkejv0eaavkv8vc61358924832.doc', '0', '', '1359016696', null, '0', '0', '1', null, null, '7', '0', '0', '0', '1042');
