INSERT INTO `kc_course_pack` VALUES ('14298', '初二下学期英语课程', '3', '', '', '', '/data/default/kecheng/bbb/61a/ls8i6j8jmc8ggarda5tbopbvo01353668381.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/4ec/590/5mmu34qv638jbjvuehtc8hcb901355897498.png\";i:1;s:70:\"/data/default/kecheng/4ec/590/5mmu34qv638jbjvuehtc8hcb901355897504.png\";i:2;s:70:\"/data/default/kecheng/4ec/590/5mmu34qv638jbjvuehtc8hcb901355897508.png\";}', 'a:1:{i:1003;s:1529:\"<pre><span style=\"font-size: small;\"><strong>&ldquo;迷糊Style&rdquo;的你</strong><strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.对已学过的时态语态、常用词组、固定搭配不会区分，经常混淆不清。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.句子成分、种类分不清楚，读不明白，说不清楚，经常答非所问。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.不能系统归纳所学知识，不知如何为初三做准备，知识储备和信心都不足。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.系统归纳所学易考易错词组，对比记忆，强化练习，熟练掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.全面总结动词重点时态，横向与纵向结合讲解，轻松突破时态难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.深度剖析词类、句子，为填空、完型、写作、阅读等难点铺路，掌握基础才是王道！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong> <br /><img src=\"/data/default/upload/site/54b/530/system/2012/12/18/151je7be3hg9vel43cppuhs4s51355820772.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/18/151je7be3hg9vel43cppuhs4s51355820785.png\" alt=\"\" /></span></pre>\r\n<pre><span style=\"color: #ff0000; font-size: small;\"><br style=\"color: #ff0000; font-size: small;\" /></span></pre>\";}', '140.00', '7', '31104000', '558', '3301', null, '', '1353668164', '1353668164', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初二下学期学生</span></pre>', '<pre><span style=\"font-size: small;\">1.彻底扫清过去、现在、完成、将来四大时态重难点，易考点易错点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.复习形容词、副词，掌握句子种类区分，夯实所学基础知识。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.必考、易考词组易混辨析，归纳总结巧妙记忆，为你阅读、写作储备词汇量。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">稀里糊涂or迎头赶上？看清方向，理清思路，夯实初二，迎接初三，努力从来不晚！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14299', '初二初三物理联报课程', '4', '夯实基础，掌握规律，提高成绩！', '京城名师倾力奉献', '&#60;span style=&#34;font-size: small;&#34;&#62;&#60;br style=&#34;font-size: small;&#34; /&#62;&#60;/span&#62;', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354083114.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354861166.jpg\";i:1;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354861169.jpg\";i:2;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354861172.jpg\";}', 'a:1:{i:1004;s:2110:\"<span style=\"font-size: small;\"><strong>学习初中物理的你，是否存在以下焦虑？</strong></span><br /><span style=\"font-size: small;\">1.觉得物理非常难，找不到好的学习方法，不能取得好成绩。</span><br /><span style=\"font-size: small;\">2.明明课上很认真听讲，觉得也都听懂了，可一到考试就犯怵！</span><br /><span style=\"font-size: small;\">3.对于一些物理概念，老是弄混，感觉理解了，一做选择题就做不对，尤其多选题。</span><br /><span style=\"font-size: small;\">4.遇到具体的问题，不知道怎么应用规律去解题，不知道该用什么公式计算。</span><br /><span style=\"font-size: small;\">5.动手实验的次数比较少，不能真正理解实验题，不会分析现象，不能得出结论。</span><br /><span style=\"font-size: small;\">6.遇到大的计算题，更是一头雾水，不知道如何下手！</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.名师教你学物理的最基本的方法，养成正确的学习习惯。</span><br /><span style=\"font-size: small;\">2.帮你梳理最基本的物理概念与规律，让你理解每个概念的内涵与外延，知道规律的适用条件。</span><br /><span style=\"font-size: small;\">3.精选典型的题目，带你一步步去分析，梳理解题思路，总结解题技巧。</span><br /><span style=\"font-size: small;\">4.对于题干特别长的综合题，教你如何把握题目关键点，分析题目考查哪些知识点，如果选用规律解题。</span><br /><span style=\"font-size: small;\">5.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br />&nbsp;<img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/07/t3u8j6gjtv0np5l0hlmarjvic71354861049.png\" alt=\"\" /><br />&nbsp;<img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/07/t3u8j6gjtv0np5l0hlmarjvic71354861155.png\" alt=\"\" /><br /><br />\";}', '660.00', '33', '62208000', '168', '3301', null, '', '1354069025', '1354069025', '0', '初中各年级学生', '<pre><span style=\"font-size: small;\">学习初中物理的学生</span></pre>', '<span style=\"font-size: small;\">通过本课程的学习，你将在以下几个方面有所突破：</span><br /><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span><br /><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会各类题的解法。</span><br /><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span><br /><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span><br /><span style=\"font-size: small;\">5.了解物理学的实质，掌握正确的学习方法，内化成物理学科能力。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14300', '初三化学全年课程', '4', '重难点各个击破，掌握解题要领！', '高级教师教研员', '&#60;pre&#62;&#60;strong&#62;&#60;span style=&#34;font-size: small;&#34;&#62;学习初三化学全年课程的你：&#60;/span&#62;&#60;/strong&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.对于大型实验题，做题思路混乱，不知从何下手&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.前后知识衔接不连贯，学久了容易忘，巧妙记忆能力差&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;strong&#62;&#60;span style=&#34;font-size: small;&#34;&#62;德智课程帮助你：&#60;/span&#62;&#60;/strong&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.总结规律，科学梳理，把握脉络，有条不紊&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.分析实验装置，制取原理，实验方法，收集方法，轻松突破实验难关&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.提炼关键点，概念巧拆分，知识体系化，轻松灵活掌握&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;strong&#62;&#60;span style=&#34;font-size: small;&#34;&#62;你的学习效果：&#60;/span&#62;&#60;/strong&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;熟练：计算公式，实验步骤，质量守恒定律，性质决定用途思想，金属活动性顺序表等等&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;掌握：列方程思想，关系式法，化学方程式计算步骤，溶液稀释的计算，十字交叉法等等&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;strong&#62;&#60;span style=&#34;font-size: small;&#34;&#62;你的精彩课堂：&#60;/span&#62;&#60;/strong&#62;&#60;/pre&#62;&#13;&#10;&#60;img src=&#34;/data/default/upload/site/cb2/c20/2414/2012/11/30/h25k0i6ao6pr2clj05lhrjcne11354270941.jpg&#34; alt=&#34;&#34; /&#62;&#60;img src=&#34;/data/default/upload/site/cb2/c20/2414/2012/11/30/h25k0i6ao6pr2clj05lhrjcne11354271002.jpg&#34; alt=&#34;&#34; /&#62;', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354083137.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/c1f/803/945el44e0qrjiei7ilfcd7kv201355476977.jpg\";i:1;s:70:\"/data/default/kecheng/c1f/803/945el44e0qrjiei7ilfcd7kv201355477024.jpg\";i:2;s:70:\"/data/default/kecheng/c1f/803/945el44e0qrjiei7ilfcd7kv201355477049.jpg\";}', 'a:1:{i:1005;s:1289:\"<pre><strong><span style=\"font-size: small;\">学习初三化学全年课程的你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对于大型实验题，做题思路混乱，不知从何下手。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.前后知识衔接不连贯，学久了容易忘，巧妙记忆能力差。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">德智课程帮助你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.总结规律，科学梳理，把握脉络，有条不紊。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.分析实验装置，制取原理，实验方法，收集方法，轻松突破实验难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.提炼关键点，概念巧拆分，知识体系化，轻松灵活掌握。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/11/30/h25k0i6ao6pr2clj05lhrjcne11354270941.jpg\" alt=\"\" />&nbsp;<img src=\"/data/default/upload/site/54b/530/system/2012/12/19/cjepsnsvrv9ir3uohbg1u4shc41355893884.jpg\" alt=\"\" />\";}', '400.00', '20', '31104000', '233', '3301', null, '', '1354069764', '1354069764', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初三学生</span></pre>', '<span style=\"font-size: small;\">1.能够熟练运用计算公式、质量守恒定律、金属活动性顺序表等，解决常见的化学问题。</span><br /><span style=\"font-size: small;\">2.掌握关系式法、十字交叉法、守恒法等解题技巧，轻松快捷解决化学计算。</span><br /><span style=\"font-size: small;\">3.归纳总结重难点知识，揭秘易错点，培养逻辑思维能力，轻松学习化学！</span><br /><span style=\"font-size: small;\">4.综合整理，形成系统的知识网络，搞清联系，全面掌握。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14301', '初一初二初三语文联报课程', '4', '提升语文素养，掌握技巧冲刺中考', '状元名师通力合作', '&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;处于初中学习阶段的你：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;知道初中阶段语文的学习目标吗？提分？中考？对，但绝不仅于此。初中语文学习，还有很重要的一个方面：积累语文素养。那么语文素养有什么用？语文素养不仅仅是知识、能力的学习与运用，更是人个性品质、道德修养、审美情趣的综合体现。所以德智课程在立足帮你应对考试的基础上，更加注重初中阶段的你，在语文素养方面的提高。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;我们的课程特色：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;1.&#60;/strong&#62;&#60;strong&#62;课程体系：&#60;/strong&#62;针对初中阶段语文学习的特点，我们为你精心打造了&#38;ldquo;腹有诗书气自华&#38;rdquo;系列专题课程。按照初中三年你的成长体验和认知规律，分为&#38;ldquo;成长篇&#38;rdquo;&#38;ldquo;生活篇&#38;rdquo;&#38;ldquo;认识篇&#38;rdquo;&#38;ldquo;思想篇&#38;rdquo;等。初一初二注重素养提升，初三注重应试技巧。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;2.&#60;/strong&#62;&#60;strong&#62;课程内容：&#60;/strong&#62;每个学期一个大的专题，下面的每一节课都围绕大的专题设置课程。分别从阅读、写作、认识体验方面进行指导：阅读与写作，教你应付语文考试的两大重点内容；认识体验，助你语文学习更上层楼，这正是对语文素养的提升。&#38;nbsp;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;你的学习收获：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.学会文章的阅读技巧和答题方法，中考阅读不失分。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.掌握作文素材积累的方法，并学会作文的审题立意，开头结尾，写就高分作文。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.通过老师对专题内容的阐释，在认识自我、认识世界、感受亲情、哲理思考中，获取新的人生体验。&#60;/span&#62;&#60;/pre&#62;', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354083163.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1b2/f16/96cm6i44egh20477q8re5k53951357352487.jpg\";i:1;s:70:\"/data/default/kecheng/1b2/f16/96cm6i44egh20477q8re5k53951357352500.jpg\";i:2;s:70:\"/data/default/kecheng/1b2/f16/96cm6i44egh20477q8re5k53951357352505.jpg\";}', 'a:1:{i:1001;s:2173:\"<pre><span style=\"font-size: small;\"><strong>处于初中学习阶段的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">知道初中阶段语文的学习目标吗？提分？中考？对，但绝不仅于此。初中语文学习，还有很重要的一个方面：积累语文素养。那么语文素养有什么用？语文素养不仅仅是知识、能力的学习与运用，更是人个性品质、道德修养、审美情趣的综合体现。所以德智课程在立足帮你应对考试的基础上，更加注重初中阶段的你，在语文素养方面的提高。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.课程体系：针对初中阶段语文学习的特点，我们为你精心打造了&ldquo;腹有诗书气自华&rdquo;系列专题课程。按照初中三年你的成长体验和认知规律，分为&ldquo;成长篇&rdquo;&ldquo;生活篇&rdquo;&ldquo;认识篇&rdquo;&ldquo;思想篇&rdquo;等。初一初二注重素养提升，初三注重应试技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.课程内容：每个学期一个大的专题，下面的每一节课都围绕大的专题设置课程。分别从阅读、写作、认识体验方面进行指导：阅读与写作，教你应付语文考试的两大重点内容；认识体验，助你语文学习更上层楼，这正是对语文素养的提升。&nbsp;</span></pre>\r\n<pre><span style=\"font-size: small;\"><span style=\"font-size: x-small;\"><strong>你的精彩课堂：</strong></span></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357352158.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357352172.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357352183.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357352195.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357352205.jpg\" alt=\"\" />\";}', '900.00', '45', '93312000', '217', '3301', null, '', '1354070467', '1354070467', '1', '', '<pre><span style=\"font-size: small;\">全国各版本初一初二初三年级学生</span></pre>', '<pre><span style=\"font-size: small;\">1.学会文章的阅读技巧和答题方法，中考阅读不失分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握作文素材积累的方法，并学会作文的审题立意，开头结尾，写就高分作文。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.通过老师对专题内容的阐释，在认识自我、认识世界、感受亲情、哲理思考中，获取新的人生体验。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14302', '初一初二初三英语联报课程', '4', '攻克重要语法点，掌握写作要领！', '', '', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354083182.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631356000035.png\";i:1;s:70:\"/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631356000037.png\";i:2;s:70:\"/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631356000039.png\";}', 'a:1:{i:1003;s:2588:\"<strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">学习初中英语的你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.科目变多，课本变厚，难度加深，很难适应初中的学习生活。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.学习自主性差，上课注意力不集中，很难独立完成课后作业。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.成绩忽高忽低，上课开始听不懂、跟不上，学习站在&ldquo;分水岭&rdquo;？</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4.面多&ldquo;三多一少&rdquo;即课程多、作业多、考试多、休息少，感觉时间越来越不够用。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">5.希望全面掌握单词、语法、写作、阅读，为高中打下夯实的基础。</span><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">德智课程帮助你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">初一：衔接小升初，快速进入角色，适应初中的学习方法。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">初二：掌握必考时态语态、词组搭配、写作阅读等核心知识。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">初三：整合初一初二，学习初三新知识，同时全面复习，全力迎接中考。</span><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small; color: #ff0000;\">三年规划，帮助你稳步提升英语成绩。想进入理想的高中就从现在开始吧！</span></strong><br /><span style=\"font-size: small; color: #ff0000; font-family: arial,helvetica,sans-serif;\"><strong><span style=\"color: #000000;\">你的精彩课堂</span></strong></span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355999798.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355999808.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355999816.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355999823.png\" alt=\"\" /></span>\";}', '760.00', '38', '93312000', '336', '3301', null, '', '1354074058', '1354074058', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初中学生</span></pre>', '<pre><span style=\"font-size: small;\">1.心态：快速适应初中学习方法，培养英语学习兴趣，打下夯实的英语基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.知识：掌握初中8种词性、三大从句以及所有时态语态等核心语法点。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.题型：单选、完型、写作、阅读几大关键题型的解题技巧和方法。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.综合：词汇、语法、阅读、写作融会贯通，把知识越学越活。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14303', '初一初二初三数学联报课程', '4', '梳理知识、掌握方法、高效解题！', '高级教师全程引导', '', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354083203.jpg', null, null, '1220.00', '61', '93312000', '471', '3301', null, '', '1354081360', '1354081360', '1', null, null, null, '3701');
INSERT INTO `kc_course_pack` VALUES ('14304', '初一全年三科联报课程', '4', '夯实基础，掌握方法，赢在起跑线', '高级教师专家团队', '初一全年三科联报课程，包括初一数学、语文、英语三个学科，共计53个课时的课程。所有课程均由京城名师倾力为您打造，课程定位于同步提升 性质。由名师带您同步复习所学课程，总结解题技巧、解题规律，让您掌握学习方法，形成学科能力，轻松应对学期的各种考试！', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354092960.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354849412.jpg\";i:1;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354849416.jpg\";i:2;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354849419.jpg\";}', 'a:3:{i:1001;s:3840:\"<span style=\"font-size: small;\"><strong>刚升入初一的你们</strong></span><br /><span style=\"font-size: small;\">是不是还沉浸在小学语文学习的内容？是不是对于初中的语文学习一头雾水、茫然无措？是不是渴望找到一种方法能够打牢初中语文学习基础、实现小学到初中的课程衔接？是不是希望摆脱小学灌输式的枯燥无味的学习方式，渴望养成良好的学习习惯？是不是想继续让你的语文学习领先起跑？收获更多的人生感悟？<br /><strong>我们的课程特色</strong></span><br /><span style=\"font-size: small;\">初一上学期的语文课程宗旨是：立足基础，科学提升，培养能力。</span><br /><span style=\"font-size: small;\">所以上下学期，我们都以专题的形式，上学期精心为你们打造了&ldquo;腹有诗书气自华&mdash;&mdash;成长&rdquo;系列课程。每节课都是一个独立的专题，每个专题之间又围绕着&ldquo;成长&rdquo;主题进行设置。课堂所选文章皆与主题相关，在基础知识、阅读方法、写作技巧等方面予以指导。而且&ldquo;成长&rdquo;这个主题恰好与刚刚升入初中的你们密切相关。生活中，面对父母的呵护、老去的亲情和感伤的别离&hellip;&hellip;你的心是不是也一起成长了？</span><br /><span style=\"font-size: small;\">下学期设计了&ldquo;腹有诗书气自华&mdash;&mdash;人情练达篇&rdquo;系列课程。前六节课分别围绕&ldquo;母爱&rdquo;&ldquo;父爱&rdquo;&ldquo;祖孙情&rdquo;等情感专题设置课程，每节课分为&ldquo;美文赏读&rdquo;&ldquo;中考链接&rdquo;&ldquo;写作训练&rdquo;三个环节，分别从情感体验、阅读技巧、写作方法三个角度予以指导。阅读与写作，仍然作为你语文学习的重点，但是对生活的中真情实感的体悟，也是你必备的&ldquo;人生课堂&rdquo;噢。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.继续帮你打好语文学习基础。</span><br /><span style=\"font-size: small;\">2.跨越小升初学习障碍。</span><br /><span style=\"font-size: small;\">3.阅读、写作，双管齐下，语文成绩迅速提高。</span><br /><span style=\"font-size: small;\">4.摆脱灌输式学习，培养自主学习能力。</span><br /><span style=\"font-size: small;\">5.结合最新中考试题，从命题角度教你扫清现代文阅读障碍，提前与中考&ldquo;面对面&rdquo;。</span><br /><span style=\"font-size: small;\">6.每节课都从不同的角度给你写作的技巧，整个课程结束，写作技巧全面覆盖，应付考试作文，绰绰有余。</span><br /><span style=\"font-size: small;\">7.带你感受亲情，这是我们本次系列课程要给你的另外一项重点内容。学会体察父母之爱、祖孙之爱、师生之爱，甚至是人间大爱，这样你才会有一颗感恩的心。<br /></span><span style=\"font-size: small;\"><strong>你的学习效果</strong></span><br /><span style=\"font-size: small;\">1.扎实基础，掌握阅读和写作技巧，初中语文领先起跑。</span><br /><span style=\"font-size: small;\">2.养成良好的学习和阅读习惯，学会自主学习。</span><br /><span style=\"font-size: small;\">3.懂得思考成长过程中遇到的各种问题，结合自身的成长经历，获得新的人生体验。</span><br /><span style=\"font-size: small;\">4.明确中考现代文阅读的考查角度，针对不同角度，学会每个角度的应答策略。</span><br /><span style=\"font-size: small;\">5.学会高分作文的写作技巧，养成从生活中积累素材的好习惯。</span><br /><span style=\"font-size: small;\">6.懂得感恩亲情之爱，并开始懂得回报。</span>\";i:1002;s:3418:\"<pre><span style=\"font-size: small;\"><strong>学习初一的你</strong></span><br /><span style=\"font-size: small;\">1.是不是明明那么简单的计算题，还是经常出现小错误？掉数字、掉字母、去括号不变号&hellip;&hellip;</span><br /><span style=\"font-size: small;\">2.是不是看到应用题就犯怵，我该设什么？我怎么列等式？</span><br /><span style=\"font-size: small;\">3.是不是看到几何语言就犯晕，它说的到底是什么意思？看不懂啊有木有！</span><br /><span style=\"font-size: small;\">4.平行线的性质与判定，不能灵活贯通运用 ，证明过程陷入僵局。</span><br /><span style=\"font-size: small;\">5.解二元一次方程组，不能选择合适的方法解答，增加计算过程，导致出错。</span><br /><span style=\"font-size: small;\">6.解字母系数的不等式，不能正确解答，没有分类讨论意识。</span><br /><span style=\"font-size: small;\">7.解不等式组，无法正确判断解集。<br /></span><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.计算题一步一步细致讲解，指出计算的出错点。教你理解和熟记运算法则，不仅仅会用，</span><span style=\"font-size: small;\">还知道如何用！</span><br /><span style=\"font-size: small;\">2.大段文字找关键词，教你如何找到题中的数量关系，用什么建立相等的条件，加强你的建模思想的认识！</span><br /><span style=\"font-size: small;\">3.反复进行&ldquo;几何模型&rarr;图形&rarr;文字&rarr;符号&rdquo;的练习，让你对几何语言不在陌生！</span><br /><span style=\"font-size: small;\">4.知识再次讲解配合典型例题，帮助你消化性质与判定，做到融会贯通。</span><br /><span style=\"font-size: small;\">5.&ldquo;观察法&rdquo;教你面对二元一次方程组，如何针对性选择解法，简化计算过程，提高正确率。</span><br /><span style=\"font-size: small;\">6.对含有字母系数的不等式，例题由简单到复杂，教会你求解原理的基础上，进一步提高你的变通能力。</span><br />&nbsp;<span style=\"font-size: small;\"><strong>学习效果</strong></span><br /><span style=\"font-size: small;\">1.重点知识的再次学习，加深理解与记忆！</span><br /><span style=\"font-size: small;\">2.对运算法则更加熟练的运用，做计算题更简单，更准确！</span><br /><span style=\"font-size: small;\">3.初步形成几何语言的组织运用和理解能力，为之后的几何学习打下坚实基础！</span><br /><span style=\"font-size: small;\">4.方程思想，分类讨论思想等数学重要思想的入门学习！</span><br /><span style=\"font-size: small;\">5.熟练掌握平行线的性质与判定。</span><br /><span style=\"font-size: small;\">6.熟练掌握导出新方程法，设比例系数法，换元法，整体法等解决二元一次方程组。</span><br /><span style=\"font-size: small;\">7.体会消元、转化思想，分类讨论思想，整体思想在解题中的运用。<br /><strong>你的精彩课堂</strong><br /> <img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354690992.png\" alt=\"\" /><br /> <img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354691045.png\" alt=\"\" /><br /></span></pre>\";i:1003;s:2916:\"<span style=\"font-size: small;\"><strong>刚踏入初中的你</strong></span><br /><span style=\"font-size: small;\">1.科目变多，课本变厚，难度加深，很难适应初中的学习生活。</span><br /><span style=\"font-size: small;\">2.句式语法模糊，成绩不理想，逐渐失去新鲜感。</span><br /><span style=\"font-size: small;\">3.学习自主性差，上课注意力不集中，很难独立完成课后作业。</span><br /><span style=\"font-size: small;\">4.学习兴趣不高，不喜欢大声朗读，单词忘的快。</span><br /><span style=\"font-size: small;\">5.课前不会预习，课后不会做题，课业负担重，常常手忙脚乱。</span><br /><span style=\"font-size: small;\">6.写作成为老大难问题，不是有口说不出，就是肚子里没词儿。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.衔接初升高，快速进入角色，适应初中的学习方法。</span><br /><span style=\"font-size: small;\">2.讲练结合，课堂互动性强，轻松提高你学习英语的兴趣。</span><br /><span style=\"font-size: small;\">3.培养良好学习习惯，形成正确的学习方法，为以后的学习打下夯实的基础。</span><br /><span style=\"font-size: small;\">4.从不同词性展开讲解，逐一分析，扩充你的词汇量。</span><br /><span style=\"font-size: small;\">5.学习整理写作思路，收集写作素材，建立正确的写作方法。</span><br /><span style=\"font-size: small;\">6.课上吸收课上消化，课后强化训练，全面提升综合能力。</span><br /><span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">1.培养学习兴趣，养成良好的学习习惯，打好初中英语基础。</span><br /><span style=\"font-size: small;\">2.轻松掌握比较级、进行时、there be、疑问句以及名词代词的重难点知识。</span><br /><span style=\"font-size: small;\">3.增加词汇储备量，课上消化吸收所学内容，课后独立完成作业。</span><br /><span style=\"font-size: small;\">4.吃透动词的相关知识点，掌握最基本的动词才能为以后学习句子打下基础。</span><br /><span style=\"font-size: small;\">5.熟练掌握数词、介词、副词等词类使用方法， 能够区分、记忆、运用。</span><br /><span style=\"font-size: small;\">6.学会日常用语写作，掌握度假与学校生活等话题的写作方法。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br />&nbsp; <img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354691107.png\" alt=\"\" width=\"483\" height=\"355\" /><br />&nbsp; <img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354691138.png\" alt=\"\" width=\"483\" height=\"354\" />\";}', '1080.00', '54', '31104000', '1058', '3301', null, '', '1354092976', '1354092976', '1', '', '<span style=\"font-size: small;\">新初一的学生</span>', '<span style=\"font-size: small;\">1.完美跨越小升初，养成中学的学习方法，高效学习。</span><br /><span style=\"font-size: small;\">2.初步形成几何语言的组织运用和理解能力，为之后的几何学习打下坚实基础！</span><br /><span style=\"font-size: small;\">3.熟练掌握导出新方程法，设比例系数法，换元法，整体法等解决二元一次方程组。</span><br /><span style=\"font-size: small;\">4.攻克英语语法的重点与难点，学会交际语言。</span><br /><span style=\"font-size: small;\">5.养成良好的学习和阅读习惯，学会自主学习。</span><br /><span style=\"font-size: small;\">6.懂得思考成长过程中遇到的各种问题，结合自身的成长经历，获得新的人生体验。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14305', '高中化学选修3', '1', '详细分析轻松掌握物质结构与性质', '', '', '/data/default/kecheng/e08/988/2gklefh4qgopb4sunvgcbialb11354096811.jpg', 'a:0:{}', 'a:1:{i:1005;s:1174:\"<span style=\"font-size: small;\"><strong>学习选修3的你</strong></span><br /><span style=\"font-size: small;\">1.新概念、理论理解不透彻，导致做题常出现错误。</span><br /><span style=\"font-size: small;\">2.不能清楚掌握重要物质的结构、性质，导致常陷入误区。</span><br /><span style=\"font-size: small;\">3.对于常见晶体的类型、结构不熟悉，相关计算也不清楚明白。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.深入剖析概念、理论，并配以相关试题详细讲解，确保举一反三，触类旁通。</span><br /><span style=\"font-size: small;\">2.归纳整理常见物质的结构、性质，形成知识网络，揭秘易错点，学习效果显著。</span><br /><span style=\"font-size: small;\">3.总结常见晶体的类型、结构特征，配以相关结构图清楚分析，使计算轻松、快捷。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/11/o33e33ma2tf5h73rm6nsrfcrt51355191572.jpg\" alt=\"\" />\";}', '60.00', '3', '31104000', '199', '3302', null, '', '1354093506', '1354093506', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高二理科学生</span></pre>', '<span style=\"font-size: small;\">熟练：核外电子排布规律，等电子原理，电负性和电离能规律，分子的性质等等。</span><br /><span style=\"font-size: small;\">掌握：泡利原理，洪特规则，常见晶体类型结构，十字交叉法，杂化轨道理论等等。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14306', '初二全年四科联报课程', '4', '科科俱到 全年提升 胜券在握', '高级教师骨干力量', '&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;1.&#60;/strong&#62;&#60;strong&#62;学习初二的你&#60;/strong&#62;&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;现在学习很吃力有没有？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;语文阅读终于能看懂了，但是不会答题有没有？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;好不容易弄明白&#38;ldquo;现在进行时&#38;rdquo;，但是&#38;ldquo;将来时&#38;rdquo;，&#38;ldquo;过去时&#38;rdquo;全来了，分不清了有没有？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;好不容易都理解那些定理，公式了，看到因式分解，全等证明还是想抓狂有没有？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;突然学起了物理，完全不知所云有没有？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#38;nbsp;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;2.&#60;/strong&#62;&#60;strong&#62;德智课程帮助你&#60;/strong&#62;&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;语文：课程结合2012年最新的中考文章进行分析，以趣味故事引出话题，主要从阅读和写作两个角度着手，读写并举，帮助大家提高阅读水平和写作能力。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;英语：重难点知识庖丁解牛，逐句分析般讲解；时态语态对比讲解，例句真题配套练习。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;数学：知识，定理深探究，加深理解与记忆；各大公式对比分析，总结不同与规律。复杂图形简单化，提炼模型，引出证明思路。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;物理：详细梳理了初二物理的考点、重点、难点、易错点，逐一讲解，重点突破，配以不同难度的典型例题，让大家学会如何分析题目，如何正确解答，掌握解题技巧。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#38;nbsp;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;3.&#60;/strong&#62;&#60;strong&#62;学习效果&#60;/strong&#62;&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（1）在这里，你将学会如何分析文章，如何体会作者情感，轻松把握答题技巧和阅读规律&#38;hellip;&#38;hellip;而这些，都是语文获取高分的必备法宝。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（2）积累一定的解题技巧，在考试中能快速解题，提高准确率。掌握正确的学习方法，内化成物理学科能力。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（3）开拓数学思维，例题的细致讲解带你逐步理解解题思路，教你如何思考问题，初步锻炼数学思维，同时一道题多种解法，解救你的思维局限！&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#38;nbsp;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;4.&#60;/strong&#62;&#60;strong&#62;你的精彩课堂&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;p&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354270731.jpg&#34; alt=&#34;&#34; /&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354270746.jpg&#34; alt=&#34;&#34; width=&#34;470&#34; height=&#34;358&#34; /&#62;&#60;/p&#62;&#13;&#10;&#60;p&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354270764.jpg&#34; alt=&#34;&#34; /&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354271342.jpg&#34; alt=&#34;&#34; width=&#34;467&#34; height=&#34;320&#34; /&#62;&#60;/p&#62;', '/data/default/kecheng/e08/988/q41dtisudpj1fbj29lnk6msnf31354095744.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354848695.jpg\";i:1;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354848698.jpg\";i:2;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354848700.jpg\";}', 'a:4:{i:1001;s:2384:\"<span style=\"font-size: small;\"><strong>升入初二的你们</strong></span><br /><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 是不是希望拓展语文知识储备，提高语文素养？是不是希望摆脱枯燥无味的语文学习，渴望在语文考试中独占鳌头？是不是想了解关于说明文的学习方法与技巧？</span><br /><span style=\"font-size: small;\"><strong>我们的课程特色</strong></span><br /><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 初二的语文学习，重点仍然是阅读与写作。文体主要集中在写人、叙事类文章上。我们以专题的形式，精心为同学们打造了&ldquo;阅读经典、认识世界&rdquo;系列课程。每节课都是一个独立的专题，但每个专题之间又有着内在的联系。课程结合2012年最新的中考文章进行分析，每节课都分为&ldquo;趣味故事&rdquo;&ldquo;美文赏读&rdquo;&ldquo;写作训练&rdquo;&ldquo;好书推荐&rdquo;四个部分，以趣味故事引出话题，主要从阅读和写作两个角度着手，读写并举，帮助大家提高阅读水平和写作能力。</span><br /><span style=\"font-size: small;\"><strong>帮你解决的问题</strong></span><br /><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 阅读其实是与作者的对话。所以在阅读过程中，大家往往会觉得&ldquo;阅读很容易，答题很困难&rdquo;，这是因为你没有真正理解作者的&ldquo;说话&rdquo;意图和文章的考查角度，缺少熟练、规范的答题方法。写起文章，也觉得无从下笔，不知如何选材、立意、构思。平时看了很多书，积累了很多素材，却又苦于不懂得何时运用、如何运用。这些，我们都将在课堂上给你。</span><br /><span style=\"font-size: small;\"><strong>你的收获</strong></span><br /><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 在这里，你将学会如何分析文章，如何体会作者情感，轻松把握答题技巧和阅读规律&hellip;&hellip;而这些，都是语文获取高分的必备法宝。</span><br /><span style=\"font-size: small;\">在这里，你将从阅读中认识自我，认识自然，认识生活，认识世界&hellip;&hellip;从四季更迭与自然变幻中发现生命的可贵与生活的惬意，懂得热爱生命，尊重自然，从而陶冶情操，增长智慧！</span>\";i:1002;s:3262:\"<span style=\"font-size: small;\"><strong>学习初二数学的你</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.证明全等，找条件是否经常遇到瓶颈？</span><br /><span style=\"font-size: small;\">2.证明过程如何写，你是否书写规范？条理是否清晰？</span><br /><span style=\"font-size: small;\">3.函数&mdash;&mdash;概念是否理解？应用题如何寻找等式关系？</span><br /><span style=\"font-size: small;\">4.反比例函数的独特性质你是否熟练掌握？</span><br /><span style=\"font-size: small;\">5.四边形的角，线段，面积求值问题是否是你一大难题？</span><br /><span style=\"font-size: small;\">6.四边形问题中，如何巧妙做辅助线，你是否有所了解？</span><br /><span style=\"font-size: small;\">7.四边形中存在哪些基本图形，能助你快速解题？</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.探索研究能证明全等的方法，对全等条件进行一个深刻了解，同时培养从条件中找隐</span><span style=\"font-size: small;\">含条件的能力。</span><br /><span style=\"font-size: small;\">2.证明推理逻辑强，推理表达需精准，由简单例题体会证明思路与格式，逐渐增加题目</span><span style=\"font-size: small;\">复杂程度，循序渐进过程。</span><br /><span style=\"font-size: small;\">3. 简单例子，带你领会从抽象到具体的转化过程，理解函数定义的变化与对应关系。</span><br /><span style=\"font-size: small;\">4.反比例函数的深层剖析，挖掘反比例函数的特性，典型例题辅助理解。</span><br /><span style=\"font-size: small;\">5.总结归纳基本图形，教你如何添加辅助线，化未知为已知。</span><br /><span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">1.查漏补缺，找出知识漏洞，熟悉知识考点、攻破知识难点。</span><br /><span style=\"font-size: small;\">2.温故知新，对所学知识巩固复习，加深理解与记忆</span><br /><span style=\"font-size: small;\">3.知识拓展，学习课堂之外的东西，对知识进行一个丰富过程。</span><br /><span style=\"font-size: small;\">4.开拓数学思维，例题的细致讲解带你逐步理解解题思路，教你如何思考问题，初步锻炼数学思维，同时一道题多种解法，解救你的思维局限！</span><br /><span style=\"font-size: small;\">5.熟练掌握：分式的有关概念，易错点；反比例函数的性质；分清四边形、矩形、正方形、菱形的区别于联系。</span><br /><span style=\"font-size: small;\">6.帮你寻找添加辅助线的思路，而不是凭着自己的感觉去胡乱添加。</span><br /><span style=\"font-size: small;\">7.熟练掌握基本图形模型。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692297.png\" alt=\"\" /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692316.png\" alt=\"\" width=\"509\" height=\"344\" />\";i:1003;s:1530:\"<span style=\"font-size: small;\"><strong>你，具有&ldquo;初二现象\"吗？</strong></span><br /><span style=\"font-size: small;\">1.成绩忽高忽低，上课开始听不懂、跟不上，学习站在&ldquo;分水岭&ldquo;？</span><br /><span style=\"font-size: small;\">2.单词难度，语法深度，写作高度陡然徒增，成绩进入&ldquo;爬坡期&ldquo;？</span><br /><span style=\"font-size: small;\">3.时态语态易混易错，词性词法区分不清，写作常丢分？</span><br /><span style=\"font-size: small;\"><strong>面对困扰，我们需要这样的英语课</strong></span><br /><span style=\"font-size: small;\">1.不想学&mdash;课堂幽默精彩，环节设置多种多样，抓住你全部的注意力!</span><br /><span style=\"font-size: small;\">2.学不会&mdash;重难点知识庖丁解牛，逐句分析般讲解，摒弃死记硬背句型结构！</span><br /><span style=\"font-size: small;\">3.分不清&mdash;时态语态对比讲解，例句真题配套练习，彻底吃透语法难点！</span><br /><span style=\"font-size: small;\">4.记不住&mdash;词汇句型拓展记忆，优美篇章课上赏析，语境学习增强联想记忆！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692222.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692252.png\" alt=\"\" /><br /></strong><strong></strong></span>\";i:1004;s:2300:\"<span style=\"font-size: small;\"><strong>学习初二物理的你，是否存在以下焦虑？</strong></span><br /><span style=\"font-size: small;\">1.在课堂上听不懂老师所讲的概念与规律，不知道如何学物理；</span><br /><span style=\"font-size: small;\">2.虽然在课堂上听懂了，但是一到做题就眼高手低，无法正确解答！</span><br /><span style=\"font-size: small;\">3.你平时掌握的并不错，但是考试仍旧无法轻松拿高分，一到考试就焦虑！</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.弄清最基本的物理概念与规律，教会你正确的学习方法。</span><br /><span style=\"font-size: small;\">2.老师带领大家一起分析题目，理清思路，让你看到题目不再迷惑。</span><br /><span style=\"font-size: small;\">3.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span><br /><span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">学习本课程前，请先下载学案，仔细看一遍，这样学习效果更好。通过本课程的学习，你将在以下几个方面有所突破：</span><br /><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span><br /><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会一类题的解法。</span><br /><span style=\"font-size: small;\">3.积累一定的解题技巧，在考试中能快速解题，提高准确率。</span><br /><span style=\"font-size: small;\">4.掌握正确的学习方法，内化成物理学科能力。</span><br /><span style=\"font-size: small;\">5.轻松解决声学、光学、热学、力学板块的知识，学会如何应用这些知识解题，形成正确的解题思路，掌握规范的答题步骤。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692135.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692156.png\" alt=\"\" /></strong><strong></strong></span>\";}', '1480.00', '74', '31104000', '805', '3301', null, '', '1354095755', '1354095755', '1', '', '<span style=\"font-size: small;\">主要适用于初二年级学生，也可以供初三学生复习使用</span>', '<span style=\"font-size: small;\">1.提高语文素养，掌握高效阅读方法，训练规范答题技巧。</span><br /><span style=\"font-size: small;\">2.攻克重要的语法点，掌握语言的实际应用。</span><br /><span style=\"font-size: small;\">3.细致复习初二数学重难点，各个击破，掌握解题思路，学会分析问题。</span><br /><span style=\"font-size: small;\">4.重点解决力学板块综合题的思路分析与规范解题，同时光学、声学、热学知识更加熟练。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14307', '初三全年五科联报课程', '4', '重点难点易错点一网打尽！', '高级教师精英团队', '&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;1.&#60;/strong&#62;&#60;strong&#62;学习初三的你：&#60;/strong&#62;&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;是不是学过的知识如散沙，老师课上一讲就会，考场做题一做就错！&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;是不是看到化学实验就自乱阵脚，不知如何下手？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;是不是单选填空语法题丢分易，阅读写作综合题提分难？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;是不是对议论文有&#38;ldquo;谈虎色变&#38;rdquo;之感？是不是觉得读起来好深，写起来好难？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;是不是圆的所有定理推论都知道，但是做起题来还是要绞尽脑汁？&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#38;nbsp;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;2.&#60;/strong&#62;&#60;strong&#62;德智课程帮助你：&#60;/strong&#62;&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（1）分析实验装置，制取原理，实验方法，收集方法，快速解决实验难题&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（2）最新议论文真题，让你与中考零距离。教给你如何读懂议论文，如何在读懂的基础上拿高分。写作方面，我们结合中考满分作文，从审题、选材、立意等方面入手，教你轻松写出满分议论文。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（3）熟悉探索法的推理过程，熟练地用综合法进行证明，进一步提高同学们的思维能力和逻辑推理能力。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（4）掌握重难语法点的学习与运用，提升写作阅读等英语综合成绩！&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;3.&#60;/strong&#62;&#60;strong&#62;课程学习效果：&#60;/strong&#62;&#60;strong&#62;&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（1）熟练：计算公式，实验步骤，质量守恒定律，性质决定用途思想，金属活动性顺序表等等&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（2）摸清自己学习的薄弱点，培优补差，为中考复习打定基础！&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（3）逐步培养中考应试能力，学习应试技巧，合理规划时间！&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;（4）学会议论文如何审题、立意，如何积累素材，写就中考满分作文。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;4.&#60;/strong&#62;&#60;strong&#62;你的精彩课堂：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;p&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354274680.jpg&#34; alt=&#34;&#34; /&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354274693.jpg&#34; alt=&#34;&#34; /&#62;&#60;/p&#62;&#13;&#10;&#60;p&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354274724.jpg&#34; alt=&#34;&#34; width=&#34;453&#34; height=&#34;319&#34; /&#62;&#60;img src=&#34;/data/default/upload/site/558/e94/90625/2012/11/30/hccelilqjh1vql05fdne5rk6g11354274751.jpg&#34; alt=&#34;&#34; width=&#34;459&#34; height=&#34;318&#34; /&#62;&#60;/p&#62;', '/data/default/kecheng/ce4/e8f/1rbdu9otnvicnc4keahsbpc6g21354161516.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354849003.jpg\";i:1;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354849006.jpg\";i:2;s:70:\"/data/default/kecheng/82b/1b6/t3u8j6gjtv0np5l0hlmarjvic71354849009.jpg\";}', 'a:5:{i:1001;s:1655:\"<span style=\"font-size: small;\"><strong>升入初三的你们</strong></span><br /><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 知道哪种文体是学习重点吗？知道哪种文体写起来最难吗？对，当然是议论文。那你是不是对议论文有&ldquo;谈虎色变&rdquo;之感？是不是觉得读起来好深，写起来好难？不要急，德智给你巧支招。</span><br /><span style=\"font-size: small;\"><strong>我们帮你来解决</strong></span><br /><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 初三语文课程我们带你主攻议论文的阅读和写作。议论文是中考的必考文体，又是以后高中阶段阅读和写作的重点文体。所以本次课程，我们围绕中考议论文常出现的议论主题，精心设置了&ldquo;腹有诗书气自华&mdash;&mdash;思想篇&rdquo;系类课程，每节课一个主题，每个主题都是中考的考查热门。最新议论文真题，让你与中考零距离。教给你如何读懂议论文，如何在读懂的基础上拿高分。写作方面，我们结合中考满分作文，从审题、选材、立意等方面入手，教你轻松写出满分议论文。</span><br /><span style=\"font-size: small;\"><strong>你的学习收获</strong></span><br /><span style=\"font-size: small;\">1.知道议论文常见的考查主题，熟练掌握议论文的阅读方法和答题规范。</span><br /><span style=\"font-size: small;\">2.学会议论文如何审题、立意，如何积累素材，写就中考满分作文。</span><br /><span style=\"font-size: small;\">3.通过阅读文章，学会辨证思考问题，品悟人生真谛。</span>\";i:1002;s:3682:\"<span style=\"font-size: small;\"><strong>学习初三的你，是否也有以下&ldquo;症状&rdquo;</strong></span><br /><span style=\"font-size: small;\">1.解决实际应用问题，找不到相等关系，判断最值失误？</span><br /><span style=\"font-size: small;\">2.旋转&mdash;&mdash;不知道什么时候用？如何用，怎么用？</span><br /><span style=\"font-size: small;\">3.圆的切线证明，经常找不到突破口，往往走进死胡同！</span><br /><span style=\"font-size: small;\">4.如何快而准的求出二次函数的解析式，你知道么?</span><br /><span style=\"font-size: small;\">5.面积最大最小如何求？利润最大最小如何求？你知道么？</span><br /><span style=\"font-size: small;\">6.证明相似能干什么？你真的全都知道么？</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.弄清实际问题背景，把有关数量关系分析透彻，特别是找出作为列方程依据的主要相等关系。</span><br /><span style=\"font-size: small;\">2. 通过实例让你认识和感受旋转，从熟悉的图形出发，让你学会解决旋转的问题的方法。</span><br /><span style=\"font-size: small;\">3. 通过课程讲解会使同学们熟悉探索法的推理过程，熟练地用综合法进行证明，进一步提高同学们的思维能力和逻辑推理能力。</span><br /><span style=\"font-size: small;\">4.二次函数形式的特点分析，独到而全面的例题教你如何设二次函数，能更快、更准的算出函数解析式！</span><br /><span style=\"font-size: small;\">5.典型例题，细致讲解，方法归纳教你面对最值问题不再烦恼！</span><br /><span style=\"font-size: small;\">6.相似的模型归纳，模型特点的详细阐述，让你面对几何图形，不再害怕！</span><br /><span style=\"font-size: small;\"><strong>课程学习效果</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.熟练掌握二次根式有关内容，一元二次方程的解法，圆的众多性质。</span><br /><span style=\"font-size: small;\">2.熟练掌握旋转这一图形变换工具，有效解决部分几何证明问题。</span><br /><span style=\"font-size: small;\">3.理解和掌握圆的众多知识点，对圆的知识点进行一个有效梳理。</span><br /><span style=\"font-size: small;\">4.教会你一种新的证明思路&mdash;&mdash;探索法的推理，帮助你在毫无头绪的思路下寻找出路！</span><br /><span style=\"font-size: small;\">5.对所学重点知识加强记忆与理解，温故而知新！</span><br /><span style=\"font-size: small;\">6.对二次函数的图像与系数之间的关系认识更加深刻，不只是停留于课本上的知识，彻底弄清二次函数的图像、性质！</span><br /><span style=\"font-size: small;\">7.对相似有更深刻的认识，能从几何图形中，提炼模型，帮你更快，更准的解答几何题！</span><br /><span style=\"font-size: small;\">8.理清三角函数，让你对一大堆的公式记忆不再模糊！</span><br /><span style=\"font-size: small;\">9.分类讨论，数形变换，数形结合的思想面面俱到，让你的思维来个大解放！</span><br /><span style=\"font-size: small;\">10.一道题，多种解法，让你头脑变灵活，不再墨守成规！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br /><span style=\"font-size: small;\"><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693042.png\" alt=\"\" width=\"508\" height=\"347\" /></span>\";i:1003;s:2295:\"<strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">面对中考压力的你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.学过的知识如散沙，老师课上一讲就会，考场做题一做就错！</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.单选填空语法题丢分易，阅读写作综合题提分难！</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.测验考试周周有，随着时间中考压力&ldquo;水涨船高&rdquo;！</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4.面多&ldquo;三多一少&rdquo;即课程多、作业多、考试多、休息少，感觉时间越来越不够用！</span><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">面对困扰，我们需要这样的英语课</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.归纳总结初一初二，掌握初三重难点，做好知识衔接与巩固！</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.摸清自己学习的薄弱点，培优补差，为中考复习打定基础。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.掌握重难语法点的学习与运用，提升写作阅读等英语综合成绩。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4.逐步培养中考应试能力，学习应试技巧，合理规划时间！</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>你的精彩课堂</strong></span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693087.png\" alt=\"\" /></strong></span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693124.png\" alt=\"\" /></strong></span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><br /></strong></span>\";i:1004;s:2406:\"<span style=\"font-size: small;\"><strong>学习初三物理的你</strong></span> <br /><span style=\"font-size: small;\">1.面对众多板块知识的学习，摸不着头脑，找不到正确有效的方法。</span>&nbsp;<br /><span style=\"font-size: small;\">2.基础知识不牢固，一遇到概念类问题就容易丢分。</span>&nbsp;<br /><span style=\"font-size: small;\">3.遇到比较复杂的题目，更是一头雾水，无从下手。</span>&nbsp;<br /><span style=\"font-size: small;\">4.遇到力学综合性题目，不知道如何分析，找不到任何思路。</span>&nbsp;<br /><span style=\"font-size: small;\">5.不知道如何巧妙的快速解题，考试无法取得高分。</span>&nbsp;<br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.帮你梳理物理概念，讲解物理规律，为你的物理学习打下坚实的基础。</span>&nbsp;<br /><span style=\"font-size: small;\">2.教你如何提取题目的有用信息，如何整理思路，利用物理规律解题。</span>&nbsp;<br /><span style=\"font-size: small;\">3.总结典型题目的解题规律与解题方法，教会你解决各类物理问题。</span>&nbsp;<br /><span style=\"font-size: small;\">4.帮你积累考试的技巧，攻克物理学习瓶颈，迅速提分！</span>&nbsp;<br /><span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">1.学会如何解决有关密度测量的实验探究题与密度应用的计算题。</span>&nbsp;<br /><span style=\"font-size: small;\">2.知道如何分析浮力及其应用的综合题，找到解题思路。</span>&nbsp;<br /><span style=\"font-size: small;\">3.对于功、功率和机械效率的计算题，将会掌握解题方法，找到解题规律与技巧。</span>&nbsp;<br /><span style=\"font-size: small;\">4.提升物理学科能力，掌握正确的学习方法，考试得高分。</span>&nbsp;<br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br /><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693157.png\" alt=\"\" /></strong></span><br /><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693174.png\" alt=\"\" /></strong></span>\";i:1005;s:1558:\"<span style=\"font-size: small;\"><strong>学习初三化学的你</strong></span><br /><span style=\"font-size: small;\">1.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握</span>&nbsp;<br /><span style=\"font-size: small;\">2.对于大型实验题，做题思路混乱，不知从何下手</span>&nbsp;<br /><span style=\"font-size: small;\">3.前后知识衔接不连贯，学久了容易忘，巧妙记忆能力差</span>&nbsp;<br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.总结规律，科学梳理，把握脉络，有条不紊</span>&nbsp;<br /><span style=\"font-size: small;\">2.分析实验装置，制取原理，实验方法，收集方法，轻松突破实验难关</span>&nbsp;<br /><span style=\"font-size: small;\">3.提炼关键点，概念巧拆分，知识体系化，轻松灵活掌握</span>&nbsp;<br /><span style=\"font-size: small;\"><strong>你的学习效果</strong></span> <br /><span style=\"font-size: small;\">熟练：计算公式，实验步骤，质量守恒定律，性质决定用途思想，金属活动性顺序表等等</span>&nbsp;<br /><span style=\"font-size: small;\">掌握：列方程思想，关系式法，化学方程式计算步骤，溶液稀释的计算，十字交叉法等等</span>&nbsp;<br /><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693209.png\" alt=\"\" width=\"496\" height=\"339\" /></strong></span>\";}', '1360.00', '68', '31104000', '931', '3301', null, '', '1354161560', '1354161560', '1', '', '<span style=\"font-size: small;\">初三年级的学生</span>', '<span style=\"font-size: small;\">1.分类讨论，数形变换，数形结合的思想面面俱到，让你的思维来个大解放！</span><br /><span style=\"font-size: small;\">2.一道题，多种解法，让你头脑变灵活，不再墨守成规！</span><br /><span style=\"font-size: small;\">3.知道议论文常见的考查主题，熟练掌握议论文的阅读方法和答题规范。</span><br /><span style=\"font-size: small;\">4.学会议论文如何审题、立意，如何积累素材，写就中考满分作文。</span><br /><span style=\"font-size: small;\">5.掌握列方程思想，关系式法，化学方程式计算步骤，溶液稀释的计算，十字交叉法等等。</span><br /><span style=\"font-size: small;\">6.提升物理学科能力，掌握正确的学习方法，考试得高分。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14308', '高一高二高三化学课程联报', '4', '', '', '', '/data/default/kecheng/d19/976/i6ad55rgr0gldn626jhfhg0sl31354263877.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/486/b54/16f3pcogb4aph7q8vpsja7ih201355973814.jpg\";i:1;s:70:\"/data/default/kecheng/486/b54/16f3pcogb4aph7q8vpsja7ih201355973829.jpg\";i:2;s:70:\"/data/default/kecheng/486/b54/16f3pcogb4aph7q8vpsja7ih201355973843.jpg\";}', 'a:1:{i:1005;s:1973:\"<pre><strong><span style=\"font-size: small;\">学习高中化学的你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.元素化合物知识内容多、繁杂，学习没头绪或者没条理。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对化学多方面知识的认识理解缺少系统，没有建立化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.化学原理抽象难懂，知识难度大，无法突破。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.有机推断物质、条件众多，无法找到突破口。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.忽视规律的总结，陷入题海，前做后忘，费时费力没有成效。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">德智课程帮助你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.科学梳理金属、非金属及其化合物之间反应及转化，让丰富知识条理、科学、系统。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.建立&ldquo;位、构、性&rdquo;模型，深入了解元素周期律，建立基本化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.用规律，找方法，突破难点，抓住关键。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.巧用官能团转化关系、有机反应条件，轻松解决有机推断。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.精析解题思路和方法，总结规律、技巧、考试策略，举一反三，触类旁通。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/16f3pcogb4aph7q8vpsja7ih201355973598.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/16f3pcogb4aph7q8vpsja7ih201355973619.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/16f3pcogb4aph7q8vpsja7ih201355973640.jpg\" alt=\"\" />\";}', '1600.00', '80', '93312000', '135', '3302', null, '', '1354258389', '1354258389', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高中理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.系统掌握元素化合物知识，形成网络，全面深刻记忆。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.熟练运用盖斯定律、溶解平衡原理、pH计算方法等，突破化学原理难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握有机官能团的性质、有机反应类型、同分异构体的书写方法等，轻松解决有机推断。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.清楚掌握高考重难点、易错点和热点知识，抓准考试命脉，高效、精准学习，轻松赢高分！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14309', '高一高二高三理科数学课程联报', '4', '', '', '&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;正在读高中的你：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.初中数学语言概念等具体直观，容易理解，高中数学抽象，如集合、映射等概念难以理解；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.高中数学知识难度加大，尤其是一些综合问题没有头绪，如圆锥曲线、导数等；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.学不得法，做了很多题，成绩提高却不明显。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;德智课程帮助你：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.梳理知识，解读相关概念及公式，让你对抽象概念的理解更深入；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.提炼和总结解题策略，培养逻辑思维能力，达到会解一道题从而会解这一类问题的目的，把你从茫茫题海中解放出来；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.一题多解，一题多变，多角度全方位分析问题，教你轻松学好数学！&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;课程学习效果：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;掌握：集合，函数与导数，三解函数、立体几何、解析几何，数列，概率与统计等；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;熟练：数形结合，分类讨论，转化与化归，函数与方程、特殊值法，换元法，待定系数法等。&#60;/span&#62;&#60;/pre&#62;', '/data/default/kecheng/347/186/pkgm4sgmrrn7skl4aacphgo9241355193431.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/2f3/c62/qi1uoj3tlcqnbl46dqsmpu9ds31355134958.jpg\";}', 'a:1:{i:1002;s:1677:\"<pre><span style=\"font-size: small;\"><strong>正在读高中的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.初中数学语言概念等具体直观，容易理解，高中数学抽象，如集合、映射等概念难以理解。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.高中数学知识难度加大，尤其是一些综合问题没有头绪，如圆锥曲线、导数等。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.学不得法，做了很多题，成绩提高却不明显。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.梳理知识，解读相关概念及公式，让你对抽象概念的理解更深入。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.提炼和总结解题策略，培养逻辑思维能力，达到会解一道题从而会解这一类问题的目的，把你从茫茫题海中解放出来。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.一题多解，一题多变，多角度全方位分析问题，教你轻松学好数学！<br /></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355820838.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355820926.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355820956.png\" alt=\"\" /><br /></strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><br /></span></pre>\";}', '2380.00', '119', '93312000', '178', '3302', null, '', '1354259396', '1354259396', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高一、高二、高三理科学生</span></pre>', '<pre><span style=\"font-size: small;\">掌握：集合，函数与导数，三解函数、立体几何、解析几何，数列，概率与统计等。</span></pre>\r\n<pre><span style=\"font-size: small;\">熟练：数形结合，分类讨论，转化与化归，函数与方程、特殊值法，换元法，待定系数法等。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14310', '高一高二高三物理课程联报', '4', '', '', '', '/data/default/kecheng/d19/976/tctt05mdbh7ug004a26t3rsk431354263834.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1f6/9b3/mb3krjfkg4o6nfff2gtp574iq11356084017.png\";i:1;s:70:\"/data/default/kecheng/1f6/9b3/mb3krjfkg4o6nfff2gtp574iq11356084024.png\";i:2;s:70:\"/data/default/kecheng/1f6/9b3/mb3krjfkg4o6nfff2gtp574iq11356084029.png\";}', 'a:1:{i:1004;s:1513:\"<pre><span style=\"font-size: small;\"><strong>学习高中物理的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.物理情境想象不出，不会分析物体受力，不会分析物理过程。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对综合问题思维打不开、只会套用公式，不会灵活变形求解。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对各种规律与现代生活实际相结合的应用类问题，找不到思路,如对运动学与功能原理结合类及带电粒子在复合场中的运动问题不能快速找到突破口。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.精选例题让你掌握各种解题方法：图像法、比例法、极值法、微元法、模型法等。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明确动量与动能、冲量与功、动量定理与动能定理的区别，并能够灵活应用各种规律解决实际问题。<strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">3.找准电磁场中运动问题的规律，节约时间，事半功倍。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356083284.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356083314.png\" alt=\"\" /></strong></span></pre>\";}', '2360.00', '118', '93312000', '222', '3302', null, '', '1354260048', '1354260048', '1', '', '<pre><span style=\"font-size: small;\">全国各版本理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.明确各公式的作用，深化对各公式的理解，提高对公式的综合运用能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对重要考点的相关综合问题能够形成自己的解题思路。<strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">3.点拨解题思路和方法，总结规律、技巧、考试策略，举一反三，触类旁通。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14311', '高一高二高三文科数学课程联报', '4', '', '', '&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;正在读高中的你：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.初中数学语言概念等具体直观，容易理解，高中数学抽象，如集合、映射等概念难以理解；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.高中数学知识难度加大，尤其是一些综合问题没有头绪，如圆锥曲线、导数等；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.作为文科生，对数学学不得法，做了很多题，成绩提高却不明显。&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;德智课程帮助你：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;1.梳理知识，解读相关概念及公式，让你对抽象概念的理解更深入；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre class=&#34;1&#34;&#62;&#60;span style=&#34;font-size: small;&#34;&#62;2.提炼和总结解题策略，培养逻辑思维能力，达到会解一道题从而会解这一类问题的目的，把你从茫茫题海中解放出来；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;3.一题多解，一题多变，多角度全方位分析问题，教你轻松学好数学！&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;&#60;strong&#62;课程学习效果：&#60;/strong&#62;&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;掌握：集合，函数与导数，三解函数、立体几何、解析几何，数列，概率与统计等；&#60;/span&#62;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#60;span style=&#34;font-size: small;&#34;&#62;熟练：数形结合，分类讨论，转化与化归，函数与方程、特殊值法，换元法，待定系数法等。&#60;/span&#62;&#60;/pre&#62;', '/data/default/kecheng/d19/976/o94jf97uco2ja7c06m3vt9j6i31354263748.jpg', 'a:0:{}', 'a:1:{i:1002;s:1824:\"<pre><span style=\"font-size: small;\"><strong>正在读高中的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.初中数学语言概念等具体直观，容易理解，高中数学抽象，如集合、映射等概念难以理解。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.高中数学知识难度加大，尤其是一些综合问题没有头绪，如圆锥曲线、导数等。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.作为文科生，对数学学不得法，做了很多题，成绩提高却不明显。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.梳理知识，解读相关概念及公式，让你对抽象概念的理解更深入。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.提炼和总结解题策略，培养逻辑思维能力，达到会解一道题从而会解这一类问题的目的，把你从茫茫题海中解放出来。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.一题多解，一题多变，多角度全方位分析问题，教你轻松学好数学！<br /></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355821026.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355821060.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355821099.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355821122.png\" alt=\"\" /><br /></strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><br /></span></pre>\";}', '2000.00', '100', '93312000', '175', '3303', null, '', '1354260837', '1354260837', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高一、高二、高三文科学生</span></pre>', '<pre><span style=\"font-size: small;\">掌握：集合，函数与导数，三解函数、立体几何、解析几何，数列，概率与统计等。</span></pre>\r\n<pre><span style=\"font-size: small;\">熟练：数形结合，分类讨论，转化与化归，函数与方程、特殊值法，换元法，待定系数法等。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14312', '期末点睛之单项选择', '2', '', '', '', '/data/default/kecheng/d19/976/i6ad55rgr0gldn626jhfhg0sl31354266863.jpg', null, null, '20.00', '1', '31104000', '75', '3301', null, '', '1354266868', '1354266868', '0', null, null, null, '3701');
INSERT INTO `kc_course_pack` VALUES ('14313', '期末作文高分突破：审题立意', '2', '', '', '', '/data/default/kecheng/d19/976/i6ad55rgr0gldn626jhfhg0sl31354267654.jpg', null, null, '20.00', '1', '31104000', '52', '3301', null, '', '1354266935', '1354266935', '0', null, null, null, '3701');
INSERT INTO `kc_course_pack` VALUES ('14314', '初二物理期末复习课程', '2', '梳理知识、掌握方法', '京城名校高级教师', '', '/data/default/kecheng/d19/976/f84eosprrr5qn9qcum47ea2cs01354275460.png', null, null, '20.00', '1', '31104000', '158', '3301', null, '', '1354274579', '1354274579', '0', null, null, null, '3701');
INSERT INTO `kc_course_pack` VALUES ('14315', '初三数学期末复习课程', '2', '名师巧讲解题技巧', '重点学校高级教师', '', '/data/default/kecheng/d19/976/f84eosprrr5qn9qcum47ea2cs01354275481.png', null, null, '20.00', '1', '31104000', '105', '3301', null, '', '1354274848', '1354274848', '0', null, null, null, '3701');
INSERT INTO `kc_course_pack` VALUES ('14316', '初一语文数学期末复习课程', '2', '立足期末，全面提升考试技巧', '高级教师精英团队', '', '/data/default/kecheng/6a1/f59/b0kokmfaahf9leka7e38kn43941354336972.png', 'a:0:{}', 'a:0:{}', '40.00', '2', '31104000', '209', '3301', null, '', '1354274989', '1354274989', '0', '初一期末复习的学生', '', '', '3701');
INSERT INTO `kc_course_pack` VALUES ('14317', '高一下学期六科联报课程', '3', '', '', '', '/data/default/kecheng/347/186/5mlna7kq5lg5c2ncumtjb1lkm01355205270.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/347/186/5mlna7kq5lg5c2ncumtjb1lkm01355195938.jpg\";}', 'a:6:{i:1001;s:1656:\"<pre><span style=\"font-size: small;\"><strong>你的学习困惑</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">读散文不能够理解主旨、把握作者情感？社科文不知如何梳理文意、整合概括？应用文写作从未接触，不知如何下笔？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.精选课内外优美散文，通过对散文的形象、语言细致入微的分析，教会你鉴赏散文的方法，让你真正读懂散文的思想，感受散文的情感魅力。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.社科文阅读宝典让你巧得分。本课程将教你如何筛选信息，整合内容，概括表述。并通过经典例题的讲解，教授一些实用的整合方法，如摘引原句法、择要概括法、本质概括法等，让你能够举一反三，轻松把握社会科学类文章的阅读。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.应用文写作：应用文与我们的日常生活密切相关，所以学会写应用文是十分必要的。书信、倡议书、邀请函&hellip;&hellip;都属于应用文。本次课程中，老师将带领大家将各种应用文体&ldquo;一网打尽&rdquo;，在教会你应用文写作的规范与标准的基础上，教你应用文写作的创新之道。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<span style=\"font-size: small;\"><img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/5mlna7kq5lg5c2ncumtjb1lkm01355194255.png\" alt=\"\" /><br /></span>\";i:1002;s:1692:\"<pre><span style=\"font-size: small;\"><strong>正在学习高一下学期数学的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.空间想像能力差，在解题时无从下手。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.逻辑推理能力差，能读懂题并且能画出几何图形，证明时却不得要领。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.数列题目难，方法多，综合问题常常不知道该如何入手。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">4.利用基本不等式求最值时，常常不知道该如何变形。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.在理解的基础上记忆公理和定理，最终内化吸收。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.借助模型培养空间想像能力，教你轻松画出图形。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.教你面对难题时如何化复杂为简单，化陌生为熟悉，轻松破解难题。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">4.总结解题的各种方法，如转化与化归、累加（乘）法，错位相减法、裂项相消法，配凑法，换元法等。<br /><br /></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355817653.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/18/vt7cn37n6v4a7ioife8t2drr501355817691.png\" alt=\"\" /></strong></span></pre>\";i:1003;s:1139:\"<pre><span style=\"font-size: small;\"><strong>学习高一下的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.上学期一晃过去，语法点以及重难点还是似懂非懂。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.很努力学习语法，单选还是常丢分，阅读理解还是读不明白。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.课业负担重，压力大，努力半天却收效甚微，迷茫无助。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结方法，找准规律，帮助你理清学习思路。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学会运用猜词能力，提升阅读速度和准确率。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.热门易考话题作文素材全面搜集，从容学会各种体裁的作文。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n&nbsp;<img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/5mlna7kq5lg5c2ncumtjb1lkm01355194475.png\" alt=\"\" />\";i:1004;s:1206:\"<pre><span style=\"font-size: small;\"><strong>学习物理必修2的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1. 对圆周运动的线速度、角速度、周期、频率、向心加速度等物理量一头雾水。</span></pre>\r\n<pre><span style=\"font-size: small;\">2. 感觉万有引力、宇宙速度离你很遥远，对此类问题没思路、没头绪。</span></pre>\r\n<pre><span style=\"font-size: small;\">3. 对功和能的各种关系不会分析，只能生搬硬套公式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从根本上理清各种物理量的来龙去脉，建立信心。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.生动直观的图解帮你突破万有引力与天体的各个难点。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.引导你建立求解功和能的物理模型，找到自己最擅长的解题思路。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n&nbsp;<img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/5mlna7kq5lg5c2ncumtjb1lkm01355194625.png\" alt=\"\" />\";i:1005;s:1677:\"<pre><span style=\"font-size: small;\"><strong>学习化学必修2的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.知识内容覆盖面较广，难度不大，让你的学习难于梳理。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.文科的你，这是必须学习课程。理科的你，这是你的基础课程。元素周期律、元素周期表的理论学习困难，&ldquo;位、构、性&rdquo;抽象理解难于想象。</span></pre>\r\n<pre><span style=\"font-size: small;\">3. 对化学多方面知识的认识理解缺少系统，没有建立化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.周老师独门秘笈帮你解决如何记忆元素周期表结构，特殊技巧事半功倍而且不会遗忘，并让你学以致用。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.初步掌握化学基本理论的知识，学会原理基本应用、能量分析及简单有机化合物性质和反应，轻松应对会考，顺畅衔接高二理科化学学习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.&ldquo;位、构、性&rdquo;模式建立，深入了解元素周期律，建立基本化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.<strong>独家奉献</strong>：元素周期表特殊记忆方法、最外层8电子结构判断技巧。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n&nbsp; <img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/5mlna7kq5lg5c2ncumtjb1lkm01355194686.png\" alt=\"\" />\";i:1006;s:982:\"<pre><span style=\"font-size: small;\"><strong>学习必修2的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.感觉遗传与变异知识细碎、名词概念多，抽象性强，难理解。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.涉及实验及推理、相关计算等综合的问题，感觉难度大，无法入手。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.学习记忆相关名词概念的基本策略，即理解意义才能记住、用准。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学会解决与过程有关的计算问题要绘制简图，进而理清题目信息，解决复杂问题。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/5mlna7kq5lg5c2ncumtjb1lkm01355194775.png\" alt=\"\" />\r\n<pre>&nbsp;</pre>\";}', '1900.00', '95', '31104000', '462', '3301', null, '', '1355193712', '1355193712', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高一学生</span></pre>', '<div class=\"user_nr_left user_box\">\r\n<pre><span style=\"font-size: small;\">1.提炼关键点，揭秘易错点，轻松避开题目陷阱。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.立足基础知识，抓纲靠本，力求做到&ldquo;面全，线通，点透&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.总结规律，提炼方法，针对难点，综合突破。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.针对学生如何解决文化生活中出现的一些困惑和问题进行着重讲解，把握问题核心。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">5.梳理知识，理清思路，帮你构建知识网络。总结各种题型的解题步骤，让你解题时有法可依。</span></pre>\r\n</div>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14318', '高一下学期政史地课程', '3', '', '', '', '/data/default/kecheng/141/308/uvdckqprlo0h6ve7qdl845r4h61355278165.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/141/308/uvdckqprlo0h6ve7qdl845r4h61355278211.jpg\";}', 'a:3:{i:1007;s:1365:\"<pre><span style=\"font-size: small;\"><strong>学习必修2的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.政治领域涉及的名词、术语繁多，理论性强，记忆混乱。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从历史发展的角度，无法清晰分析不同政治制度对历史进程的影响。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.政治制度的学习只注重横向比较，忽视纵向沿革，理不清点、线，面等关系。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.政治概念的解读运用从感性到理性、从个别到一般的方法，分析史实或典型材料，理解某一具体制度的特点，然后对同类制度进行归纳、概括，从而理解该类制度的内涵和实质。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在特定的历史环境下分析和评价中外政治制度，从自然环境、生产方式、民族和文化传统、制度溯源等方面，客观地分析其呈现出不同特点的原因。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.注意政治发展的沿革，依据发展的阶段性变化，纵向归纳有关政治制度、国际关系格局等发展的沿革与演变。</span></pre>\r\n<pre><br /><br /></pre>\";i:1008;s:1290:\"<pre><span style=\"font-size: small;\"><strong>学习地理必修2的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.内容多，知识繁杂，不知如何有效理解地理基本原理、规律等。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.知识难度大，不知如何有效学习，清楚掌握自然地理环境要素的相互作用机制和规律。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.学习压力大，把握不准核心考点，学习效率低。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.考点梳理与典型例题相结合，以考点带例题，以例题学考点，面向考试，招招带劲。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.专题形式突破，应用分析鞭辟入里，细致入微，考法解法，应有尽有。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.规律方法总结，针对地理核心知识和高频考点详尽剖析各种规律方法的迁移运用。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n&nbsp;<img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/5mlna7kq5lg5c2ncumtjb1lkm01355195408.png\" alt=\"\" />\";i:1009;s:1113:\"<pre><span style=\"font-size: small;\"><strong>学习必修2的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">对待物价问题你该如何正确看待？消费的作用该如何发挥？<strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">分配与公平的关系该如何正确处理?</span></pre>\r\n<pre><span style=\"font-size: small;\">怎样加快经济发展方式的转变？</span></pre>\r\n<pre><span style=\"font-size: small;\">怎样用经济全球化的相关知识正确认识今天的对外经济关系？</span></pre>\r\n<pre><span style=\"font-size: small;\">这些&hellip;&hellip;，你都清楚了么？</span></pre>\r\n<pre>&nbsp;<strong style=\"font-size: small;\">德智课程帮助你</strong><span style=\"font-size: small;\"><br /><br />1.联系现实经济生活中的现象和问题，综合运用经济学理论分析现象、探求规律，整合理论知识，提高学生的解题技巧和能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.引导学生分清不同行为主体在经济生活中的不同地位、不同作用和不同做法。</span></pre>\";}', '400.00', '20', '31104000', '36', '3301', null, '', '1355195738', '1355195738', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高一年级学生</span></pre>', '<div class=\"user_nr_left user_box\">\r\n<pre><span style=\"font-size: small;\">1.提炼关键点，揭秘易错点，轻松避开题目陷阱。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.立足基础知识，抓纲靠本，力求做到&ldquo;面全，线通，点透&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.总结规律，提炼方法，针对难点，综合突破。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.针对学生如何解决文化生活中出现的一些困惑和问题进行着重讲解，把握问题核心。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">5.梳理知识，理清思路，帮你构建知识网络。总结各种题型的解题步骤，让你解题时有法可依。</span></pre>\r\n</div>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14319', '英语核心考点串讲课程', '3', '', '', '', '/data/default/kecheng/141/308/vtbgclm9epcksh9mi5ndq5dus11355280056.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/4ec/590/5mmu34qv638jbjvuehtc8hcb901355898690.png\";i:1;s:70:\"/data/default/kecheng/4ec/590/5mmu34qv638jbjvuehtc8hcb901355898692.png\";i:2;s:70:\"/data/default/kecheng/4ec/590/5mmu34qv638jbjvuehtc8hcb901355898695.png\";}', 'a:1:{i:1003;s:1416:\"<pre><span style=\"font-size: small;\"><strong>面临中考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从句先行词掌握不清，时态语态易混淆，近似词组搭配不会区分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.阅读理解经常丢分，写作无从下笔，不知从哪儿开始复习，如何复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时间紧，知识多，学习没动力，复习没成效，逐渐丧失信心和兴趣。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.理清复习思路，掌握复习节奏，全面高效复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从语法、阅读、词汇、写作四个方面进行整合复习，科学合理节省你的复习时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.剖析阅读解题方法，总结归纳易混词组，动词时态归纳讲解，让你的复习学无遗漏！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<span style=\"font-size: small; color: #ff0000;\"> <img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/19/5mmu34qv638jbjvuehtc8hcb901355898436.png\" alt=\"\" /><img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/19/5mmu34qv638jbjvuehtc8hcb901355898454.png\" alt=\"\" /></span>\";}', '240.00', '12', '31104000', '325', '3301', null, '', '1355279842', '1355279842', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初三中考复习的学生</span></pre>', '<pre><span style=\"font-size: small;\">1.掌握定、状、宾语三大从句、被动语态、不定式等重难点语法知识。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.吃透动词时态（过去、现在、将来）和易混词组区别和联系，轻松辨析使用。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.学会写作思路方法，提升写作档次，掌握阅读理解方法和规律，稳拿高分。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14320', '初一初二初三五科联报课程', '4', '提炼方法，全面掌握', '', '', '/data/default/kecheng/c1f/803/njd9a8n6ia92lpsrapv1v7ft731355478896.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/c1f/803/bgv1dtkqmoajiosrqgflhnce331355483729.jpg\";i:1;s:70:\"/data/default/kecheng/c1f/803/bgv1dtkqmoajiosrqgflhnce331355483759.jpg\";i:2;s:70:\"/data/default/kecheng/c1f/803/bgv1dtkqmoajiosrqgflhnce331355483817.jpg\";}', 'a:5:{i:1001;s:2395:\"<pre><span style=\"font-size: small;\"><strong>处于初中学习阶段的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">知道初中阶段语文的学习目标吗？提分？中考？对，但绝不仅于此。初中语文学习，还有很重要的一个方面：积累语文素养。那么语文素养有什么用？语文素养不仅仅是知识、能力的学习与运用，更是人个性品质、道德修养、审美情趣的综合体现。所以德智课程在立足帮你应对考试的基础上，更加注重初中阶段的你，在语文素养方面的提高。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.课程体系：针对初中阶段语文学习的特点，我们为你精心打造了&ldquo;腹有诗书气自华&rdquo;系列专题课程。按照初中三年你的成长体验和认知规律，分为&ldquo;成长篇&rdquo;&ldquo;生活篇&rdquo;&ldquo;认识篇&rdquo;&ldquo;思想篇&rdquo;等。初一初二注重素养提升，初三注重应试技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.课程内容：每个学期一个大的专题，下面的每一节课都围绕大的专题设置课程。分别从阅读、写作、认识体验方面进行指导：阅读与写作，教你应付语文考试的两大重点内容；认识体验，助你语文学习更上层楼，这正是对语文素养的提升。&nbsp;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习收获</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.学会文章的阅读技巧和答题方法，中考阅读不失分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握作文素材积累的方法，并学会作文的审题立意，开头结尾，写就高分作文。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.通过老师对专题内容的阐释，在认识自我、认识世界、感受亲情、哲理思考中，获取新的人生体验。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355482212.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355482226.jpg\" alt=\"\" /></span></pre>\";i:1002;s:2029:\"<pre><span style=\"font-size: small;\"><strong>学习数学的你</strong></span><br /><span style=\"font-size: small;\">1.证明全等，找条件是否经常遇到瓶颈？</span><br /><span style=\"font-size: small;\">2.证明过程如何写，你是否书写规范？条理是否清晰？</span><br /><span style=\"font-size: small;\">3.函数&mdash;&mdash;概念是否理解？应用题如何寻找等式关系？</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.探索研究能证明全等的方法，对全等条件进行一个深刻了解，同时培养从条件中找隐含条件的能力。</span><br /><span style=\"font-size: small;\">2.证明推理逻辑强，推理表达需精准，由简单例题体会证明思路与格式，逐渐增加题目复杂程度，循序渐进过程。</span><br /><span style=\"font-size: small;\">3.简单例子，带你领会从抽象到具体的转化过程，理解函数定义的变化与对应关系。</span><br /><span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">1.查漏补缺，找出知识漏洞，熟悉知识考点、攻破知识难点。</span><br /><span style=\"font-size: small;\">2.温故知新，对所学知识巩固复习，加深理解与记忆</span><br /><span style=\"font-size: small;\">3.知识拓展，学习课堂之外的东西，对知识进行一个丰富过程。</span><br /><span style=\"font-size: small;\">4.开拓数学思维，例题的细致讲解带你逐步理解解题思路，教你如何思考问题，初步锻炼数学思维，同时一道题多种解法，解救你的思维局限！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br /><br /></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355483331.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355483354.jpg\" alt=\"\" />\";i:1003;s:1458:\"<pre><span style=\"font-size: small;\"><strong>学习英语的你</strong></span><br /><span style=\"font-size: small;\">1.成绩忽高忽低，上课开始听不懂、跟不上，学习站在&ldquo;分水岭&ldquo;？</span><br /><span style=\"font-size: small;\">2.单词难度，语法深度，写作高度陡然徒增，成绩进入&ldquo;爬坡期&ldquo;？</span><br /><span style=\"font-size: small;\">3.时态语态易混易错，词性词法区分不清，写作常丢分？</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.不想学&mdash;课堂幽默精彩，环节设置多种多样，抓住你全部的注意力!</span><br /><span style=\"font-size: small;\">2.学不会&mdash;重难点知识庖丁解牛，逐句分析般讲解，摒弃死记硬背句型结构！</span><br /><span style=\"font-size: small;\">3.分不清&mdash;时态语态对比讲解，例句真题配套练习，彻底吃透语法难点！</span><br /><span style=\"font-size: small;\">4.记不住&mdash;词汇句型拓展记忆，优美篇章课上赏析，语境学习增强联想记忆！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355483522.png\" alt=\"\" /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355483535.png\" alt=\"\" />\";i:1004;s:2900:\"<pre><span style=\"font-size: small;\"><strong>学习初中物理的你，是否存在以下焦虑？</strong></span><br /><span style=\"font-size: small;\">1.觉得物理非常难，找不到好的学习方法，不能取得好成绩。</span><br /><span style=\"font-size: small;\">2.明明课上很认真听讲，觉得也都听懂了，可一到考试就犯怵！</span><br /><span style=\"font-size: small;\">3.对于一些物理概念，老是弄混，感觉理解了，一做选择题就做不对，尤其多选题。</span><br /><span style=\"font-size: small;\">4.遇到具体的问题，不知道怎么应用规律去解题，不知道该用什么公式计算。</span><br /><span style=\"font-size: small;\">5.动手实验的次数比较少，不能真正理解实验题，不会分析现象，不能得出结论。</span><br /><span style=\"font-size: small;\">6.遇到大的计算题，更是一头雾水，不知道如何下手！</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.名师教你学物理的最基本的方法，养成正确的学习习惯。</span><br /><span style=\"font-size: small;\">2.帮你梳理最基本的物理概念与规律，让你理解每个概念的内涵与外延，知道规律的适用条件。</span><br /><span style=\"font-size: small;\">3.精选典型的题目，带你一步步去分析，梳理解题思路，总结解题技巧。</span><br /><span style=\"font-size: small;\">4.对于题干特别长的综合题，教你如何把握题目关键点，分析题目考查哪些知识点，如果选用规律解题。</span><br /><span style=\"font-size: small;\">5.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span><br /><span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">通过本课程的学习，你将在以下几个方面有所突破：</span><br /><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span><br /><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会各类题的解法。</span><br /><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span><br /><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span><br /><span style=\"font-size: small;\">5.了解物理学的实质，掌握正确的学习方法，内化成物理学科能力。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355483139.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/nc5c084rdop5cdgr8g6617q9h71355484137.jpg\" alt=\"\" />\";i:1005;s:1629:\"<pre><span style=\"font-size: small;\"><strong>学习初三化学的你</strong></span><br /><span style=\"font-size: small;\">1.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握。</span><br /><span style=\"font-size: small;\">2.对于大型实验题，做题思路混乱，不知从何下手。</span><br /><span style=\"font-size: small;\">3.前后知识衔接不连贯，学久了容易忘，巧妙记忆能力差。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.总结规律，科学梳理，把握脉络，有条不紊。</span><br /><span style=\"font-size: small;\">2.分析实验装置，制取原理，实验方法，收集方法，轻松突破实验难关。</span><br /><span style=\"font-size: small;\">3.提炼关键点，概念巧拆分，知识体系化，轻松灵活掌握。</span><br /><span style=\"font-size: small;\"><strong>你的学习效果</strong></span><br /><span style=\"font-size: small;\">熟练：计算公式，实验步骤，质量守恒定律，性质决定用途思想，金属活动性顺序表等等。</span><br /><span style=\"font-size: small;\">掌握：列方程思想，关系式法，化学方程式计算步骤，溶液稀释的计算，十字交叉法等等。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355482884.jpg\" alt=\"\" />&nbsp;<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/bgv1dtkqmoajiosrqgflhnce331355482905.jpg\" alt=\"\" />\";}', '3940.00', '197', '93312000', '479', '3301', null, '', '1355282246', '1355282246', '1', '全国各版本初中学生', '<pre><span style=\"font-size: small;\">全国各版本初中学生</span></pre>', '<pre><span style=\"font-size: small;\">1.掌握作文素材积累的方法，并学会作文的审题立意，开头结尾，写就高分作文。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.温故知新，对所学知识巩固复习，加深理解与记忆</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14321', '初中语文阅读写作特色专题课程', '2', '特色专题课程，提升语文素养！', '', '', '/data/default/kecheng/b6f/f58/ionlaaei8522nchfki8nlm2eq01355807188.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355897369.jpg\";i:1;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355897377.jpg\";i:2;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355897381.jpg\";}', 'a:1:{i:1001;s:3094:\"<pre><span style=\"font-size: small;\"><strong>课程宣言：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">为考试，更为学好语文！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>语文应该怎样学？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">学好语文并非一朝一夕之功，需要长期的阅读和积累。妄想通过学习某一节或某几节课，就能迅速提高语文成绩的同学，要赶快改变一下你的学习思路了。不如每天多看一点，多读一点，多想一点，多写一点，长此以往，你的语文成绩和语文素养就在不知不觉间提高了。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">&ldquo;腹有诗书气自华&rdquo;系列课程是德智专家团队精心为你打造的，课程目标是：为考试，更为提高语文素养。本着这样的宗旨，我们采用目前比较前沿的一种教学方式&mdash;&mdash;专题教学法进行授课，根据年级的不同和你的成长过程，设置不同的专题。比如初一以&ldquo;成长体验&rdquo;为主要话题，初二以&ldquo;认识感悟&rdquo;为话题，初三以&ldquo;哲理思辨&rdquo;为话题，话题的设置既关照了个体的成长规律，也涵盖了你不同的成长阶段会遇到的各种问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">另外，课程都会结合2012年最新的中考文章进行分析，每节课分为&ldquo;趣味故事&rdquo;&ldquo;美文赏读&rdquo;&ldquo;写作训练&rdquo;&ldquo;好书推荐&rdquo;四个部分，以趣味故事引出话题，主要从阅读和写作两个角度着手，读写并举，帮助大家提高阅读水平和写作能力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">阅读其实是与作者的对话。所以在阅读过程中，大家往往会觉得&ldquo;阅读很容易，答题很困难&rdquo;，这是因为你没有真正理解作者的&ldquo;说话&rdquo;意图和文章的考查角度，缺少熟练、规范的答题方法。写起文章，也觉得无从下笔，不知如何选材、立意、构思。平时看了很多书，积累了很多素材，却又苦于不懂得何时运用、如何运用。这些，我们都将在课堂上给你。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>精彩课堂抢先看：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355897215.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355897234.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355897249.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355897289.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355897347.jpg\" alt=\"\" />\";}', '600.00', '30', '31104000', '222', '3301', null, '', '1355296660', '1355296660', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初一初二初三年级学生，想打好初中语文学习基础的学生，想通过长期阅读积累提升素养的学生，语文学习能够持之以恒的学生，渴望摆脱枯燥无味的语文学习的学生&hellip;&hellip;</span></pre>\r\n<pre>&nbsp;</pre>', '<pre><span style=\"font-size: small;\">在这里，你将学会如何分析文章，如何体会作者情感，轻松把握答题技巧和阅读规律&hellip;&hellip;而这些，都是语文获取高分的必备法宝。</span></pre>\r\n<pre><span style=\"font-size: small;\">在这里，你将从阅读中体验成长，认识自我，认识世界，学会思考&hellip;&hellip;从四季更迭与自然变幻中发现生命的可贵与生活的惬意，懂得热爱生命，尊重自然，从而陶冶情操，增长智慧！</span></pre>\r\n&nbsp;&nbsp;\r\n<pre>&nbsp;</pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14322', '穿越古今的对话——古诗词鉴赏专题课程', '2', '寻诗之源起，品诗之华彩', '', '', '/data/default/kecheng/4ec/590/33agsjelmuckkkniu2bs0os6v61355907340.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/323/ccb/ap0fkkfm0bd3gtjq7do1v918851356318167.jpg\";i:1;s:70:\"/data/default/kecheng/323/ccb/ap0fkkfm0bd3gtjq7do1v918851356318174.jpg\";i:2;s:70:\"/data/default/kecheng/323/ccb/ap0fkkfm0bd3gtjq7do1v918851356318178.jpg\";}', 'a:1:{i:1001;s:2139:\"<pre><span style=\"font-size: small;\"><strong>课程学习思路：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">了解时代发展（即明确诗词产生的时代背景）&mdash;&mdash;走近诗人内心（即明白作者的内心呼告）&mdash;&mdash;捕捉诗人情感（即鉴赏诗词意象）</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">&ldquo;文章合为时而著，歌诗合为事而作&rdquo;，所以鉴赏诗词我们一定不能脱时代背景。特定的时代，产生特定的文学样式，就像唐诗，宋词，元曲，明清小说等。了解了时代的发展变化，我们就能够了解文学产生的背景，进而了解诗人的内心世界，而诗词正是他们面对特定的时代所发出的内心的呼唤。那么对于诗人诗作中所表达的情感，诗歌的相关意象，我们还难以理解吗？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>老师给你的学习建议：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本次课程，老师从诗词的发展脉络入手，结合特定的历史背景，关注诗人的内心世界。所以老师给你的学习建议是：学习诗词，并不能仅仅关注考题，若没有完整的时代背景，不了解诗人内心情感，不会关注诗词中的意象寄托，那么你的学习是支离破碎的，不完整的。只有有了一定的历史背景和文化背景的积淀，才能学会鉴赏诗词，当然答题得分，更不在话下。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317989.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317999.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356318008.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356318017.jpg\" alt=\"\" />\";}', '300.00', '15', '31104000', '110', '3301', null, '', '1355297707', '1355297707', '0', '', '<pre><span style=\"font-size: small;\">全国各地高一高二高三学生，对于诗词发展脉络不了解的学生，想增加诗词阅读积累的学生，想用打破常规的学习方法获得诗词高分的学生，想换一种方法学诗的学生&hellip;&hellip;</span></pre>', '<pre><span style=\"font-size: small;\">1.了解诗歌的起源和发展脉络。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学会结合历史背景学习诗歌，把握诗人情感。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握一些诗歌的常见意象极表现手法。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14323', '高中语文阅读理解综合提升', '2', '', '', '', '/data/default/kecheng/b6f/f58/ionlaaei8522nchfki8nlm2eq01355807096.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355895280.jpg\";i:1;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355895664.jpg\";i:2;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355895292.jpg\";}', 'a:1:{i:1001;s:1745:\"<pre><span style=\"font-size: small;\"><strong style=\"font-size: small;\">你的学习困惑：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">阅读理解几乎占据高中语文考试的&ldquo;半壁江山&rdquo;，无论高考还是期末考试，所占分值颇大。那么你是不是一直苦于&ldquo;占分多，得分少&rdquo;状况？散文抓不住中心，小说分析人物不到位，说明文生涩难解，议论文找不准论点&hellip;&hellip;这么多问题，你的阅读怎么得高分？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>怎样解决？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本次课程主要是帮你扫清高中阶段语文阅读学习中的学习困惑。所讲文体包括小说、散文、说明文、文言文、古诗词等，都是高考语文的必考文体。重难点、必考点、易错点、失分点、设题陷阱&hellip;&hellip;一网打尽；散文、小说、说明文、议论文、古诗文&hellip;&hellip;轻松得分。同时独家的解题秘籍让你形成自主学习的能力和分析能力，让你高一高二打好基础，高三稳坐阅读&ldquo;半壁江山&rdquo;！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355895235.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355895246.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355895255.jpg\" alt=\"\" />\r\n<div><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355895565.jpg\" alt=\"\" /></div>\";}', '440.00', '22', '31104000', '156', '3301', null, '', '1355298566', '1355298566', '0', '', '<pre><span style=\"font-size: small;\">高一高二高三学生，想阅读理解得高分的学生，答题不准的学生</span></pre>', '<pre><span style=\"font-size: small;\">1.帮你树立读题意识、踩点意识、推敲意识，做到准确答题，有备答题。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学会各种文体的阅读方法和答题规律，迅速阅读，准备作答。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.形成独立的分析和思考能力，扎实语文阅读基础，提高阅读理解水平。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14324', '高中语文写作快速突破', '2', '', '', '', '/data/default/kecheng/b6f/f58/ionlaaei8522nchfki8nlm2eq01355807119.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355896146.jpg\";i:1;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355896150.jpg\";i:2;s:70:\"/data/default/kecheng/4ec/590/1q9qmo0c62n4toug23im7t7m061355896155.jpg\";}', 'a:1:{i:1001;s:1968:\"<pre><span style=\"font-size: small;\"><strong>学习困惑：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">素材积累不够，作文言之无物？审题不清，立意不新？结构混乱，逻辑不明？写出来的文章文体不明&ldquo;四不像&rdquo;？&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>怎么解决？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">写人记事文、写景抒情文、应用文、议论文&hellip;&hellip;都是语文考试甚至高考的必考文体，同阅读理解一样，占据了语文学习的另外&ldquo;半边天&rdquo;。所以本次课程中，我们将对你平时写作中遇到的上述困惑进行指导，每一个难题都会给你相应&ldquo;妙招&rdquo;去应对，避免出现&ldquo;四不像&rdquo;文章，让你的作文分数迅速提高。尤其是议论文写作，我们将主要从素材积累、审题、立意、构思、开头、结尾等方面进行突破，帮你打造满分作文。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>学习建议：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">高一建议以写人记事、写景抒情类文章的写作为主，当然学有余力的同学，也可以逐步尝试议论文的写作。高二高三同学，以议论文、议论随笔文的写作为主。但无论哪种文体，写作方面的技巧都是可以借鉴的噢，同学要注意灵活运用，多维借鉴，这样才能让你&ldquo;妙笔生花&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355896073.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355896101.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/19/1q9qmo0c62n4toug23im7t7m061355896130.jpg\" alt=\"\" />\";}', '360.00', '18', '31104000', '82', '3301', null, '', '1355298965', '1355298965', '0', '', '<pre><span style=\"font-size: small;\">高一高二高三学生，写作没有素材的学生，不会审题立意的学生，作文想突破50分的学生，想尽快突破写作瓶颈的学生</span></pre>', '<pre><span style=\"font-size: small;\">1.学好高一的记叙文写作，为高二高三议论文写作打好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.写作方法借鉴多元化，开拓思路，下笔有神，言之有物。</span></pre>', '3701');
