INSERT INTO `kc_course_pack` VALUES ('14329', '高一语文全年课程', '4', '', '', '', '', 'a:0:{}', 'a:0:{}', '440.00', '22', '31104000', '8', '3301', null, '', '1355370349', '1355370349', '0', '', '', '', '3703');
INSERT INTO `kc_course_pack` VALUES ('14330', '高二语文全年课程', '4', '', '', '', '', 'a:0:{}', 'a:0:{}', '360.00', '18', '31104000', '4', '3301', null, '', '1355370552', '1355370552', '0', '', '', '', '3703');
INSERT INTO `kc_course_pack` VALUES ('14331', '化学核心考点串讲课程', '3', '把握核心考点，轻松赢得高分', '北京高级教师讲授', '', '/data/default/kecheng/847/7be/hc0jhires395e539ijue6794t21355391513.jpg', 'a:2:{i:0;s:70:\"/data/default/kecheng/c1f/803/945el44e0qrjiei7ilfcd7kv201355476753.jpg\";i:1;s:70:\"/data/default/kecheng/c1f/803/945el44e0qrjiei7ilfcd7kv201355476786.jpg\";}', 'a:1:{i:1005;s:1261:\"<pre><strong><span style=\"font-size: small;\">学习化学核心考点串讲的你</span></strong><br /><span style=\"font-size: small;\">1.化学知识繁多、复杂，不能抓住核心知识，主抓方向偏离。</span><br /><span style=\"font-size: small;\">2.重难点知识学习困难，找不到突破口。</span><br /><span style=\"font-size: small;\">3.知识掌握范范，不精准，对于重点解题方法、技巧不能有效运用于解题中。</span><br /><strong><span style=\"font-size: small;\">德智课程帮助你</span></strong><br /><span style=\"font-size: small;\">1.提炼中考核心知识点，让学生轻松把握中考考向，快速提分。</span><br /><span style=\"font-size: small;\">2.重难点题目，详细分类讲解，总结规律，提炼方法，轻松突破。</span><br /><span style=\"font-size: small;\">3.知识、题目精确解析，精析方法、技巧、规律运用技巧，轻松快速解析。</span><br /><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/t1vtho43f171o8dcak12a4frk11355473139.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/tha8fnld3s4t5ft3327asidul51355474658.jpg\" alt=\"\" />\";}', '160.00', '8', '31104000', '169', '3301', null, '', '1355391529', '1355391529', '0', '全国各版本初三学生', '<pre><span style=\"font-size: small;\">全国各版本初三学生</span></pre>', '<span style=\"font-size: small;\">1.能够熟练运用质量守恒定律、金属活动性顺序表等，解决常见的化学问题。</span><br /><span style=\"font-size: small;\">2.分析实验装置，制取原理，实验方法，收集方法，快速解决实验难题。</span><br /><span style=\"font-size: small;\">3.归纳总结重难点知识，揭秘易错点，培养逻辑思维能力，轻松学习化学！</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14332', '数学核心考点串讲课程', '3', '中考必考重难知识点，一网打尽。', '重难点，全面掌握', '', '/data/default/kecheng/c1f/803/kl134855q9i7rihmssmq7ndao21355450312.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/c1f/803/t7vsmjm39qc4mab7jqucgqe6k31355479252.jpg\";i:1;s:70:\"/data/default/kecheng/c1f/803/t7vsmjm39qc4mab7jqucgqe6k31355479260.jpg\";i:2;s:70:\"/data/default/kecheng/c1f/803/t7vsmjm39qc4mab7jqucgqe6k31355479265.jpg\";}', 'a:1:{i:1002;s:1869:\"<br /><span style=\"font-family: %value; font-size: small;\"><strong>进行一轮复习的你：</strong></span><br /><span style=\"font-family: %value; font-size: small;\">1.知识点繁多，分散，学校复习类似上新课，一堆知识点扑面而来，让你抓不到重点。</span><br /><span style=\"font-family: %value; font-size: small;\">2.复习时间紧凑，对难点知识还没弄懂，又开始了下一个知识点的轮番轰炸，结果不懂的还是没弄懂。</span><br /><span style=\"font-family: %value; font-size: small;\">3.开始题海战术，大把时间浪费在重复无用的题目上。</span><br /><span style=\"font-family: %value; font-size: small;\"><strong>德智课程帮助你：</strong></span><br /><span style=\"font-family: %value; font-size: small;\">1.提炼中考必考难点知识，高效复习，节省更多的时间与精力用于攻克中考失分点！</span><br /><span style=\"font-family: %value; font-size: small;\">2.重难点细致讲解，典型例题解决知识的疑点、难点、易错点。王牌例题提升你对知识应用能力。在复习知识点的同时，增强对知识的应用能力，达到双赢！</span><br /><span style=\"font-family: %value; font-size: small;\">3.提炼和总结解题方法，解题规律，让你由一道题会做一类型的题，摆脱题海战术！</span><br /><span style=\"font-family: %value; font-size: small;\">4.每堂课都至少学习一种数学思想方法，慢慢开拓你的数学思维，达到质的飞跃！</span><br /><span style=\"font-family: %value; font-size: small;\"><strong>你的精彩课堂：</strong></span>\r\n<p><img src=\"/data/default/upload/site/558/e94/90625/2012/12/14/t7vsmjm39qc4mab7jqucgqe6k31355477465.jpg\" alt=\"\" /></p>\r\n<img src=\"/data/default/upload/site/558/e94/90625/2012/12/14/t7vsmjm39qc4mab7jqucgqe6k31355477535.jpg\" alt=\"\" />\";}', '440.00', '22', '31104000', '263', '3301', null, '', '1355449602', '1355449602', '0', '全国各版本初三学生', '<pre><span style=\"font-family: %value; font-size: small;\">全国各版本初三学生</span></pre>', '<pre><span style=\"font-family: %value; font-size: small;\"><strong>课程学习效果：</strong></span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.掌握中考重难点：全等与轴对称、函数与方程不等式、圆、二次函数、相似、四边形。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.掌握思想方法：分类讨论思想、方程思想、数形结合思想、换元思想、转化思想等。</span></pre>\r\n<pre>&nbsp;</pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14333', '物理核心考点串讲课程', '3', '核心考点各个击破', '北京名校高级教师', '', '/data/default/kecheng/c1f/803/g3a0c5t5ktqqnjn0k9ilsje7j21355449832.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/c1f/803/g3a0c5t5ktqqnjn0k9ilsje7j21355449925.jpg\";i:1;s:70:\"/data/default/kecheng/c1f/803/g3a0c5t5ktqqnjn0k9ilsje7j21355449930.jpg\";i:2;s:70:\"/data/default/kecheng/c1f/803/g3a0c5t5ktqqnjn0k9ilsje7j21355449935.jpg\";}', 'a:1:{i:1004;s:2542:\"<span style=\"font-size: small;\"><strong>面临中考的你，是否存在以下焦虑？</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.面对众多板块知识的复习，不知道哪些是中考的核心考点，不知如何高效复习。</span><br /><span style=\"font-size: small;\">2.基础知识不牢固，一遇到概念类问题就容易丢分。</span><br /><span style=\"font-size: small;\">3.遇到比较复杂的题目，更是一头雾水，无从下手。</span><br /><span style=\"font-size: small;\">4.遇到力学综合性题目，不知道如何分析，找不到任何思路。</span><br /><span style=\"font-size: small;\">5.不知道如何巧妙的快速解题，考试无法取得高分。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.精选中考物理的核心考点，传授你最高效的复习方法。</span><br /><span style=\"font-size: small;\">2.帮你梳理物理概念，讲解物理规律，为你的物理学习打下坚实的基础。</span><br /><span style=\"font-size: small;\">3.教你如何提取题目的有用信息，如何整理思路，利用物理规律解题。</span><br /><span style=\"font-size: small;\">4.总结典型题目的解题规律与解题方法，教会你解决各类物理问题。</span><br /><span style=\"font-size: small;\">5.帮你搜集中考的考试技巧，改掉各种不好的考试习惯，迅速提分！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong><br /></span><img src=\"/data/default/upload/formula/54b/530/system/2012/12/14/g3a0c5t5ktqqnjn0k9ilsje7j21355452192.png\" alt=\"\" /><br /><br /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/14/g3a0c5t5ktqqnjn0k9ilsje7j21355451996.png\" alt=\"\" />\r\n<div class=\"user_nr_left user_box\">\r\n<pre class=\"subject_dis subject_dis_1003\"><span style=\"color: #ff0000; font-size: small;\"><br /></span><span style=\"font-size: small;\"><span style=\"color: #ff0000;\">温馨提示：</span><span style=\"color: #ff0000;\"><br />1、德智课程质量要求严格，经过多个严谨的制作流程完成，每一流程都要花费很多精力与时间。为保证课程质量并兼顾学习进度，课程将采用陆续更新的方式上线，每周至少上线1～2节课，敬请关注。</span><span style=\"color: #ff0000;\"><br />2、课时列表会根据课程标准、考试说明及教材变动等做及时的更新和调整。</span></span></pre>\r\n</div>\r\n<br /><br />\";}', '260.00', '13', '31104000', '218', '3301', null, '', '1355449977', '1355449977', '0', '初三学生', '<pre><span style=\"font-size: small;\">初三学生，中考一轮复习使用</span></pre>', '<span style=\"font-size: small;\">1.集中解决力学的基本概念与规律，学会解力学的综合题目，尤其压强、浮力、功、功率、机械效率等最核心的知识点。</span><br /><span style=\"font-size: small;\">2.攻克电学的硬骨头，重点在欧姆定律、电功、电功率、电路故障分析专题。</span><br /><span style=\"font-size: small;\">3.知道如何分析浮力及其应用的综合题，找到解题思路。</span><br /><span style=\"font-size: small;\">4.对于功、功率和机械效率的计算题，将会掌握解题方法，找到解题规律与技巧。</span><br /><span style=\"font-size: small;\">5.对于电路的题目，找到正确的解题方法，掌握解题规律。</span><br /><span style=\"font-size: small;\">6.提升物理考试技巧，在考试中得高分。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14334', '高中英语读写能力综合提升（含阅读技巧、词汇解读）', '2', '', '', '', '/data/default/kecheng/b6f/f58/28vfh72nq6u8qpmolghn7sv7271355802611.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1f6/9b3/g5ecqa47c15a6dd3n2gdmqllf61356077304.png\";i:1;s:70:\"/data/default/kecheng/1f6/9b3/g5ecqa47c15a6dd3n2gdmqllf61356077316.png\";i:2;s:70:\"/data/default/kecheng/1f6/9b3/g5ecqa47c15a6dd3n2gdmqllf61356077323.png\";}', 'a:1:{i:1003;s:1178:\"<pre><span style=\"font-size: small;\"><strong>单词和阅读&ldquo;两手都要抓，两手都要硬&rdquo;的你</strong><br />1.机械记忆单词，缺乏方法技巧，单词记不住或者背的快忘的更快。<br />2.篇章读不懂，阅读速度慢， 理解不准确，细节题型经常丢分。<br />3.孤零零的死记硬背或者盲目阅读，不能把所学词汇和阅读理解结合学习，往往事倍功半。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong><br />1.精选课标重点话题，一篇文章发散学习，阅读技巧和单词记忆一次搞定。<br />2.节省宝贵时间，避免书山题海，吃透一篇文章，掌握多种技巧。<br />3.学会联想学习，在记忆词汇的同时理解文章，在阅读文章的同时掌握单词用法。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/17/r9lju7i8o26m82kpn1i22dd2i01355734273.png\" alt=\"\" /><img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/17/r9lju7i8o26m82kpn1i22dd2i01355734309.png\" alt=\"\" width=\"473\" height=\"328\" />\";}', '600.00', '30', '31104000', '104', '3301', null, '', '1355452409', '1355452409', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高中学生</span></pre>', '<pre><span style=\"font-size: small;\">1.学一篇吃透一篇，掌握课标重点话题文章的阅读以及核心词记忆。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握如何在语境下记忆单词并熟练使用词根法、联想法等多种记忆单词方法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握人物、情感、社科等多类型重点话题文章的阅读技巧，提升阅读速度和质量。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.通过1节阅读课+2节词汇课，吃透每一篇文章，产生1+2＞3的效果。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14335', '中考核心考点串讲四科联报课程', '3', '', '', '', '/data/default/kecheng/c1f/803/g3a0c5t5ktqqnjn0k9ilsje7j21355462521.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/c1f/803/g3a0c5t5ktqqnjn0k9ilsje7j21355465221.jpg\";i:1;s:70:\"/data/default/kecheng/c1f/803/945el44e0qrjiei7ilfcd7kv201355476667.jpg\";i:2;s:70:\"/data/default/kecheng/c1f/803/fh1pjga23ur4q8h8s4t78emop11355480754.jpg\";}', 'a:4:{i:1002;s:2063:\"<pre><span style=\"font-family: %value; font-size: small;\"><strong>进行一轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.知识点繁多，分散，学校复习类似上新课，一堆知识点扑面而来，让你抓不到重点。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.复习时间紧凑，对难点知识还没弄懂，又开始了下一个知识点的轮番轰炸，结果不懂的还是没弄懂。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.开始题海战术，大把时间浪费在重复无用的题目上。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.提炼中考必考难点知识，高效复习，节省更多的时间与精力用于攻克中考失分点！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.重难点细致讲解，典型例题解决知识的疑点、难点、易错点。王牌例题提升你对知识应用能力。在复习知识点的同时，增强对知识的应用能力，达到双赢！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.提炼和总结解题方法，解题规律，让你由一道题会做一类型的题，摆脱题海战术！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">4.每堂课都至少学习一种数学思想方法，慢慢开拓你的数学思维，达到质的飞跃！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><img src=\"/data/default/upload/site/558/e94/90625/2012/12/14/t7vsmjm39qc4mab7jqucgqe6k31355478270.jpg\" alt=\"\" /></span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><img src=\"/data/default/upload/site/558/e94/90625/2012/12/14/t7vsmjm39qc4mab7jqucgqe6k31355478293.jpg\" alt=\"\" /></span></pre>\";i:1003;s:1325:\"<pre><span style=\"font-size: small;\"><strong>面临中考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从句先行词掌握不清，时态语态易混淆，近似词组搭配不会区分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.阅读理解经常丢分，写作无从下笔，不知从哪儿开始复习，如何复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时间紧，知识多，学习没动力，复习没成效，逐渐丧失信心和兴趣。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.理清复习思路，掌握复习节奏，全面高效复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从语法、阅读、词汇、写作四个方面进行整合复习，科学合理节省你的复习时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.剖析阅读解题方法，总结归纳易混词组，动词时态归纳讲解，让你的复习学无遗漏！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/17/r9lju7i8o26m82kpn1i22dd2i01355732000.png\" alt=\"\" width=\"516\" height=\"398\" /></span></pre>\";i:1004;s:2480:\"<pre><span style=\"font-size: small;\"><strong>面临中考的你，是否存在以下焦虑？</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.面对众多板块知识的复习，不知道哪些是中考的核心考点，不知如何高效复习。</span><br /><span style=\"font-size: small;\">2.基础知识不牢固，一遇到概念类问题就容易丢分。</span><br /><span style=\"font-size: small;\">3.遇到比较复杂的题目，更是一头雾水，无从下手。</span><br /><span style=\"font-size: small;\">4.遇到力学综合性题目，不知道如何分析，找不到任何思路。</span><br /><span style=\"font-size: small;\">5.不知道如何巧妙的快速解题，考试无法取得高分。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong><strong></strong></span><br /><span style=\"font-size: small;\">1.精选中考物理的核心考点，传授你最高效的复习方法。</span><br /><span style=\"font-size: small;\">2.帮你梳理物理概念，讲解物理规律，为你的物理学习打下坚实的基础。</span><br /><span style=\"font-size: small;\">3.教你如何提取题目的有用信息，如何整理思路，利用物理规律解题。</span><br /><span style=\"font-size: small;\">4.总结典型题目的解题规律与解题方法，教会你解决各类物理问题。</span><br /><span style=\"font-size: small;\">5.帮你搜集中考的考试技巧，改掉各种不好的考试习惯，迅速提分！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong><br /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/14/g3a0c5t5ktqqnjn0k9ilsje7j21355452192.png\" alt=\"\" /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/14/g3a0c5t5ktqqnjn0k9ilsje7j21355451996.png\" alt=\"\" /></span></pre>\r\n<pre><span style=\"font-size: small;\">温馨提示：</span></pre>\r\n<pre><span style=\"color: #ff0000; font-size: small;\">1、德智课程质量要求严格，经过多个严谨的制作流程完成，每一流程都要花费很多精力与时间。为保证课程质量并兼顾学习进度，课程将采用陆续更新的方式上线，每周至少上线1～2节课，敬请关注。</span></pre>\r\n<pre>&nbsp;</pre>\r\n<pre><span style=\"color: #ff0000; font-size: small;\">2、课时列表会根据课程标准、考试说明及教材变动等做及时的更新和调整。</span></pre>\r\n<pre><span style=\"font-size: small;\"><br /><br /></span></pre>\";i:1005;s:1384:\"<pre><strong><span style=\"font-size: small;\">学习化学核心考点串讲的你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.化学知识繁多、复杂，不能抓住核心知识，主抓方向偏离。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.重难点知识学习困难，找不到突破口。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.知识掌握范范，不精准，对于重点解题方法、技巧不能有效运用于解题中。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">德智课程帮助你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.提炼中考核心知识点，让学生轻松把握中考考向，快速提分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.重难点题目，详细分类讲解，总结规律，提炼方法，轻松突破。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.知识、题目精确解析，精析方法、技巧、规律运用技巧，轻松快速解析。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<pre><strong><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/t1vtho43f171o8dcak12a4frk11355473139.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/14/tha8fnld3s4t5ft3327asidul51355474658.jpg\" alt=\"\" /></span></strong></pre>\";}', '1100.00', '55', '31104000', '606', '3301', null, '', '1355462534', '1355462534', '1', '', '<pre><span style=\"font-size: small;\">全国各版本初三学生</span></pre>', '<span style=\"font-size: small;\">1.掌握中考重难点：全等与轴对称、函数与方程不等式、圆、二次函数、相似、四边形。</span><br /><span style=\"font-size: small;\">2.掌握思想方法：分类讨论思想、方程思想、数形结合思想、换元思想、转化思想等。</span><br /><span style=\"font-size: small;\">3.归纳总结重难点知识，揭秘易错点，培养逻辑思维能力，轻松学习！</span><br /><span style=\"font-size: small;\">4.攻克电学的硬骨头，重点在欧姆定律、电功、电功率、电路故障分析专题。</span><br /><span style=\"font-size: small;\">5.对于电路的题目，找到正确的解题方法，掌握解题规律。</span><br /><span style=\"font-size: small;\">6.提升物理考试技巧，在考试中得高分。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14336', '初一下学期三科联报课程', '3', '', '', '', '/data/default/kecheng/729/01d/r9lju7i8o26m82kpn1i22dd2i01355739209.jpg', 'a:0:{}', 'a:3:{i:1001;s:2178:\"<pre><span style=\"font-size: small;\"><strong>进入初一下的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">学习了上学期的&ldquo;成长&rdquo;系列课程，你一定养成了良好的语文学习习惯，收获了不少的成长体验吧。那么这学期的你，是不是想继续让你的语文学习领先起跑？收获更多的人生感悟？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong style=\"font-size: small;\">我们的课程特色</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本学期我们仍然以专题形式授课，设计了&ldquo;腹有诗书气自华&mdash;&mdash;人情练达篇&rdquo;系列课程。前六节课分别围绕&ldquo;母爱&rdquo;&ldquo;父爱&rdquo;&ldquo;祖孙情&rdquo;等情感专题设置课程，每节课分为&ldquo;美文赏读&rdquo;&ldquo;中考链接&rdquo;&ldquo;写作训练&rdquo;三个环节，分别从情感体验、阅读技巧、写作方法三个角度予以指导。阅读与写作，仍然作为你语文学习的重点，但是对生活的中真情实感的体悟，也是你必备的&ldquo;人生课堂&rdquo;噢。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.结合最新中考试题，从命题角度教你扫清现代文阅读障碍，提前与中考&ldquo;面对面&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.每节课都从不同的角度给你写作的技巧，整个课程结束，写作技巧全面覆盖，应付考试作文，绰绰有余。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.带你感受亲情，这是我们本次系列课程要给你的另外一项重点内容。学会体察父母之爱、祖孙之爱、师生之爱，甚至是人间大爱，这样你才会有一颗感恩的心。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/31/6ogpu1b8035m6iqbdjl5230i861356919512.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/31/6ogpu1b8035m6iqbdjl5230i861356919523.jpg\" alt=\"\" />\";i:1002;s:1216:\"<pre><span style=\"font-size: small;\"><strong>学习初一下的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.平行线的性质与判定，不能灵活贯通运用 ，证明过程陷入僵局。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.解二元一次方程组，不能选择合适的方法解答，增加计算过程，导致出错。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.解字母系数的不等式，不能正确解答，没有分类讨论意识。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.解不等式组，无法正确判断解集。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.知识再次讲解配合典型例题，帮助你消化性质与判定，做到融会贯通。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.&ldquo;观察法&rdquo;教你面对二元一次方程组，如何针对性选择解法，简化计算过程，提高正确率。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对含有字母系数的不等式，例题由简单到复杂，教会你求解原理的基础上，进一步提高你的变通能力</span></pre>\";i:1003;s:1313:\"<pre><span style=\"font-size: small;\"><strong>进入初一下的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.学习兴趣不高，不喜欢大声朗读，单词忘的快。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.课前不会预习，课后不会做题，课业负担重，常常手忙脚乱。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.写作成为老大难问题，不是有口说不出，就是肚子里没词儿。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从不同词性展开讲解，逐一分析，扩充你的词汇量。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学习整理写作思路，收集写作素材，建立正确的写作方法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.课上吸收课上消化，课后强化训练，全面提升综合能力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<span style=\"font-size: small; color: #ff0000;\"><img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/18/151je7be3hg9vel43cppuhs4s51355823550.png\" alt=\"\" /><img src=\"/data/default/upload/site/c8f/bbc/906/2012/12/18/151je7be3hg9vel43cppuhs4s51355823565.png\" alt=\"\" /></span>\";}', '540.00', '27', '31104000', '314', '3301', null, '', '1355715680', '1355715680', '0', '想复习上学期、预习下学期知识的学生', '<pre><span style=\"font-size: small;\">全国各版本初一下学期学生</span></pre>', '<pre><span style=\"font-size: small;\">1.吃透动词、数词、介词、副词等词类的相关知识点， 能够区分、记忆、运用。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明确中考现代文阅读的考查角度，针对不同角度，学会每个角度的应答策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.学会高分作文的写作技巧，养成从生活中积累素材的好习惯。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.熟练掌握平行线的性质与判定以及解决二元一次方程组，体会消元、转化思想，分类讨论思想，整体思想在解题中的运用。</span></pre>\r\n<pre>&nbsp;</pre>\r\n<pre>&nbsp;</pre>\r\n<pre>&nbsp;</pre>\r\n<pre><span style=\"font-size: small;\"><br /></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14337', '初二下学期四科联报课程', '3', '', '', '', '/data/default/kecheng/729/01d/94qnsasmrsjjk6hc82phuc8ed01355731554.jpg', 'a:0:{}', 'a:4:{i:1001;s:2252:\"<pre><span style=\"font-size: small;\"><strong>初二下学期的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">学习了初一全年及初二上学期的记叙文，一定收获不小吧。那么这学期，我们主要给你讲一讲中考语文的另外一种必考文体：说明文。你知道说明方法有哪些吗？各有什么作用？会找说明对象的特征吗？知道如何筛选说明文的信息吗？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本学期我们仍然以专题的形式设置课程，每一个专题即是一类说明文，专题阅读与写作相结合，方法指导与具体实践相结合。如果你弄懂了某一个专题，其实你就掌握了某一大类的说明文。当然，我们的专题设置，覆盖了所有中考说明文考查的热门题材和必考内容哦。本次课程，除了帮你轻松搞定说明文阅读之外，还将带你穿越星空，探秘人体，感悟世间百态，开拓你的阅读视野。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.结合最新中考文章，讲解说明文的说明方法及作用。说明文的阅读方法指导，教你准确筛选说明文信息。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在写作方面教你拓宽思路，展开联想，学会托物言志的写作手法。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong style=\"font-size: small;\">你的学习效果：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.掌握说明文的说明方法及作用，能够准确抓住说明对象的特征。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学会托物言志、借物抒情的表达方式，拓展写作思维。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong style=\"font-size: x-small;\">你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357376069.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2013/01/05/96cm6i44egh20477q8re5k53951357376084.jpg\" alt=\"\" />\";i:1002;s:1677:\"<pre><span style=\"font-size: small;\"><strong>学习初二下的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">1.</span>反比例函数的独特性质你是否熟练掌握？</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">2.</span>四边形的角，线段，面积求值问题是否是你一大难题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">3.</span>四边形问题中，如何巧妙做辅助线，你是否有所了解？</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">4.</span>四边形中存在哪些基本图形，能助你快速解题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">1.</span>反比例函数的深层剖析，挖掘反比例函数的特性，典型例题辅助理解。</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">2.</span>总结归纳基本图形，教你如何添加辅助线，化未知为已知。</span></pre>\r\n<pre><strong style=\"font-size: small;\">课程学习效果：</strong></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">1.</span>熟练掌握：分式的有关概念，易错点；反比例函数的性质；分清四边形、矩形、正方形、菱形的区别于联系。</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">2.</span>帮你寻找添加辅助线的思路，而不是凭着自己的感觉去胡乱添加。</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">3.</span>熟练掌握基本图形模型。</span></pre>\";i:1003;s:1862:\"<pre><span style=\"font-size: small;\"><strong>&ldquo;</strong><strong>迷糊</strong><strong>Style&rdquo;</strong><strong>的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.对已学过的时态语态、常用词组、固定搭配不会区分，经常混淆不清。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.句子成分、种类分不清楚，读不明白，说不清楚，经常答非所问。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.不能系统归纳所学知识，不知如何为初三做准备，知识储备和信心都不足。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.系统归纳所学易考易错词组，对比记忆，强化练习，熟练掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.全面总结动词重点时态，横向与纵向结合讲解，轻松突破时态难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.深度剖析词类、句子，掌握基础才是王道，为从句、写作、阅读等难点铺路。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果：</strong><strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.彻底扫清过去、现在、完成、将来四大时态重难点，易考点易错点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.复习形容词、副词，掌握句子种类区分，夯实所学基础知识。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.必考、易考词组易混辨析，归纳总结巧妙记忆，为你阅读、写作储备词汇量。</span></pre>\r\n<pre><span style=\"font-size: small;\">稀里糊涂or迎头赶上？看清方向，理清思路，夯实初二，迎接初三，努力从来不晚！拒绝&ldquo;迷糊Style&rdquo;！</span></pre>\";i:1004;s:2127:\"<pre><span style=\"font-size: small;\"><strong>学习初二下学期物理课程的你，是否存在以下焦虑？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"> 1．没有深刻理解重力、弹力、摩擦力的概念与特点，遇到概念题老出错。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 2．对于运动和力的关系总是搞不清楚，一遇到问题老是混淆。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 3．对于压力压强知识，一遇到计算题就发懵。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 4．觉得浮力、浮力的应用太难了，计算题比较难解。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 5．功、功率、机械效率更是难上加难，综合题的题干太长，根本读不懂什么意思，更不知道如何分析解题。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 6．对简单机械的知识，搞不清动滑轮绳子的条数，不知道如何分析受力情况。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"> 1．弄清最基本的物理概念与规律，完美拿下概念理解类题目。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 2．老师带领大家一起分析题目，理清思路，让你看到题目不再迷惑。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 3．对于综合性的答题，老师带你一起读题干，抓住题目的关键点，教你如何读取有用信息。</span></pre>\r\n<pre><span style=\"font-size: small;\"> 4．传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/999/600/2390/2012/12/17/94qnsasmrsjjk6hc82phuc8ed01355730361.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/17/94qnsasmrsjjk6hc82phuc8ed01355730378.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/999/600/2390/2012/12/17/94qnsasmrsjjk6hc82phuc8ed01355730389.jpg\" alt=\"\" />\";}', '720.00', '36', '31104000', '297', '3301', null, '', '1355716873', '1355716873', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初中二年级学生</span></pre>', '<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">1</span>．老师带你分析题目，理清思路，让你看到题目不再迷惑。</span></pre>\r\n<pre><span style=\"font-size: small;\"><span lang=\"EN-US\">2</span>．对于综合性的答题，老师带你一起读题干，抓住题目的关键点，教你如何读取有用信息。<span lang=\"EN-US\"><br /></span><span lang=\"EN-US\">3</span>．传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各科考试！<span lang=\"EN-US\"><br /> <!--[if !supportLineBreakNewLine]--><br /> <!--[endif]--></span></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14339', '高一高二高三语文联报课程1', '4', '', '', '', '', 'a:0:{}', 'a:0:{}', '1160.00', '0', '31104000', '0', '3301', null, '', '1355882616', '1355882616', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高一高二高三年级学生</span></pre>', '', '3702');
INSERT INTO `kc_course_pack` VALUES ('14351', '高三测试课程包', '1', '', '', '神鼎飞丹砂', '', 'a:0:{}', 'a:1:{i:1001;s:18:\"撒的方式打法\";}', '666.00', '2', '31104000', '0', '3301', null, '', '1355911890', '1355911890', '0', '', '神鼎飞丹砂', '是代付大师傅', '3702');
INSERT INTO `kc_course_pack` VALUES ('14353', '高三测试课程包222', '1', '', '', 'ads范德萨飞', '', 'a:0:{}', 'a:1:{i:1001;s:15:\"ads范德萨飞\";}', '4434.00', '2', '31104000', '0', '3301', null, '', '1355913892', '1355913892', '0', '', '是代付大师傅', '神鼎飞丹砂', '3702');
INSERT INTO `kc_course_pack` VALUES ('14354', '高一下学期数学预习课（数列）', '2', '', '', '', '/data/default/kecheng/486/b54/15cf9g8q6k61ur7lj90tgiuv811355983504.jpg', 'a:0:{}', 'a:1:{i:1002;s:1210:\"<pre><span style=\"font-size: small;\"><strong>正在学数列的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.数列的一些概念，如通项公式、递推公式等难以理解。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.数列公式多，方法多，遇到问题常常不知道该如何入手。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.深入剖析概念以及公式，在理解的基础上记忆，并能达到灵活运用的目的。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.分类解析常见题型，让你轻松掌握数列基本问题。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/54b/530/system/2012/12/31/lrfd4de2ucm9lkog45g3iamh871356937843.png\" alt=\"\" /><br /><br /></strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong><br /></strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong><br /></strong></span></pre>\";}', '60.00', '3', '31104000', '40', '3301', null, '', '1355983519', '1355983519', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高一学生</span></pre>', '<pre><span style=\"font-size: small;\">掌握：数列的概念、通项公式、递推公式、等差数列等。</span></pre>\r\n<pre><span style=\"font-size: small;\">熟练：等差数列的通项公式，等差数列的前n项和公式等。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14355', '高二下学期理科数学预习课（导数）', '2', '', '', '', '/data/default/kecheng/486/b54/15cf9g8q6k61ur7lj90tgiuv811355983740.jpg', 'a:0:{}', 'a:1:{i:1002;s:1289:\"<pre><span style=\"font-size: small;\"><strong>正在学导数的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.导数概念抽象，难以理解。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.导数的求导公式多，求复合函数导数时常忽略内层函数的导数。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.求函数过某点的切线时，常忽略点不在函数图象上的情况。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.深入解读概念，如导数概念，导数的几何意义等让你对相关概念的理解更透彻。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.一题多解，一题多变，多角度全方位分析问题，带你轻松学好导数，赢在起点！</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/54b/530/system/2012/12/31/lrfd4de2ucm9lkog45g3iamh871356938459.png\" alt=\"\" /><br /></strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong><br /></strong></span></pre>\";}', '60.00', '3', '31104000', '22', '3302', null, '', '1355983748', '1355983748', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高二理科学生</span></pre>', '<pre><span style=\"font-size: small;\">掌握：导数的概念，导数的几何意义，导数的运算等。</span></pre>\r\n<pre><span style=\"font-size: small;\">熟练：用定义求导数的步骤，基本初等函数的求导公式，导数的运算法则等。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14356', '高二下学期文科数学预习课（导数）', '2', '', '', '', '/data/default/kecheng/486/b54/15cf9g8q6k61ur7lj90tgiuv811355983804.jpg', 'a:0:{}', 'a:1:{i:1002;s:1244:\"<pre><span style=\"font-size: small;\"><strong>正在学导数的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.导数概念抽象，难以理解。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.导数的求导公式多，记不住。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.求函数过某点的切线时，常忽略点不在函数图象上的情况。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.深入解读概念，如导数概念，导数的几何意义等让你对相关概念的理解更透彻。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.一题多解，一题多变，多角度全方位分析问题，带你轻松学好导数，赢在起点！</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/54b/530/system/2012/12/31/lrfd4de2ucm9lkog45g3iamh871356938459.png\" alt=\"\" /><br /></strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><strong><br /></strong></span></pre>\";}', '60.00', '3', '31104000', '21', '3303', null, '', '1355983812', '1355983812', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高二文科学生</span></pre>', '<pre><span style=\"font-size: small;\">掌握：导数的概念，导数的几何意义，导数的运算等。</span></pre>\r\n<pre><span style=\"font-size: small;\">熟练：用定义求导数的步骤，基本初等函数的求导公式，导数的运算法则等。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14357', '中考物理二轮复习课程', '3', '重点出击,攻克必考点,掌握方法', '北京名校高级教师', '', '/data/default/kecheng/486/b54/d7qc4st3ti4m1oapjruschdjo51355988234.jpg', 'a:0:{}', 'a:1:{i:1004;s:1405:\"<pre><span style=\"font-size: small;\"><strong>面对中考的压力，你是否会感到以下焦虑</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.面对庞杂的物理知识，不知道哪些是重点，不知道如何有效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.虽然选择题与填空题比较简单，但还是会丢分，基础知识掌握不牢！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对于力学的复杂题目，一看就懵，不知道如何分析，更不知道怎么解题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.面对众多的电学知识，电功、电功率、欧姆定律、电路故障分析，更不知道如何面对。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.挑选最重点的知识进行复习，用最短的时间，达到最好的效果。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.帮你梳理中考的要求，结合近年的中考真题，细致分析，抓住重点，牢固掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.重点讲解力学中较难的知识，专门解决综合题！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.对电学的重点内容设置了相对较多的课时，重点突破各个重点板块，并配有专题总结分析。</span></pre>\";}', '200.00', '10', '31104000', '142', '3301', null, '', '1355987168', '1355987168', '0', '面临中考的初三学生', '<pre><span style=\"font-size: small;\">面临中考的初三学生，或者水平较高的初二学生</span></pre>', '<pre><span style=\"font-size: small;\"><strong></strong>通过本课程的学习，你将在以下几个方面有所突破：</span></pre>\r\n<pre><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会各类题的解法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14358', '高一高二高三语文联报课程', '4', '', '', '', '/data/default/kecheng/1f6/9b3/j6sc7rdv4rfrr8c4g5qcigu5921356067446.jpg', 'a:0:{}', 'a:1:{i:1001;s:2376:\"<pre><span style=\"font-size: small;\"><strong>你的学习困惑：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">高中语文学习，你还在死磕书本吗？是不是有&ldquo;学习容易得分难&rdquo;的尴尬？&ldquo;阅读&rdquo;和&ldquo;写作&rdquo;两座大山，你能否成功翻越？阅读理解不透，作文立意不新，文言如同&ldquo;天书&rdquo;？&hellip;&hellip;不要急，德智课堂有妙招。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">德智高中语文课程按照高一高二高三的年级特点，设置了相应的课程，比如高一的阅读和写作以&ldquo;写人记事&rdquo;类为主，高二高三的课程则以&ldquo;说明文&rdquo;和&ldquo;议论文&rdquo;为主。高三一轮复习课程帮你制定系统的复习规划，重点难点必考点，&ldquo;一网打尽&rdquo;。二轮复习重点&ldquo;捕捞&rdquo;高考的高频高点，直接向高考进发！ </span></pre>\r\n<pre><span style=\"font-size: small;\">1.阅读分别从小说、散文、古诗文等高考必考文体入手，教你理解文章主旨、把握作者情感、明确作者表达意图等考查角度的答题思路，教你&ldquo;把阅读看成是与作者对话&rdquo;，精准作答。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.文言文独家秘籍让你熟记18个必考虚词，五大重点文言句式，还有高考文言文的变式考法&hellip;&hellip;其实读懂&ldquo;天书&rdquo;很容易。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.戚峰老师的议论文写作，从选材、审题、立意等角度，让你的作文有新意、有高度、有文采，帮你攻克高考作文的必考文体&mdash;&mdash;议论文这一堡垒。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083802.png\" alt=\"\" /><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083818.png\" alt=\"\" /><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083836.png\" alt=\"\" /><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083858.png\" alt=\"\" />\";}', '1160.00', '58', '93312000', '82', '3301', null, '', '1355987392', '1355987392', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高一高二高三年级学生</span></pre>', '<pre><span style=\"font-size: small;\">1.三年完整的语文学习规划，明确不同阶段的不同学习目标和需求，让学习不盲目。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明确阅读现代文、古诗文的一般考查内容和考查角度，具备相应的应答策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟悉高中阶段各种文体的写作，摆脱&ldquo;四不像&rdquo;作文的困扰，向50分作文冲刺！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.高一高二结结实实打基础，高三轻轻松松大冲刺！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14359', '高一高二高三英语联报课程', '4', '', '', '', '/data/default/kecheng/1f6/9b3/g5ecqa47c15a6dd3n2gdmqllf61356054793.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631355988395.png\";i:1;s:70:\"/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631355988397.png\";i:2;s:70:\"/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631355988399.png\";}', 'a:1:{i:1003;s:1996:\"<span style=\"font-size: small;\"><strong>学习高中英语的你</strong></span><br /><span style=\"font-size: small;\">1.知识难度、深度远超初中内容，课上老师讲解跟不上，听不懂。</span><br /><span style=\"font-size: small;\">2.课业负担重，压力大，努力半天却收效甚微，没有整体的三年规划。</span><br /><span style=\"font-size: small;\">3.做阅读单词不会，做单选语法不通，不知从哪里学起？该学什么？</span><br /><span style=\"font-size: small;\">4.句型、固定搭配、习惯用法、语法等方面的基础知识薄弱，单选完型常丢分。</span><br /><span style=\"font-size: small;\">5.遗漏要点、生硬翻译、表达不准确、逻辑思维混乱，阅读写作成绩不上不下。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.科学规划高中三年英语学习方案，逐步提升英语综合能力。</span><br /><span style=\"font-size: small;\">2.全面提升听说读写的整体水平，把词汇、语法、阅读、写作融会贯通的结合学习。</span><br /><span style=\"font-size: small;\">3.归纳总结规律方法，理清学习脉络，形成系统的高中英语学习计划。</span><br /><span style=\"color: #ff0000; font-size: small;\">从高一的懵懵懂懂，到高二的两极分化，到高三的背水一战，都将有属于你的精彩课程！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span><br /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355988031.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355988040.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355988049.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/470t3a55hhtqt5vj9ffa3d5u631355988080.png\" alt=\"\" />\";}', '1660.00', '83', '93312000', '161', '3301', null, '', '1355987633', '1355987633', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高中学生</span></pre>', '<pre><span style=\"font-size: small;\">1.扫清定语从句、名词性从句、非谓语、虚拟语气所有重难点，理清语法脉络。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.熟练掌握多种单词记忆方法，提高记忆效率、翻译能力，拓展词汇储备量。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握课标要求重点话题，作文不再生搬硬套，千篇一律，提高自我语言表达能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.掌握猜词能力和多种题材话题的文章解题技巧，提高阅读速度和精准度。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.整合词汇、阅读、语法、写作所学知识，做好高三复习，全面迎接高考。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14360', '初一初二初三高一高二（理）高三（理）联报课程', '4', '六年规划，备战理科高考！', '', '', '/data/default/kecheng/486/b54/te32vnn05sqhfps3guq6ducdt11355999833.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1f6/9b3/dui1fq78qbh6lpeal0pvbbhuu21356084627.jpg\";i:1;s:70:\"/data/default/kecheng/1f6/9b3/dui1fq78qbh6lpeal0pvbbhuu21356084632.png\";i:2;s:70:\"/data/default/kecheng/1f6/9b3/dui1fq78qbh6lpeal0pvbbhuu21356084636.jpg\";}', 'a:9:{i:1001;s:2889:\"<pre><span style=\"font-size: small;\"><strong>你的学习困惑：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">担心小升初、初升高课堂跳跃大，学习跟不上？语文学习死磕书本，知识面窄？读书不多，积累不够？高中阅读理解，答题不准？作文审题不清，立意不新？&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">我们的课程宗旨是：初中打基础，高中猛提升！</span></pre>\r\n<pre><span style=\"font-size: small;\">初中课程我们主要是采用&ldquo;专题教学法&rdquo;进行授课，专题设置既关注你不同的成长阶段，又涵盖了初中阶段的一些热门话题和素材，拓宽你的阅读视野，课程主要从阅读和写作两个方面着手，以读带写，以写促读，达到读写双提升，更为以后高中阶段的学习打下坚实基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">高中语文课程，我们根据不同的学习阶段，设置了相关的课程。高一阅读和写作以写人记事类文章为主，高二高三阶段，以议论文阅读和写作为主，更有独家文言文、古诗词阅读秘籍，帮你啃掉&ldquo;文言文&rdquo;这块硬骨头。尤其在议论文写作方面，我们为你设置了6节课程，分别从选材、审题、立意、构思等角度进行讲解，帮你解决另一个写作难题。另外，高三一轮二轮课程，直击高考重难点，让你轻松避开高考题型陷阱。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.六年完整的语文学习规划，明确不同阶段的不同学习目标和需求，让学习不盲目。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.初中阶段获得丰富的文学储备，为学好高中语文打下坚实基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.明确阅读现代文、古诗文的一般考查内容和考查角度，具备相应的应答策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟悉高中阶段各种文体的写作，摆脱&ldquo;四不像&rdquo;作文的困扰，向50分作文冲刺！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<p><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083531.png\" alt=\"\" /></p>\r\n<p><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083548.png\" alt=\"\" /></p>\r\n<p><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083569.png\" alt=\"\" /></p>\r\n<p><img src=\"/data/default/upload/site/008/bd5/1994/2012/12/21/dui1fq78qbh6lpeal0pvbbhuu21356083625.png\" alt=\"\" /></p>\";i:1002;s:1752:\"<pre><span style=\"font-size: small;\"><strong>进入中学的你：</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.知识难度加大，概念抽象，老师上课讲解听不懂。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.学不得法，做了很多题，成绩提高却不明显。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.学习成绩不稳定，忽高忽低，容易厌学和偏科。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你： </strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.科学规划中学六年数学学习方案，逐步提升数学思维能力。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.分析重难点，理清思路，总结规律方法，形成知识脉络，形成系统的数学学习习惯。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.总结解题策略，渗透数学思想方法，培养逻辑思维能力，教会你学习数学的方法。<br /></span></pre>\r\n<pre class=\"1\"><strong><span style=\"font-size: small;\">你的精彩课堂：</span></strong></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354691045.png\" alt=\"\" width=\"476\" height=\"322\" /><br /><span style=\"font-size: small;\"><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693042.png\" alt=\"\" width=\"474\" height=\"323\" /></span><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/21/u3g4nkodvc219jeor35m8seb261356081315.png\" alt=\"\" width=\"474\" height=\"327\" /><br /></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><br /></span></pre>\";i:1003;s:2026:\"<strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">开启&ldquo;英语学习之旅&rdquo;的你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.小升初的你充满好奇，英语课堂对你来说是一个新奇的世界&hellip;&hellip;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.初中的你懵懵懂懂，希望英语课丰富多彩，更好的为高中夯实基础&hellip;&hellip;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.步入高中的你迷茫无助，希望能够把复杂的阅读、语法、写作、单词简单划一&hellip;&hellip;</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4.高三总复习的你压力山大，希望能在有限的时间达到最佳的复习效果，冲刺高考&hellip;&hellip;</span><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">德智课程帮助你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.衔接小升初，快速进入角色，适应初中的学习方法。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.整合初一初二，学习初三新知识，同时全面复习，全力迎接中考。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.科学规划高中三年英语学习方案，逐步提升词汇、语法、阅读、写作融会贯通的学习能力。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong>你的精彩课堂</strong></span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/kdplbehb0or78net9359l8gce31356081911.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/kdplbehb0or78net9359l8gce31356081919.png\" alt=\"\" /></span>\";i:1004;s:3035:\"<pre><span style=\"font-size: small;\"><strong>学习中学物理的你，是否存在以下焦虑？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.觉得物理非常难，找不到好的学习方法，不能取得好成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明明课上很认真听讲，觉得也都听懂了，可一到考试就犯怵！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对于一些物理概念，老是弄混，感觉理解了，一做选择题就做不对，尤其多选题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.物理情境想象不出，不会分析物体受力，不会分析物理过程。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.对综合问题思维打不开、只会套用公式，不会灵活变形求解。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.名师教你学物理的最基本的方法，养成正确的学习习惯。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.帮你梳理最基本的物理概念与规律，让你理解每个概念的内涵与外延，知道规律的适用条件。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精选例题让你掌握各种解题方法：图像法、比例法、极值法、微元法、模型法等。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.对于题干特别长的综合题，教你如何把握题目关键点，分析题目考查哪些知识点，如果选用规律解题。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">通过本课程的学习，你将在以下几个方面有所突破：</span></pre>\r\n<pre><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会各类题的解法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.了解物理学的实质，掌握正确的学习方法，内化成物理学科能力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：<br /><img src=\"/data/default/upload/formula/08f/e26/1016/2012/12/21/f99l1m0vh1shmhh56kt50iru741356084308.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/08f/e26/1016/2012/12/21/f99l1m0vh1shmhh56kt50iru741356084336.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356085694.png\" alt=\"\" /><br /></strong></span></pre>\";i:1005;s:2388:\"<pre><span style=\"font-size: small;\"><strong>学习化学的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.化学原理抽象难懂，知识难度大，无法突破。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.从无机到有机思维转换困难，有机物种类、性质、官能团繁杂记忆混淆，有机推断无法突破。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结规律，科学梳理，把握脉络，提炼关键点，概念巧拆分，知识体系化，轻松灵活掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.用规律，找方法，突破难点，抓住关键。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.归纳整理有机物官能团之间的转化、性质，典型有机反应类型，科学记忆，形成网络，巧用官能团转化关系、有机反应条件、正向和逆向结合的方法，轻松解决有机合</span><span style=\"font-size: small;\">成与推断。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.归纳总结重难点知识，揭秘易错点，培养逻辑思维能力，轻松学习化学！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.综合整理，形成系统的知识网络，搞清联系，全面掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.系统掌握元素化合物知识，形成网络，全面深刻记忆。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.熟练运用盖斯定律、溶解平衡原理、pH计算方法等，突破化学原理难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.掌握有机官能团的性质、有机反应类型、同分异构体的书写方法等，轻松解决有机推断。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<span style=\"font-size: small;\"><img src=\"/data/default/upload/site/54b/530/system/2012/12/24/2dbb16dlqd2mj7mn4aed3ss1p11356314950.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/24/2dbb16dlqd2mj7mn4aed3ss1p11356314992.jpg\" alt=\"\" /></span>\";i:1006;s:1756:\"<pre><span style=\"font-size: small;\"><strong>学习高中生物的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.对设计、探究型的生物实验题没有思路，找不到突破口。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.涉及实验及推理、相关计算等综合的问题，感觉难度大，无法入手。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.面对各项生物技术的实践应用类问题感觉没有头绪。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.感觉内容繁杂，需要记忆的知识太多，感觉需要掌握的知识很多，复习抓不住重点，不知如何复习。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.运用实验法进行研究，体会实验法中的自变量、因变量和无关变量的含义，学会在相关实验资料中识别它们，并运用三者之间的相互关系解决实际问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在巩固知识的基础上进行前后综合联系，顺利实现知识的迁移和应用。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.从高考要求、典型例题分析、知识与方法、解题要领等方面来全方位诠释高考的命题规律及你的思维盲点，帮你逐项突破高考生物题的各大热点及难点内容。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：<br /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356085406.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356085438.png\" alt=\"\" /></strong></span></pre>\";i:1007;s:1945:\"<pre><span style=\"font-size: small;\"><strong>初学历史的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.政治领域涉及的名词、术语繁多，理论性强，记忆混乱。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从历史发展的角度，无法清晰分析不同政治制度对历史进程的影响。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.政治制度的学习只注重横向比较，忽视纵向沿革，理不清点、线，面等关系。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.专题间的思维跳跃性大，重难点知识把握不准，浪费过多学习精力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.政治概念的解读运用从感性到理性、从个别到一般的方法，分析史实或典型材料，理解某一具体制度的特点，然后对同类制度进行归纳、概括，从而理解该类制度的内涵和实质。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.重点揭示经济制度、经济结构、经济政策、经济现象等方面的特点，从多方面、多角度认识某一时期的经济发展水平，从而把握经济发展的概貌。<strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">3.重视主干知识，着重梳理重要史实、历史概念、历史结论、阶段特征、基本线索、发展过程等。<strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354692827.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354692850.png\" alt=\"\" /><br /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354692875.png\" alt=\"\" />\";i:1008;s:2182:\"<pre><span style=\"font-size: small;\"><strong>学习地理的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.内容多，知识繁杂，不知如何有效理解地理基本原理、规律等。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.读图内容众多，不能把握图像中的关键信息。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.应用性、灵活性强，对于相似案例不能正确迁移应用。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.考点梳理与典型例题相结合，以考点带例题，以例题学考点，面向考试，招招带劲。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.突破图像题，学会从图像中提取信息、加工整合信息。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.在主案例之外，提供相似案例，引导迁移和应用，尽快形成分析、解决问题的能力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.综合整理，详细解析，深入理解灵活运用基本概念、规律。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.清楚理解概念，建立模块之间的联系，综合全面掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.学会知识的迁移应用能力，突破难点内容，如太阳直射点在地球上的周期变化规律、地球公转的地理意义等等。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.从区域角度理解地理理论，人在地理环境发展中的作用，从而获得正确的人地观。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.学会读图、析图能力，从图中提取、加工信息，解决图像问题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/24/2dbb16dlqd2mj7mn4aed3ss1p11356315335.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/24/2dbb16dlqd2mj7mn4aed3ss1p11356315350.jpg\" alt=\"\" />\";i:1009;s:1425:\"<pre><span style=\"font-family: %value; font-size: small;\"><strong>学习必修1的你：</strong> </span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.对待物价问题你该如何正确看待？消费的作用该如何发挥？ </span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.分配与公平的关系该如何正确处理? </span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.怎样加快经济发展方式的转变？ </span></pre>\r\n<pre><span style=\"font-family: %value;\"><span style=\"font-size: small;\">4.怎样用经济全球化的相关知识正确认识今天的对外经济关系？</span> <span style=\"font-size: small;\">&nbsp; 这些&hellip;&hellip;，你都清楚了么？</span></span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span> </span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><span style=\"font-size: small;\">1.联系现实经济生活中的现象和问题，综合运用经济学理论分析现象、探求规律，整合理论知识，提高学生的解题技巧和能力。</span> </span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\"><span style=\"font-size: small;\">2.引导学生分清不同行为主体在经济生活中的不同地位、不同作用和不同做法。</span></span></pre>\";}', '16080.00', '804', '186624000', '129', '3302', null, '', '1355988527', '1355988527', '1', '初一初二初三高一高二高三全国各版本理科学生', '<pre><span style=\"font-size: small;\">全国各版本初高中理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.初一到高三，六年课程合理规划，有的放矢。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.每一科都有独家秘籍让你轻松学习，准确答题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><span style=\"font-size: small;\">3.夯实基础，巩固提高，全面提升，循序渐进的走好初高中的每一步。</span></span></pre>\r\n<pre><span style=\"font-size: small;\">4.熟悉理科高考特点，有侧重，有针对性进行备考。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14361', '中考英语二轮复习课程', '3', '', '', '', '/data/default/kecheng/486/b54/470t3a55hhtqt5vj9ffa3d5u631355989282.jpg', 'a:0:{}', 'a:1:{i:1003;s:1154:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.语法知识复习的很好，感觉自己什么都掌握了，做题却经常出错，找不到症结所在。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.常用句型、词组、习惯用法等方面的基础知识掌握不牢，单选、完型等题型经常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.阅读、写作等综合能力考查题型，得分不稳定，忽高忽低，&ldquo;挣分儿&rdquo;题变成了&ldquo;拉分题&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.查漏补缺，识别复习薄弱点，帮助你有针对性的复习，节省宝贵时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.辨别考点陷阱，归纳易错题型，帮助你灵活性的复习，归纳应试技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.单选、完型、阅读、写作全面复习，帮助你系统性的复习，提升综合能力。</span></pre>\";}', '120.00', '6', '31104000', '104', '3301', null, '', '1355989056', '1355989056', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初三二轮复习的学生</span></pre>', '<pre><span style=\"font-size: small;\">1.巩固：一轮学习成果，强化知识系统的记忆。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.完善：通过专题复习查漏补缺，以题带知识，整合所学知识要点。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.综合：减少单一知识的训练，增强知识的连接点，增强解题的综合性和灵活性。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.提高：提升总结归纳能力，掌握应试策略和技巧，提高巧得分得高分的能力。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14362', '初一初二初三高一高二（文）高三（文）联报课程', '4', '', '', '', '/data/default/kecheng/486/b54/d7qc4st3ti4m1oapjruschdjo51355993280.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1f6/9b3/kdplbehb0or78net9359l8gce31356083840.png\";i:1;s:70:\"/data/default/kecheng/1f6/9b3/kdplbehb0or78net9359l8gce31356083844.png\";i:2;s:70:\"/data/default/kecheng/1f6/9b3/kdplbehb0or78net9359l8gce31356083846.png\";}', 'a:9:{i:1001;s:3065:\"<pre><span style=\"font-size: small;\"><strong>你的学习困惑</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">担心小升初、初升高课堂跳跃大，学习跟不上？语文学习死磕书本，知识面窄？读书不多，积累不够？高中阅读理解，答题不准？作文审题不清，立意不新？&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">我们的课程宗旨是：初中打基础，高中猛提升！</span></pre>\r\n<pre><span style=\"font-size: small;\">初中课程我们主要是采用&ldquo;专题教学法&rdquo;进行授课，专题设置既关注你不同的成长阶段，又涵盖了初中阶段的一些热门话题和素材，拓宽你的阅读视野，课程主要从阅读和写作两个方面着手，以读带写，以写促读，达到读写双提升，更为以后高中阶段的学习打下坚实基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">高中语文课程，我们根据不同的学习阶段，设置了相关的课程。高一阅读和写作以写人记事类文章为主，高二高三阶段，以议论文阅读和写作为主，更有独家文言文、古诗词阅读秘籍，帮你啃掉&ldquo;文言文&rdquo;这块硬骨头。尤其在议论文写作方面，我们为你设置了6节课程，分别从选材、审题、立意、构思等角度进行讲解，帮你解决另一个写作难题。另外，高三一轮二轮课程，直击高考重难点，让你轻松避开高考题型陷阱。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.六年完整的语文学习规划，明确不同阶段的不同学习目标和需求，让学习不盲目。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.初中阶段获得丰富的文学储备，为学好高中语文打下坚实基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.明确阅读现代文、古诗文的一般考查内容和考查角度，具备相应的应答策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟悉高中阶段各种文体的写作，摆脱&ldquo;四不像&rdquo;作文的困扰，向50分作文冲刺！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317681.png\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317692.png\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317704.png\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317717.png\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317730.png\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/24/ap0fkkfm0bd3gtjq7do1v918851356317761.png\" alt=\"\" />\";i:1002;s:1687:\"<pre><span style=\"font-size: small;\"><strong>进入中学的你</strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.知识难度加大，概念抽象，老师上课讲解听不懂。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.学不得法，做了很多题，成绩提高却不明显。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.学习成绩不稳定，忽高忽低，容易厌学和偏科。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你 </strong></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">1.科学规划中学六年数学学习方案，逐步提升数学思维能力。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">2.分析重难点，理清思路，总结规律方法，形成知识脉络，形成系统的数学学习习惯。</span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\">3.总结解题策略，渗透数学思想方法，培养逻辑思维能力，教会你学习数学的方法。<br /><strong>你的精彩课堂</strong><br /></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354691045.png\" alt=\"\" width=\"476\" height=\"322\" /><br /><span style=\"font-size: small;\"><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354693042.png\" alt=\"\" width=\"474\" height=\"323\" /></span><br /><img src=\"/data/default/upload/formula/b44/050/2188/2012/12/21/u3g4nkodvc219jeor35m8seb261356081315.png\" alt=\"\" width=\"474\" height=\"327\" /><br /></span></pre>\r\n<pre class=\"1\"><span style=\"font-size: small;\"><br /></span></pre>\";i:1003;s:1231:\"<pre><span style=\"font-size: small;\"><strong>开启&ldquo;英语学习之旅&rdquo;的你</strong><br />1.小升初的你充满好奇，英语课堂对你来说是一个新奇的世界&hellip;&hellip;<br />2.初中的你懵懵懂懂，希望英语课丰富多彩，更好的为高中夯实基础&hellip;&hellip;<br />3.步入高中的你迷茫无助，希望能够把复杂的阅读、语法、写作、单词简单划一&hellip;&hellip;<br />4.高三总复习的你压力山大，希望能在有限的时间达到最佳的复习效果，冲刺高考&hellip;&hellip;</span><span style=\"font-size: small;\"><strong><br />德智课程帮助你</strong><br />1.衔接小升初，快速进入角色，适应初中的学习方法。<br />2.整合初一初二，学习初三新知识，同时全面复习，全力迎接中考。<br />3.科学规划高中三年英语学习方案，逐步提升词汇、语法、阅读、写作融会贯通的学习能力。<br /><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/21/kdplbehb0or78net9359l8gce31356081379.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/kdplbehb0or78net9359l8gce31356081388.png\" alt=\"\" />\";i:1004;s:2936:\"<pre><span style=\"font-size: small;\"><strong>学习初中物理的你，是否存在以下焦虑？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.觉得物理非常难，找不到好的学习方法，不能取得好成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明明课上很认真听讲，觉得也都听懂了，可一到考试就犯怵！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对于一些物理概念，老是弄混，感觉理解了，一做选择题就做不对，尤其多选题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.遇到具体的问题，不知道怎么应用规律去解题，不知道该用什么公式计算。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.动手实验的次数比较少，不能真正理解实验题，不会分析现象，不能得出结论。</span></pre>\r\n<pre><span style=\"font-size: small;\">6.遇到大的计算题，更是一头雾水，不知道如何下手！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.名师教你学物理的最基本的方法，养成正确的学习习惯。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.帮你梳理最基本的物理概念与规律，让你理解每个概念的内涵与外延，知道规律的适用条件。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精选典型的题目，带你一步步去分析，梳理解题思路，总结解题技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.对于题干特别长的综合题，教你如何把握题目关键点，分析题目考查哪些知识点，如何规律解题。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">通过本课程的学习，你将在以下几个方面有所突破：</span></pre>\r\n<pre><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会各类题的解法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.了解物理学的实质，掌握正确的学习方法，内化成物理学科能力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/08f/e26/1016/2012/12/21/f99l1m0vh1shmhh56kt50iru741356087327.png\" alt=\"\" /><br /></strong></span></pre>\";i:1005;s:1435:\"<pre><strong><span style=\"font-size: small;\">学习化学的你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对于大型实验题，做题思路混乱，不知从何下手。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.元素周期律、元素周期表的理论学习困难，&ldquo;位、构、性&rdquo;抽象理解难于想象。 </span></pre>\r\n<pre><strong><span style=\"font-size: small;\">德智课程帮助你</span></strong></pre>\r\n<pre><span style=\"font-size: small;\">1.总结规律，科学梳理，把握脉络，提炼关键点，概念巧拆分，知识体系化，轻松灵活掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.分析实验装置，制取原理，实验方法，收集方法，轻松突破实验难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.初步掌握化学基本理论的知识，学会原理基本应用、能量分析及简单有机化合物性质和反应，轻松应对会考。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356084520.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356084538.jpg\" alt=\"\" />\";i:1006;s:1126:\"<pre><span style=\"font-size: small;\"><strong>学习生物的你</strong><br />1.感觉细胞的分子组成、结构及代谢，内容抽象、陌生、零碎，记不住。<br />2.涉及实验及推理、相关计算等综合的问题，感觉难度大，无法入手。<br />3.感觉人体神经调节的结构基础和调节过程名词概念多，涉及微观层面多，很抽象。<strong><br />德智课程帮助你</strong><br />1.学会解决与过程有关的计算问题要绘制简图，进而理清题目信息，解决复杂问题。<strong></strong><br />2.运用实验法进行研究，体会实验法中的自变量、因变量和无关变量的含义，学会在相关实验资料中识别它们，并运用三者之间的相互关系解决实际问题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356086145.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2012/12/21/mb3krjfkg4o6nfff2gtp574iq11356086183.png\" alt=\"\" /></strong></span></pre>\r\n<pre>&nbsp;</pre>\";i:1007;s:1568:\"<pre><span style=\"font-size: small;\"><strong>初学历史的你</strong><br />1.政治领域涉及的名词、术语繁多，理论性强，记忆混乱。<br />2.从历史发展的角度，无法清晰分析不同政治制度对历史进程的影响。<br />3.政治制度的学习只注重横向比较，忽视纵向沿革，理不清点、线，面等关系。<br />4.专题间的思维跳跃性大，重难点知识把握不准，浪费过多学习精力。<strong><br />德智课程帮助你</strong><br />1.政治概念的解读运用从感性到理性、从个别到一般的方法，分析史实或典型材料，理解某一具体制度的特点，然后对同类制度进行归纳、概括，从而理解该类制度的内涵和实质。<br />2.重点揭示经济制度、经济结构、经济政策、经济现象等方面的特点，从多方面、多角度认识某一时期的经济发展水平，从而把握经济发展的概貌。<strong></strong><br />3.重视主干知识，着重梳理重要史实、历史概念、历史结论、阶段特征、基本线索、发展过程等。<strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354692827.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354692850.png\" alt=\"\" /><br /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354692875.png\" alt=\"\" />\";i:1008;s:1367:\"<pre><strong><span style=\"font-size: small;\">学习地理的你</span></strong><span style=\"font-size: small;\"><br />1.内容多，知识繁杂，不知如何有效理解地理基本原理、规律等<br />2.迁移应用复杂，无法清楚理解、掌握<br />3.读图内容众多，不能把握图像中的关键信息<br />4.重难点知识把握不准，导致侧重点偏转，学习效果不好</span><strong><span style=\"font-size: small;\"><br />德智课程帮助你</span></strong><span style=\"font-size: small;\"><br />1.考点梳理与典型例题相结合，以考点带例题，以例题学考点，面向考试，招招带劲<br />2.学会迁移应用，通过不同的情景中的应用，形成分析问题、解决问题的能力<br />3.突破图像题，学会从图像中提取信息、加工整合信息<br />4.对地理知识点进行深入解剖，准确把握考向，制定针对策略，科学高效复习。</span><strong><span style=\"font-size: small;\"><br />你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356084871.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356084884.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356084902.jpg\" alt=\"\" />\";i:1009;s:1137:\"<span style=\"font-size: small;\"><strong>学习必修</strong><strong>4 </strong><strong>的你</strong></span><br /><span style=\"font-size: small;\">面对如此抽象的哲学问题：</span><br /><span style=\"font-size: small;\">1.物质与意识的辩证关系核心？</span><br /><span style=\"font-size: small;\">2.如何用联系和发展的观点去看待一个全新问题？</span><br /><span style=\"font-size: small;\">3.辩证法和形而上学的关键区别在哪里？</span><br /><span style=\"font-size: small;\">4.社会存在和社会意识的关系？</span><br /><span style=\"font-size: small;\">死记硬背的方法，还有用么？</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.结合具体案例进行知识梳理，构建哲学的基本框架。</span><br /><span style=\"font-size: small;\">2.对重难点内容进行专家点拨，让你对考查方法了如指掌。</span><br /><span style=\"font-size: small;\">3.从知识的源头和归宿进行讲解，让你知其所以然，帮助你更好的理解这些抽象的概念。</span>\";}', '13920.00', '696', '186624000', '136', '3303', null, '', '1355989667', '1355989667', '1', '', '<pre><span style=\"font-size: small;\">全国各版本初高中文科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.快速适应初高中学习方法，培养学习兴趣，养成正确的学习习惯。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.扫清初高中各科学习重难点，理清学习脉络，形成知识网络。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.夯实基础，巩固提高，全面提升，循序渐进的走好初高中的每一步。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.整合所学知识，归纳规律方法，掌握命题规律，为高考做好充分准备。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14363', '初一初二四科联报课程', '4', '精讲细讲，轻松高效学习', '', '', '/data/default/kecheng/486/b54/hnrhl1eesbkl0tnpv1tnae75b21355995950.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356082401.jpg\";i:1;s:70:\"/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356082413.jpg\";i:2;s:70:\"/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356082426.jpg\";}', 'a:4:{i:1001;s:2276:\"<pre><span style=\"font-size: small;\"><strong>学习初一初二课程的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">是不是渴望找到一种方法能够打牢初中语文学习基础？收获更多的人生感悟？是不是希望拓展语文知识储备，提高语文素养？是不是希望摆脱枯燥无味的语文学习，渴望在语文考试中独占鳌头？是不是想了解关于说明文的学习方法与技巧？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.打好语文学习基础，阅读、写作，双管齐下，语文成绩迅速提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.摆脱灌输式学习，培养自主学习能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.结合最新中考试题，从命题角度教你扫清现代文阅读障碍，提前与中考&ldquo;面对面&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.每节课都从不同的角度给你写作的技巧，整个课程结束，写作技巧全面覆盖，应付考试作文，绰绰有余。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.带你感受亲情，这是我们本次系列课程要给你的另外一项重点内容。学会体察父母之爱、祖孙之爱、师生之爱，甚至是人间大爱，这样你才会有一颗感恩的心。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.扎实基础，掌握阅读和写作技巧，初中语文领先起跑。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.养成良好的学习和阅读习惯，学会自主学习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.懂得思考成长过程中遇到的各种问题，结合自身的成长经历，获得新的人生体验。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.明确中考现代文阅读的考查角度，针对不同角度，学会每个角度的应答策略。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21355996500.jpg\" alt=\"\" />\";i:1002;s:3181:\"<pre><span style=\"font-size: small;\"><strong>学习初一初二课程的你</strong></span><br /><span style=\"font-size: small;\">1.是不是看到几何语言就犯晕，它说的到底是什么意思？看不懂啊！</span><br /><span style=\"font-size: small;\">2.平行线的性质与判定，不能灵活贯通运用 ，证明过程陷入僵局。</span><br /><span style=\"font-size: small;\">3.解二元一次方程组，不能选择合适的方法解答，增加计算过程，导致出错。解字母系数的不等式，不能正确解答，没有分类讨论意识。</span><br /><span style=\"font-size: small;\">4.证明全等，找条件是否经常遇到瓶颈？证明过程如何写，你是否书写规范？条理是否清晰？</span><br /><span style=\"font-size: small;\">5.函数&mdash;&mdash;概念是否理解？应用题如何寻找等式关系？反比例函数的独特性质你是否熟练掌握？</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.反复进行&ldquo;几何模型&rarr;图形&rarr;文字&rarr;符号&rdquo;的练习，让你对几何语言不在陌生！</span><br /><span style=\"font-size: small;\">2.知识再次讲解配合典型例题，帮助你消化性质与判定，做到融会贯通。</span><br /><span style=\"font-size: small;\">3.&ldquo;观察法&rdquo;教你面对二元一次方程组，如何针对性选择解法，简化计算过程，提高正确率。对含有字母系数的不等式，例题由简单到复杂，教会你求解原理的基础上，进一步提高你的变通能力。</span><br /><span style=\"font-size: small;\">4.探索研究能证明全等的方法，对全等条件进行一个深刻了解，同时培养从条件中找隐含条件的能力。证明推理逻辑强，推理表达需精准，由简单例题体会证明思路与格式，逐渐增加题目复杂程度，循序渐进过程。</span><br /><span style=\"font-size: small;\">5. 简单例子，带你领会从抽象到具体的转化过程，理解函数定义的变化与对应关系。反比例函数的深层剖析，挖掘反比例函数的特性，典型例题辅助理解。</span><br /><span style=\"font-size: small;\"><strong>你的</strong><strong>学习效果</strong></span><br /><span style=\"font-size: small;\">1.查漏补缺，温故知新，找出知识漏洞，熟悉知识考点、攻破知识难点。</span><br /><span style=\"font-size: small;\">2.对运算法则更加熟练的运用，做计算题更简单，更准确！</span><br /><span style=\"font-size: small;\">3.体会消元、转化思想，分类讨论思想，整体思想在解题中的运用。</span><br /><span style=\"font-size: small;\">4.熟练掌握导出新方程法，设比例系数法，换元法，整体法等解决二元一次方程组，分式的有关概念，易错点；反比例函数的性质；分清四边形、矩形、正方形、菱形的区别于联系。</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21355996671.jpg\" alt=\"\" />\";i:1003;s:2478:\"<pre><span style=\"font-size: small;\"><strong>学习初一初二课程的你</strong></span><br /><span style=\"font-size: small;\">1.成绩忽高忽低，上课开始听不懂、跟不上，学习站在&ldquo;分水岭&ldquo;？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.学习兴趣不高，不喜欢大声朗读，单词忘的快。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.单词难度，语法深度，写作高度陡然徒增，成绩进入&ldquo;爬坡期&ldquo;？</span></pre>\r\n<pre><span style=\"font-size: small;\">4.时态语态易混易错，词性词法区分不清，写作常丢分？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.课堂幽默精彩，环节设置多种多样，抓住你全部的注意力!</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从不同词性展开讲解，逐一分析，扩充你的词汇量。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.重难点知识庖丁解牛，逐句分析般讲解，摒弃死记硬背句型结构！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.时态语态对比讲解，例句真题配套练习，彻底吃透语法难点！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.词汇句型拓展记忆，优美篇章课上赏析，语境学习增强联想记忆。学习整理写作思路，收集写作素材，建立正确的写作方法。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.培养学习兴趣，养成良好的学习习惯，打好初中英语基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.轻松掌握比较级、进行时、there be、疑问句以及名词代词的重难点知识。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.增加词汇储备量，课上消化吸收所学内容，课后独立完成作业。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.吃透动词的相关知识点，掌握最基本的动词才能为以后学习句子打下基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.熟练掌握数词、介词、副词等词类使用方法， 能够区分、记忆、运用。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21355999590.jpg\" alt=\"\" />\";i:1004;s:2255:\"<pre><span style=\"font-size: small;\"><strong>学习初二物理的你，是否存在以下焦虑？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.在课堂上听不懂老师所讲的概念与规律，不知道如何学物理。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.虽然在课堂上听懂了，但是一到做题就眼高手低，无法正确解答！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.你平时掌握的并不错，但是考试仍旧无法轻松拿高分，一到考试就焦虑！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.弄清最基本的物理概念与规律，教会你正确的学习方法。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.老师带领大家一起分析题目，理清思路，让你看到题目不再迷惑。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">学习本课程前，请先下载学案，仔细看一遍，这样学习效果更好。通过本课程的学习，你将在以下几个方面有所突破：</span></pre>\r\n<pre><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会一类题的解法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握正确的学习方法，内化成物理学科能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.轻松解决声学、光学、热学、力学板块的知识，学会如何应用这些知识解题，形成正确的解题思路，掌握规范的答题步骤。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21355999659.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21355999681.jpg\" alt=\"\" />\";}', '2520.00', '126', '62208000', '100', '3301', null, '', '1355995786', '1355995786', '1', '全国各版本初中学生', '<pre><span style=\"font-size: small;\">全国各版本初中学生</span></pre>', '<pre><span style=\"font-size: small;\">1.培养良好学习习惯，形成正确的学习方法，为以后的学习打下夯实的基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.积累一定的解题技巧，在考试中能快速解题，提高准确率。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.开拓数学思维，例题的细致讲解带你逐步理解解题思路，教你如何思考问题，初步锻炼数学思维，同时一道题多种解法，解救你的思维局限！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.学会高分作文的写作技巧，养成从生活中积累素材的好习惯。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.学会日常用语写作，掌握度假与学校生活等话题的写作方法。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14364', '初二初三五科联报课程', '4', '详细讲解，轻松突破', '', '', '/data/default/kecheng/486/b54/cgvvjkl7ohl0eiq7mkgflo5j911356013368.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356082794.jpg\";i:1;s:70:\"/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356082805.jpg\";i:2;s:70:\"/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356082819.jpg\";}', 'a:5:{i:1001;s:3052:\"<pre><span style=\"font-size: small;\"><strong>你的学习困惑</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 是不是希望拓展语文知识储备，提高语文素养？是不是希望摆脱枯燥无味的语文学习，渴望在语文考试中独占鳌头？是不是想了解关于说明文的学习方法与技巧？知道哪种文体是学习重点吗？知道哪种文体写起来最难吗？对，当然是议论文。那你是不是对议论文有&ldquo;谈虎色变&rdquo;之感？是不是觉得读起来好深，写起来好难？<strong> </strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>帮你解决的问题</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.阅读其实是与作者的对话。所以在阅读过程中，大家往往会觉得&ldquo;阅读很容易，答题很困难&rdquo;，这是因为你没有真正理解作者的&ldquo;说话&rdquo;意图和文章的考查角度，缺少熟练、规范的答题方法。写起文章，也觉得无从下笔，不知如何选材、立意、构思。平时看了很多书，积累了很多素材，却又苦于不懂得何时运用、如何运用。这些，我们都将在课堂上教给你。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.议论文是中考的必考文体，又是以后高中阶段阅读和写作的重点文体。所以本次课程，我们围绕中考议论文常出现的议论主题，精心设置了&ldquo;腹有诗书气自华&mdash;&mdash;思想篇&rdquo;系类课程，每节课一个主题，每个主题都是中考的考查热门。最新议论文真题，让你与中考零距离,教给你如何读懂议论文，如何在读懂的基础上拿高分。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.写作方面，我们结合中考满分作文，从审题、选材、立意等方面入手，教你轻松写出满分议论文。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的收获</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.学会如何分析文章，如何体会作者情感，轻松把握答题技巧和阅读规律。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从阅读中认识自我，认识自然，认识生活，认识世界&hellip;&hellip;从四季更迭与自然变幻中发现生命的可贵与生活的惬意，懂得热爱生命，尊重自然，从而陶冶情操，增长智慧！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.知道议论文常见的考查主题，熟练掌握议论文的阅读方法和答题规范。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.学会议论文如何审题、立意，如何积累素材，写就中考满分作文。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.通过阅读文章，学会辨证思考问题，品悟人生真谛。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21356000726.jpg\" alt=\"\" />\";i:1002;s:4127:\"<pre><span style=\"font-size: small;\"><strong>学习初二初三数学的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.证明全等，找条件是否经常遇到瓶颈？证明过程如何写，你是否书写规范？条理是否清晰？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.函数&mdash;&mdash;概念是否理解？应用题如何寻找等式关系？反比例函数的独特性质你是否熟练掌握？</span></pre>\r\n<pre><span style=\"font-size: small;\">3.四边形问题中，如何巧妙做辅助线，你是否有所了解？四边形中存在哪些基本图形，能助你快速解题？</span></pre>\r\n<pre><span style=\"font-size: small;\">4.解决实际应用问题，找不到相等关系，判断最值失误？</span></pre>\r\n<pre><span style=\"font-size: small;\">5.圆的切线证明，经常找不到突破口，往往走进死胡同！</span></pre>\r\n<pre><span style=\"font-size: small;\">6.如何快而准的求出二次函数的解析式，你知道么?</span></pre>\r\n<pre><span style=\"font-size: small;\">7.面积最大最小如何求？利润最大最小如何求？你知道么？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.探索研究能证明全等的方法，对全等条件进行一个深刻了解，同时培养从条件中找隐含条件的能力。证明推理逻辑强，推理表达需精准，由简单例题体会证明思路与格式，逐渐增加题目复杂程度，循序渐进过程。</span></pre>\r\n<pre><span style=\"font-size: small;\">2. 简单例子，带你领会从抽象到具体的转化过程，理解函数定义的变化与对应关系。反比例函数的深层剖析，挖掘反比例函数的特性，典型例题辅助理解。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.总结归纳基本图形，教你如何添加辅助线，化未知为已知。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.弄清实际问题背景，把有关数量关系分析透彻，特别是找出作为列方程依据的主要相等关系。</span></pre>\r\n<pre><span style=\"font-size: small;\">5. 通过课程讲解会使同学们熟悉探索法的推理过程，熟练地用综合法进行证明，进一步提高同学们的思维能力和逻辑推理能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">6.二次函数形式的特点分析，独到而全面的例题教你如何设二次函数，能更快、更准的算出函数解析式！</span></pre>\r\n<pre><span style=\"font-size: small;\">7.典型例题，细致讲解，方法归纳教你面对最值问题不再烦恼！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">4.开拓数学思维，例题的细致讲解带你逐步理解解题思路，教你如何思考问题，初步锻炼数学思维，同时一道题多种解法，解救你的思维局限！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.熟练掌握旋转这一图形变换工具，有效解决部分几何证明问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.教会你一种新的证明思路&mdash;&mdash;探索法的推理，帮助你在毫无头绪的思路下寻找出路！</span></pre>\r\n<pre><span style=\"font-size: small;\">6.对二次函数的图像与系数之间的关系认识更加深刻，不只是停留于课本上的知识，彻底弄清二次函数的图像、性质！</span></pre>\r\n<pre><span style=\"font-size: small;\">7.对相似有更深刻的认识，能从几何图形中，提炼模型，帮你更快，更准的解答几何题！</span></pre>\r\n<pre><span style=\"font-size: small;\">8.理清三角函数，让你对一大堆的公式记忆不再模糊！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21356000964.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21356000990.jpg\" alt=\"\" />\";i:1003;s:2164:\"<pre><span style=\"font-size: small;\"><strong>学习初二初三英语的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.成绩忽高忽低，上课开始听不懂、跟不上，学习站在&ldquo;分水岭&ldquo;？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.单词难度，语法深度，写作高度陡然徒增，成绩进入&ldquo;爬坡期&ldquo;？</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时态语态易混易错，词性词法区分不清，写作常丢分？</span></pre>\r\n<pre><span style=\"font-size: small;\">4.单选填空语法题丢分易，阅读写作综合题提分难！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.测验考试周周有，随着时间中考压力&ldquo;水涨船高&rdquo;！</span></pre>\r\n<pre><span style=\"font-size: small;\">6.面多&ldquo;三多一少&rdquo;即课程多、作业多、考试多、休息少，感觉时间越来越不够用！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.课堂幽默精彩，环节设置多种多样，抓住你全部的注意力!</span></pre>\r\n<pre><span style=\"font-size: small;\">2.重难点知识庖丁解牛，逐句分析般讲解，摒弃死记硬背句型结构！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时态语态对比讲解，例句真题配套练习，彻底吃透语法难点！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.词汇句型拓展记忆，优美篇章课上赏析，语境学习增强联想记忆！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.摸清自己学习的薄弱点，培优补差，为中考复习打定基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">6.掌握重难语法点的学习与运用，提升写作阅读等英语综合成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21356001029.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/20/hnrhl1eesbkl0tnpv1tnae75b21356001045.jpg\" alt=\"\" />\";i:1004;s:2178:\"<pre><span style=\"font-size: small;\"><strong>学习初中物理的你，是否存在以下焦虑？</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.觉得物理非常难，找不到好的学习方法，不能取得好成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明明课上很认真听讲，觉得也都听懂了，可一到考试就犯怵！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对于一些物理概念，老是弄混，感觉理解了，一做选择题就做不对，尤其多选题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.遇到具体的问题，不知道怎么应用规律去解题，不知道该用什么公式计算。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.动手实验的次数比较少，不能真正理解实验题，不会分析现象，不能得出结论。</span></pre>\r\n<pre><span style=\"font-size: small;\">6.遇到大的计算题，更是一头雾水，不知道如何下手！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.名师教你学物理的最基本的方法，养成正确的学习习惯。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.帮你梳理最基本的物理概念与规律，让你理解每个概念的内涵与外延，知道规律的适用条件。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精选典型的题目，带你一步步去分析，梳理解题思路，总结解题技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.对于题干特别长的综合题，教你如何把握题目关键点，分析题目考查哪些知识点，如果选用规律解题。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/07/t3u8j6gjtv0np5l0hlmarjvic71354861049.png\" alt=\"\" /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/07/t3u8j6gjtv0np5l0hlmarjvic71354861155.png\" alt=\"\" />\";i:1005;s:945:\"<pre><span style=\"font-size: small;\"><strong>学习初中化学的你</strong><br />1.对化学充满了好奇感，学习兴趣浓厚，但如何有效的学习仍比较困惑。<br />2.概念、定理、物质这么多，又抽象、又繁杂怎样才能清楚掌握。<br />3.看到实验题就乱了阵脚，不知从何下手。<br /><strong>德智课程帮助你</strong><br />1.总结规律，归纳方法，揭开谜团，轻松学习。<br />2.科学梳理，知识系统化，把握脉络，有条不紊。<br />3.分析实验装置，制取原理，实验方法，收集方法，快速解决实验难题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356088340.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/21/d5v3t31d65lftmbbma2svsms651356088352.jpg\" alt=\"\" />\";}', '2900.00', '145', '62208000', '166', '3301', null, '', '1355998109', '1355998109', '1', '全国各版本初中学生', '<pre><span style=\"font-size: small;\">全国各版本初中学生</span></pre>', '<pre><span style=\"font-size: small;\">1.查漏补缺，找出知识漏洞，熟悉知识考点、攻破知识难点。</span><br /><span style=\"font-size: small;\">2.温故知新，对所学知识巩固复习，加深理解与记忆。</span><br /><span style=\"font-size: small;\">3.一道题，多种解法，让你头脑变灵活，不再墨守成规！</span><br /><span style=\"font-size: small;\">4.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span><br /><span style=\"font-size: small;\">5.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span><br /><span style=\"font-size: small;\">6.归纳总结重难点知识，揭秘易错点，培养逻辑思维能力，轻松学习！</span><br /><span style=\"font-size: small;\">7.综合整理，形成系统的知识网络，搞清联系，全面掌握。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14365', '高一下学期物理预习课（曲线运动及万有引力）', '2', '', '', '', '/data/default/kecheng/1f6/9b3/p6hhbton8pgarc4p0e29gmd5121356059952.jpg', 'a:0:{}', 'a:1:{i:1004;s:953:\"<pre><span style=\"font-size: small;\"><strong>将要学习必修2</strong><strong>的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.曲线运动比直线运动更加难以理解，该怎样预习能够事半功倍？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对于天体间的万有引力，既新鲜好奇，又感觉离自己很遥远，学习起来有些小恐惧。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从你熟悉的直线运动切入，帮助你轻松打开曲线运动的大门。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.将遥远的天体引到你的身边，给你指明解决此类问题的突破口。<strong><br />你的精彩课堂<br /><img src=\"/data/default/upload/formula/076/828/1021/2013/01/04/vrutkrjg3ask7cqnku1d41aml51357277556.png\" alt=\"\" /></strong></span><strong></strong></pre>\";}', '40.00', '2', '31104000', '30', '3301', null, '', '1356059959', '1356059959', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高一学生</span></pre>', '<pre><span style=\"font-size: small;\">1.掌握平抛运动与匀速圆周运动涉及的基本概念、基本公式，会解相关类型题。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.会结合牛顿运动定律分析向心加速度、向心力等重要物理量，会比较并计算天体运动中的相关物理量。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14366', '高二下学期物理预习课（电磁感应及交变电流）', '2', '', '', '', '/data/default/kecheng/1f6/9b3/p6hhbton8pgarc4p0e29gmd5121356061917.jpg', 'a:0:{}', 'a:1:{i:1004;s:798:\"<pre><span style=\"font-size: small;\"><strong>将要学习选修3-2</strong><strong>的你</strong><br />1.明显感觉难度加大，想提前预习，又不知从何处着手。<br />2.对&ldquo;电生磁&rdquo;&ldquo;磁生电&rdquo;等问题混淆不清，不会分析解题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong><br />1.从整体着眼，承上启下，实现知识的顺利迁移。<br />2.从产生原理入手学习电磁感应问题，从根本上弄清物理过程，从而化解难点，学会应用解题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/076/828/1021/2013/01/04/vrutkrjg3ask7cqnku1d41aml51357277779.png\" alt=\"\" /><br /></strong></span></pre>\";}', '40.00', '2', '31104000', '14', '3302', null, '', '1356061923', '1356061923', '0', '', '<pre><span style=\"font-size: small;\">高二理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.掌握电磁感应及交流电相关的基本概念、基本公式，会解基本类型题。<br />2.会结合之前学过的知识，解决一些综合性的问题。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14367', '中考化学二轮复习课程', '3', '抓住规律，重点突破，高效学习', '北京名校高级教师', '', '/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356066879.jpg', 'a:0:{}', 'a:1:{i:1005;s:1132:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.面对中考压力大，不知从何开始复习，如何全面掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.知识零散，涉及面广，不能系统复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时间紧，任务重，不知如何高效复习，精准把握。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结中考能力要求，科学提示方法，有效制定策略，轻松全面复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结常考点，揭秘易错点，以题带点，知识与解题精讲精析，全面突破！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.提炼精华，抓准考试命脉，做到高效、精准复习！</span></pre>\r\n<pre>&nbsp;</pre>\r\n<pre><strong>温馨提示:为了保证课程质量，打造精品，中考化学二轮复习课程会陆续推出，敬请期待！</strong></pre>\r\n<pre>&nbsp;</pre>\";}', '160.00', '8', '31104000', '112', '3301', null, '', '1356067047', '1356067047', '0', '面临中考的学生', '<span style=\"font-size: small;\">全国各版本初三学生</span>', '<pre><span style=\"font-size: small;\">1.明确中考考向，做到复习方向精准，科学有效备考！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.中考五大块内容全面网罗，从身边的化学物质到实验综合题全面详细解析，让你的化学学习由难变易，熟练掌握！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握各类题型的解题方法、技巧，避开考试陷阱，高效复习，轻松应考！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14368', '高二下学期化学预习课（有机化合物）', '2', '详细全面讲解，打好有机基础', '北京名校高级教师', '', '/data/default/kecheng/1f6/9b3/d5v3t31d65lftmbbma2svsms651356067719.jpg', 'a:0:{}', 'a:1:{i:1005;s:1234:\"<pre><span style=\"font-size: small;\"><strong>即将学习选修5的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.有机化学是高考的重点和难点，不知从哪方面做好预习准备。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对于确定有机物分子式和结构式的方法不明白，不能清楚识别红外光谱图和核磁共振氢谱图。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.有机物种类、官能团繁多，学习难度大，记忆混淆。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从简单内容开始，让你逐步理解有机的内涵，为后续学习做好铺垫。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.深入分析红外光谱图和核磁共振氢谱图，快速确定有机物分子式和结构式。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.通过对比、归纳全面整理，系统总结，形成网络。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2013/01/10/5mckk2f1k1mgva6sd60qhbgn851357801414.jpg\" alt=\"\" />\";}', '60.00', '3', '31104000', '28', '3302', null, '', '1356067851', '1356067851', '0', '高二理科学生', '<pre><span style=\"font-size: small;\">全国各版本高二理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.从认识有机物开始，掌握有机物的分类、命名、分子式和结构式的确定方法，为更好地学习烃及其衍生物打好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握有机物的性质、官能团、有机反应类型等，为深入学习有机打好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟练常见有机反应、官能团间的相互转化等，为突破有机推断做好铺垫。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14369', '初一全年语文课程', '4', '独家“专题教学法”教你阅读写作', '', '&#60;pre&#62;&#38;nbsp;&#60;/pre&#62;&#13;&#10;&#60;pre&#62;&#38;nbsp;&#60;/pre&#62;', '/data/default/kecheng/eb7/f11/ok9t0dulg6r2jsjaj73m0ctlj11356516733.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356578003.jpg\";i:1;s:70:\"/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356578007.jpg\";i:2;s:70:\"/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356578011.jpg\";}', 'a:1:{i:1001;s:2766:\"<pre><span style=\"font-size: small;\"><strong>正在学习初一语文的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">是不是还沉浸在小学语文学习的内容？是不是对于初中的语文学习一头雾水、茫然无措？是不是渴望找到一种方法能够打牢初中语文学习基础、实现小学到初中的课程衔接？是不是希望摆脱小学灌输式的枯燥无味的学习方式，渴望养成良好的学习习惯？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">初一全年的语文学习，我们采用德智独家&ldquo;专题教学法&rdquo;，为你精心设置了&ldquo;腹有诗书气自华系列课程&rdquo;之&ldquo;成长篇&rdquo;和&ldquo;亲情篇&rdquo;，课程仅仅围绕这两个主题设置，在基础知识、阅读方法、写作技巧等方面予以指导。并且在初一阶段，我们还带你一起认识了中国古典诗歌的源起和发展的初始阶段，一起走近《诗经》《古诗十九首》等，了解建安风骨和陶渊明，这都为我们以后古诗词的学习与鉴赏打下良好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">成长与亲情是不是与初一的你密切相关呢？所以我们课程的设置目的，不只为了让你学好语文，更为让你上好&ldquo;人生成长&rdquo;的课堂。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.跨越小升初学习障碍，打好语文学习基础</span></pre>\r\n<pre><span style=\"font-size: small;\">2.阅读、写作，双管齐下，语文成绩迅速提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.结合最新中考试题，从命题角度教你扫清现代文阅读障碍，提前与中考&ldquo;面对面&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.每节课都从不同的角度给你写作的技巧，整个课程结束，写作技巧全面覆盖，应付考试作文，绰绰有余。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.摆脱灌输式学习，培养自主学习能力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356577778.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356577791.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356577800.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356577810.jpg\" alt=\"\" />\";}', '360.00', '18', '31104000', '152', '3301', null, '', '1356321461', '1356321461', '0', '渴望增加阅读积累，丰富人生体验的学生。', '<pre><span style=\"font-size: small;\">全国各版本初一年级学生</span></pre>', '<pre><span style=\"font-size: small;\">1.扎实基础，掌握阅读和写作技巧，初中语文领先起跑。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.养成良好的学习和阅读习惯，学会自主学习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.明确中考现代文阅读的考查角度，针对不同角度，学会每个角度的应答策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.懂得思考成长过程中遇到的各种问题，结合自身的成长经历，获得新的人生体验。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14370', '初二全年语文课程', '4', '独家“专题教学法”教你阅读写作', '', '', '/data/default/kecheng/eb7/f11/ok9t0dulg6r2jsjaj73m0ctlj11356516759.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356579702.jpg\";i:1;s:70:\"/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356579706.jpg\";i:2;s:70:\"/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356579716.jpg\";}', 'a:1:{i:1001;s:2319:\"<pre><span style=\"font-size: small;\"><strong>正在学习初二语文的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">学习了初一全年的记叙文，一定收获不小吧。那升入初二的你，是不是不满足于记叙文的学习，想接触更多的文体？想掌握更多的阅读方法和写作技巧？想从语文学习中得到更多的乐趣？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>我们的课程特色：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">初二的全年课程，我们为你安排了&ldquo;腹有诗书气自华系列课程&rdquo;之&ldquo;认识篇&rdquo;，即&ldquo;人文情怀&rdquo;和&ldquo;科技天地&rdquo;两个方面。本次课程主要涵盖记叙文和说明文两种文体，学习的核心仍然是阅读与写作。初二上学期，我们为你安排的文体依然是记叙文，主要带你一起认识自己、认识世界、认识自然&hellip;&hellip;初二下学期，主要以&ldquo;说明文专题&rdquo;为主，说明文选文覆盖了所有中考说明文考查的热门题材和必考内容，帮你轻松搞定说明文阅读，还将带你穿越星空，探秘人体，感悟世间百态，开拓你的阅读视野。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">阅读其实是与作者的对话。所以在阅读过程中，大家往往会觉得&ldquo;阅读很容易，答题很困难&rdquo;，这是因为你没有真正理解作者的&ldquo;说话&rdquo;意图和文章的考查角度，缺少熟练、规范的答题方法。写起文章，也觉得无从下笔，不知如何选材、立意、构思。平时看了很多书，积累了很多素材，却又苦于不懂得何时运用、如何运用。这些，我们都将在课堂上给你。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong>&nbsp;</span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356579609.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356579618.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2012/12/27/uqe3tnfuqngqqjemiidrudr9861356579628.jpg\" alt=\"\" />\";}', '360.00', '18', '31104000', '96', '3301', null, '', '1356321668', '1356321668', '0', '渴望增加阅读积累，丰富人生体验的学生', '<pre><span style=\"font-size: small;\">全国各版本初二年级学生</span></pre>', '<pre><span style=\"font-size: small;\">1.你将学会如何分析记叙文，如何体会作者情感，轻松把握答题技巧和阅读规律&hellip;&hellip;而这些，都是语文获取高分的必备法宝。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.结合最新中考文章，讲解说明文的说明方法及作用。说明文的阅读方法指导，教你准确筛选说明文信息。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.在写作方面教你拓宽思路，展开联想，学会托物言志的写作手法。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14371', '江苏小高考文科班（含地理）', '2', '把握核心知识，小高考轻松突破A', '', '', '/data/default/kecheng/adb/4ed/hoed7ri9h6sfi2983c8ue36l551356427319.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/98a/acb/iqaocdivfmeu1ed0vmbc5nsrg51356752201.jpg\";}', 'a:4:{i:1004;s:973:\"<pre><span style=\"font-size: small;\"><strong>选考物理的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.想象不出物理情境，不会分析物体受力，不会分析物理过程。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.感觉自己听懂了、学会了，但题型稍有变化，还是不会做。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对功和能的各种关系不会分析，只能生搬硬套公式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.迅速建立物理模型，剖析物体受力，轻松入门高中物理。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.解决匀变速运动公式混淆不清，不会选择适合的公式求解问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.引导你建立求解功和能的物理模型，找到自己最擅长的解题思路。</span></pre>\";i:1005;s:1168:\"<pre><span style=\"font-size: small;\"><strong>选考化学的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.知识内容覆盖面较广，虽然难度不大，但让你的复习难于梳理。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.元素化合物知识内容多、繁杂，学习没头绪或者没条理。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.对化学多方面知识的认识理解缺少系统，没有建立化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结知识，提炼方法，归纳技巧，例如，解题突破口&mdash;&mdash;特征化学方程式（例如Al的两性反应）。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.科学梳理金属、非金属及其化合物之间反应及转化，让丰富知识条理、科学、系统。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.初步掌握化学基本理论的知识，学会原理基本应用、能量分析及简单有机化合物性质和反应，轻松应对小高考。<strong> </strong> </span></pre>\";i:1006;s:1196:\"<pre><span style=\"font-size: small;\"><strong>选考生物的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.感觉细胞的分子组成、结构及代谢，内容抽象、陌生、零碎，记不住。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.涉及实验及推理、相关计算等综合的问题，感觉难度大，无法入手。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.感觉人体神经调节的结构基础和调节过程名词概念多，涉及微观层面多，很抽象。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.运用实验法进行研究，体会实验法中的自变量、因变量和无关变量的含义，学会在相关实验资料中识别它们，并运用三者之间的相互关系解决实际问题。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.学习记忆相关名词概念的基本策略，即理解意义才能记住、用准。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.建立生态系统组成成分模型、碳循环模型，并在解决实际问题中运用，获得分析思路。</span></pre>\";i:1008;s:1021:\"<pre><span style=\"font-size: small;\"><strong>选考地理的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.地理概念多，易混淆，理论抽象，不好理解。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.各种图像类题目众多，不能把握图像中的关键信息，影响解题。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.题目综合性强，无法找到正确的解题思路和方法。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.突破图像题，提高从图像中提取信息、加工整合信息的能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在主案例之外，提供相似案例，引导迁移和应用，尽快形成分析问题、解决问题的能力。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.考点梳理与典型例题相结合，以考点带例题，以例题学考点，面向考试，招招带劲。</span></pre>\";}', '1200.00', '60', '31104000', '63', '3303', null, '', '1356427270', '1356427270', '1', '即将参加小高考文科学生', '<pre><span style=\"font-size: small;\">适用于江苏省参加小高考文科考试（含地理）学生</span></pre>', '<pre><span style=\"font-size: small;\">1.能学会对物体进行受力分析及运动过程分析，不再认为物理题难懂。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.综合整理，详细解析，深入理解灵活运用基本概念、规律。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对试题进行归纳总结、融会贯通，最终形成&ldquo;能力网&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.提升灵活解决实验问题的能力，做题有思路、有方法。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14372', '江苏小高考文科班（含政治）', '2', '把握核心考点，小高考轻松突破A', '', '', '/data/default/kecheng/adb/4ed/hoed7ri9h6sfi2983c8ue36l551356428269.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/98a/acb/iqaocdivfmeu1ed0vmbc5nsrg51356752344.jpg\";}', 'a:4:{i:1004;s:973:\"<pre><span style=\"font-size: small;\"><strong>选考物理的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.想象不出物理情境，不会分析物体受力，不会分析物理过程。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.感觉自己听懂了、学会了，但题型稍有变化，还是不会做。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对功和能的各种关系不会分析，只能生搬硬套公式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.迅速建立物理模型，剖析物体受力，轻松入门高中物理。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.解决匀变速运动公式混淆不清，不会选择适合的公式求解问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.引导你建立求解功和能的物理模型，找到自己最擅长的解题思路。</span></pre>\";i:1005;s:1168:\"<pre><span style=\"font-size: small;\"><strong>选考化学的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.知识内容覆盖面较广，虽然难度不大，但让你的复习难于梳理。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.元素化合物知识内容多、繁杂，学习没头绪或者没条理。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.对化学多方面知识的认识理解缺少系统，没有建立化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结知识，提炼方法，归纳技巧，例如，解题突破口&mdash;&mdash;特征化学方程式（例如Al的两性反应）。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.科学梳理金属、非金属及其化合物之间反应及转化，让丰富知识条理、科学、系统。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.初步掌握化学基本理论的知识，学会原理基本应用、能量分析及简单有机化合物性质和反应，轻松应对小高考。<strong> </strong> </span></pre>\";i:1006;s:1196:\"<pre><span style=\"font-size: small;\"><strong>选考生物的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.感觉细胞的分子组成、结构及代谢，内容抽象、陌生、零碎，记不住。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.涉及实验及推理、相关计算等综合的问题，感觉难度大，无法入手。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.感觉人体神经调节的结构基础和调节过程名词概念多，涉及微观层面多，很抽象。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.运用实验法进行研究，体会实验法中的自变量、因变量和无关变量的含义，学会在相关实验资料中识别它们，并运用三者之间的相互关系解决实际问题。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.学习记忆相关名词概念的基本策略，即理解意义才能记住、用准。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.建立生态系统组成成分模型、碳循环模型，并在解决实际问题中运用，获得分析思路。</span></pre>\";i:1009;s:1266:\"<pre><span style=\"font-size: small;\"><strong>选考政治的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.分配与公平的关系该如何正确处理，怎样加快经济发展方式的转变？ </span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何正确理解民主与专政这两个矛盾体，政府的职能对内和对外有何不同？体现在哪？ </span></pre>\r\n<pre><span style=\"font-size: small;\">3.面对丰富多彩的文化生活，如何从具体的文化形式中抽出精髓&mdash;&mdash;内涵、作用、先进文化、民族精神？面对这一具体到抽象的转变过程，你能应对自如么？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.联系现实经济生活中的现象和问题，综合运用经济学理论分析现象、探求规律，整合理论知识，提高学生的解题技巧和能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.引导学生分清不同行为主体在经济生活中的不同地位和作用。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.联系现实政治生活中的热点问题，教你如何用文化的视角去分析、看待问题。</span></pre>\";}', '1200.00', '60', '31104000', '54', '3303', null, '', '1356428278', '1356428278', '1', '即将参加小高考的文科学生', '<pre><span style=\"font-size: small;\">适用于江苏省参加小高考文科考试（含政治）学生</span></pre>', '<pre><span style=\"font-size: small;\">1.能学会对物体进行受力分析及运动过程分析，不再认为物理题难懂。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.综合整理，详细解析，深入理解灵活运用基本概念、规律。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对试题进行归纳总结、融会贯通，最终形成&ldquo;能力网&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.提升灵活解决实验问题的能力，做题有思路、有方法。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14373', '中考语文二轮复习课程', '3', '', '', '', '/data/default/kecheng/dc0/153/uqe3tnfuqngqqjemiidrudr9861356588799.jpg', 'a:0:{}', 'a:1:{i:1001;s:2673:\"<pre><span style=\"font-size: small;\"><strong>正在中考二轮复习的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">现代文阅读做了一堆，是不是经常有审题不明，答题不准的情况？中考文言文课内与课外各占比例是多少，课内又有哪些是必考篇目？今年中考作文命题形式以哪种为主？材料作文？话题作文？命题作文还是半命题作文？这些作文怎么得高分&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本次语文中考二轮复习课程，我们完全是按照中考语文的考查内容和考查顺序为你设置的。对于中考必考的四大模块：基础知识、综合性学习、现代文阅读和写作，我们从每一模块内容的考查形式入手，归纳考点，并根据考点给你相应的解决方案。既让你提前熟悉中考卷面，又能第一时间把握最新中考动态和命题规律。告诉你中考常见的文言篇目有哪些，现代文阅读如何审题，如何根据作者的写作意图准确答题&hellip;&hellip;这些，让你中考语文复习不再困惑。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/d49/402/3501/2013/01/24/9j7a45q4fqcccidhf30chba0q71359008610.jpg\" alt=\"\" /></span></pre>\r\n<pre><span style=\"font-size: small;\"><img style=\"white-space: normal;\" src=\"/data/default/upload/site/d49/402/3501/2013/01/24/9j7a45q4fqcccidhf30chba0q71359008639.jpg\" alt=\"\" /></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/d49/402/3501/2013/01/24/9j7a45q4fqcccidhf30chba0q71359008657.jpg\" alt=\"\" /></span></pre>\r\n<pre><span style=\"font-size: small;\"><img style=\"font-size: 15px; white-space: normal;\" src=\"/data/default/upload/site/d49/402/3501/2013/01/24/9j7a45q4fqcccidhf30chba0q71359008672.jpg\" alt=\"\" /></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\"><strong>温馨提示：</strong></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">1.德智课程质量要求严格，经过多个严谨的制作流程完成，每一流程都要花费很多精力与时间。为保证课程质量并兼顾学习进度，课程将采用陆续更新的方式上线，每周至少上线1～2节课，敬请关注。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">2.课时列表会根据课程标准、考试说明及教材变动等做及时的更新和调整。</span></pre>\r\n<pre>&nbsp;</pre>\";}', '160.00', '8', '31104000', '97', '3301', null, '', '1356434282', '1356434282', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初三年级备考学生</span></pre>', '<pre><span style=\"font-size: small;\">1.复习巩固基础知识，掌握中考必考的基础考查类型，如重点成语运用，常见病句类型、重点诗句默写等内容。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.了解综合性学习的考查形式及出题规律，社会普遍关注的特点问题，最易成为综合性学习的命题根据。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.从读懂作者的写作意图入手，分析问题，解答题目，不再模棱两可。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14374', '初一全年数学课程', '4', '', '', '', '/data/default/kecheng/dc0/153/6ig93vj2kclpdlco7ubqvi4vn61356586109.jpg', 'a:0:{}', 'a:1:{i:1002;s:4748:\"<span style=\"font-family: %value; font-size: small;\"><strong><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana; mso-bidi-font-family: 宋体;\">学习初一的你：</span></strong></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">是不是简单的计算题，还是经常出现小错误？掉数字、掉字母、去括号不变号</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">&hellip;&hellip;</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">是不是看到应用题就犯怵，我该设什么？我怎么列等式？</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">是不是看到几何语言就犯晕，不明白它说的到底是什么意思？</span></span><br /><span style=\"font-family: %value; font-size: small;\"><strong><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana; mso-bidi-font-family: 宋体;\">德智课程帮助你：</span></strong><strong></strong></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">1.</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">计算题一步一步细致讲解，指出计算的出错点。教你理解和熟记运算法则，不仅仅会用</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">,</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">还知道如何用！</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">2.</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">大段文字找关键词，教你如何找到题中的数量关系，用什么建立相等的条件，加强你的建模思想的认识！</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">3.</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">反复进行</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">&ldquo;</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">几何模型</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\';\">&rarr;</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">图形</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\';\">&rarr;</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">文字</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\';\">&rarr;</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">符号</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">&rdquo;</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">的练习，让你对几何语言不在陌生！</span></span><br /><span style=\"font-family: %value; font-size: small;\"><strong><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana; mso-bidi-font-family: 宋体;\">你的精彩课堂</span></strong><strong></strong></span><br /><span style=\"font-family: %value; font-size: small;\"><img src=\"/data/default/upload/site/54b/530/system/2013/01/21/q5veiq928v822h0icvcioaej261358754984.jpg\" alt=\"\" /></span><br /><span style=\"font-family: %value; font-size: small;\"><img src=\"/data/default/upload/site/54b/530/system/2013/01/21/q5veiq928v822h0icvcioaej261358754998.jpg\" alt=\"\" /></span>\";}', '420.00', '21', '31104000', '301', '3301', null, '', '1356490172', '1356490172', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初一学生</span></pre>', '<span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">（</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">1</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">）重点知识的再次学习，加深理解与记忆！</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">（</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">2</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">）对运算法则更加熟练的运用，做计算题更简单，更准确！</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">（</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">3</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">）初步形成几何语言的组织运用和理解能力，为之后的几何学习打下坚实基础！</span></span><br /><span style=\"font-family: %value; font-size: small;\"><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">（</span><span style=\"mso-bidi-font-size: 12.0pt; mso-bidi-font-family: 宋体;\" lang=\"EN-US\">4</span><span style=\"mso-bidi-font-size: 12.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: Verdana;\">）方程思想，分类讨论思想等数学重要思想的入门学习！</span></span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14375', '初二全年物理课程', '4', '初二物理重难点逐一击破', '京城名校高级教师', '', '/data/default/kecheng/eb7/f11/cqdvljsreugcjr376qhmm094d61356505720.jpg', 'a:2:{i:0;s:70:\"/data/default/kecheng/eb7/f11/cqdvljsreugcjr376qhmm094d61356506712.jpg\";i:1;s:70:\"/data/default/kecheng/eb7/f11/cqdvljsreugcjr376qhmm094d61356506731.jpg\";}', 'a:1:{i:1004;s:1311:\"<span style=\"font-size: small;\"><strong>学习初二物理的你，是否存在以下焦虑？</strong></span><br /><span style=\"font-size: small;\">1.在课堂上听不懂老师所讲的概念与规律，不知道如何学物理；</span><br /><span style=\"font-size: small;\">2.虽然在课堂上听懂了，但是一到做题就眼高手低，无法正确解答！</span><br /><span style=\"font-size: small;\">3.你平时掌握的并不错，但是考试仍旧无法轻松拿高分，一到考试就焦虑！</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.弄清最基本的物理概念与规律，教会你正确的学习方法。</span><br /><span style=\"font-size: small;\">2.老师带领大家一起分析题目，理清思路，让你看到题目不再迷惑。</span><br /><span style=\"font-size: small;\">3.传授你最实用的考试技巧与规律方法，让你轻轻松松迎接各种考试！</span><br /><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692135.png\" alt=\"\" /><br /><img src=\"/data/default/upload/formula/1b0/114/1071/2012/12/05/irutbfdl4cohvria64ndodagq41354692156.png\" alt=\"\" /></strong></span>\";}', '340.00', '17', '31104000', '231', '3301', null, '', '1356505732', '1356505732', '0', '初二年级学生', '<pre><span style=\"font-size: small;\">初二年级的同学，或者想复习力学、声学、光学的初三同学。</span></pre>', '<span style=\"font-size: small;\"><strong>课程学习效果</strong></span><br /><span style=\"font-size: small;\">学习本课程前，请先下载学案，仔细看一遍，这样学习效果更好。通过本课程的学习，你将在以下几个方面有所突破：</span><br /><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span><br /><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会一类题的解法。</span><br /><span style=\"font-size: small;\">3.积累一定的解题技巧，在考试中能快速解题，提高准确率。</span><br /><span style=\"font-size: small;\">4.掌握正确的学习方法，内化成物理学科能力。</span><br /><span style=\"font-size: small;\">5.轻松解决声学、光学、热学、力学板块的知识，学会如何应用这些知识解题，形成正确的解题思路，掌握规范的答题步骤。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14376', '初一全年英语课程', '4', '', '', '', '/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356507640.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356515434.png\";i:1;s:70:\"/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356515436.png\";i:2;s:70:\"/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356515441.png\";}', 'a:1:{i:1003;s:2026:\"<pre><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">学习初一英语的你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.科目变多，课本变厚，难度加深，很难适应初中的学习生活。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.课前不会预习，课后不会做题，课业负担重，常常手忙脚乱。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.词类和句子等基础知识掌握不夯实，写作成为老大难问题。</span><br /><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">德智课程帮助你</span></strong><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">1.衔接小升初，快速进入角色，适应初中的学习方法。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">2.从不同词性展开讲解，逐一分析，扩充你的词汇量。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">3.学习整理写作思路，收集写作素材，建立正确的写作方法。</span><br /><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\">4.培养良好学习习惯，形成正确的学习方法，为整个初中打下夯实的基础。</span><strong><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><br />你的精彩课堂</span></strong></pre>\r\n<pre><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong></strong><strong><img src=\"/data/default/upload/formula/54b/530/system/2012/12/26/f1s1pgqjj2ntgvqtc2bpjh5bh31356515207.png\" alt=\"\" /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/26/f1s1pgqjj2ntgvqtc2bpjh5bh31356515245.png\" alt=\"\" /><br /></strong></span></pre>\r\n<pre><span style=\"font-family: arial,helvetica,sans-serif; font-size: small;\"><strong><br /></strong></span></pre>\";}', '300.00', '15', '31104000', '240', '3301', null, '', '1356506720', '1356506720', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初一学生</span></pre>', '<pre><span style=\"font-size: small;\">1.轻松掌握比较级、进行时、there be、疑问句以及名词代词的重难点知识。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.吃透动词的相关知识点，掌握最基本的动词才能为以后学习句子打下基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟练掌握数词、介词、副词等词类使用方法，增加词汇储备量。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.能够做到课上消化吸收所学内容，课后独立完成作业，养成良好的学习习惯。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14377', '初二全年英语课程', '4', '', '', '', '/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356507745.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356516274.png\";i:1;s:70:\"/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356516277.png\";i:2;s:70:\"/data/default/kecheng/eb7/f11/f1s1pgqjj2ntgvqtc2bpjh5bh31356516279.png\";}', 'a:1:{i:1003;s:1747:\"<pre><span style=\"font-size: small;\"><strong>遭遇&ldquo;初二现象&ldquo;的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.成绩忽高忽低，上课开始听不懂、跟不上，学习站在&ldquo;分水岭&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.单词难度，语法深度，写作高度陡然徒增，成绩进入&ldquo;爬坡期&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时态语态易混易错，词性词法区分不清，写作常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.初一稀里糊涂，初二转瞬即逝，不知如何为初三做准备。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.全面讲解初中重点语法知识，系统梳理学习思路和方法。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.归纳所有重点易混词组，对比记忆，强化练习，熟练掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.总结动词重点时态，横向与纵向结合讲解，轻松突破时态难关。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.深度剖析词类、句子，掌握基础才是王道，为从句、写作、阅读等难点铺路。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><br /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/26/f1s1pgqjj2ntgvqtc2bpjh5bh31356515694.png\" alt=\"\" /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/26/f1s1pgqjj2ntgvqtc2bpjh5bh31356515727.png\" alt=\"\" /><br /><br style=\"color: #ff0000; font-size: small;\" /></strong></span></pre>\r\n<p align=\"left\">&nbsp;</p>\r\n<strong></strong><br /><br />\";}', '300.00', '15', '31104000', '187', '3301', null, '', '1356506837', '1356506837', '0', '', '<pre><span style=\"font-size: small;\">全国各版本初二学生</span></pre>', '<pre><span style=\"font-size: small;\">1.彻底扫清过去、现在、完成、将来四大时态重难点，易考点易错点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.复习形容词、副词、介词、代词，掌握句子种类区分，夯实所学基础知识。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握状语从句和先行词、情态动词两大难点内容的学习，轻松完成相关题目。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.必考易考词组易混辨析，写作步骤方法归纳，提升英语整体得分能力。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14378', '初二全年数学课程', '4', '', '', '', '/data/default/kecheng/dc0/153/6ig93vj2kclpdlco7ubqvi4vn61356586162.jpg', 'a:0:{}', 'a:1:{i:1002;s:1351:\"<pre><span style=\"font-size: small;\"><strong>学习初二的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.证明全等，找条件是否经常遇到瓶颈？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.证明过程如何写，你是否书写规范？条理是否清晰？</span></pre>\r\n<pre><span style=\"font-size: small;\">3.函数&mdash;&mdash;概念是否理解？应用题如何寻找等式关系？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.探索研究能证明全等的方法，对全等条件进行一个深刻了解，同时培养从条件中找隐含条件的能力。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.证明推理逻辑强，推理表达需精准，由简单例题体会证明思路与格式，逐渐增加题目复杂程度，循序渐进过程。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.简单例子，带你领会从抽象到具体的转化过程，理解函数定义的变化与对应关系。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<span style=\"font-size: small;\">&nbsp;&nbsp;<img src=\"/data/default/upload/site/54b/530/system/2013/01/21/q5veiq928v822h0icvcioaej261358754545.jpg\" alt=\"\" />&nbsp;</span>\";}', '480.00', '24', '31104000', '291', '3301', null, '', '1356513680', '1356513680', '0', '', '全国各版本初二学生', '<pre><span style=\"font-family: %value; font-size: small;\">1.查漏补缺，找出知识漏洞，熟悉知识考点、攻破知识难点。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.温故知新，对所学知识巩固复习，加深理解与记忆</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.知识拓展，学习课堂之外的东西，对知识进行一个丰富过程。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">4.开拓数学思维，例题的细致讲解带你逐步理解解题思路，教你如何思考问题，初步锻炼数学思维，同时一道题多种解法，解救你的思维局限！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14379', '江苏小高考理科班（含化学）', '2', '', '', '', '/data/default/kecheng/eb7/f11/u0mkq6usucku7dj4gvvb033uk01356513675.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/9b6/687/8785k8hqvmba121d3ioqqqn8671356665590.png\";}', 'a:4:{i:1005;s:1107:\"<pre><span style=\"font-size: small;\"><strong>选考化学的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.知识内容覆盖面较广，虽然难度不大，但让你的复习难于梳理。<br />2.元素化合物知识内容多、繁杂，学习没头绪或者没条理。<br />3.对化学多方面知识的认识理解缺少系统，没有建立化学学习思维模式。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结知识，提炼方法，归纳技巧，例如，解题突破口&mdash;&mdash;特征化学方程式（例如Al的两性反应）。<br />2.科学梳理金属、非金属及其化合物之间反应及转化，让丰富知识条理、科学、系统。<br />3.<strong>独家奉献</strong>：Al（OH）<sub>3</sub>与NaOH（不足、过量）反应快速计算公式。<br />4.初步掌握化学基本理论的知识，学会原理基本应用、能量分析及简单有机化合物性质和反应，轻松应对小高考。<strong><br /></strong><br /></span></pre>\";i:1007;s:852:\"<pre><span style=\"font-size: small;\"><strong>选考历史的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.不能清楚地分析不同国家与地区的社会经济发展模式及影响因素。<br />2.从历史发展的角度，无法清晰分析不同政治制度对历史进程的影响。<br />3.专题间的思维跳跃性大，重难点知识把握不准，浪费过多复习精力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.抓住主干知识，着重梳理重要史实、历史概念、历史结论、阶段特征、基本线索、发展过程等。<br />2.学会客观、辩证地分析不同国家与地区的社会经济发展模式。<br />3.构建单元专题知识体系，归纳解题技巧与规律。</span></pre>\";i:1008;s:834:\"<pre><span style=\"font-size: small;\"><strong>选考地理的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.地理概念多，易混淆，理论抽象，不好理解。<br />2.各种图像类题目众多，不能把握图像中的关键信息，影响解题。<br />3.题目综合性强，无法找到正确的解题思路和方法。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.突破图像题，提高从图像中提取信息、加工整合信息的能力。<br />2.在主案例之外，提供相似案例，引导迁移和应用，尽快形成分析问题、解决问题的能力。<br />3.考点梳理与典型例题相结合，以考点带例题，以例题学考点，面向考试，招招带劲。</span></pre>\";i:1009;s:1118:\"<pre><span style=\"font-size: small;\"><strong>选考政治的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.分配与公平的关系该如何正确处理，怎样加快经济发展方式的转变？<br />2.如何正确理解民主与专政这两个矛盾体，政府的职能对内和对外有何不同？体现在哪？<br />3.面对丰富多彩的文化生活，如何从具体的文化形式中抽出精髓&mdash;&mdash;内涵、作用、先进文化、民族精神？面对这一具体到抽象的转变过程，你能应对自如么？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.联系现实经济生活中的现象和问题，综合运用经济学理论分析现象、探求规律，整合理论知识，提高学生的解题技巧和能力。</span><span style=\"font-size: small;\"><br />2.引导学生分清不同行为主体在经济生活中的不同地位和作用。<br />3.联系现实政治生活中的热点问题，教你如何用文化的视角去分析、看待问题。</span></pre>\";}', '900.00', '45', '31104000', '107', '3302', null, '', '1356513697', '1356513697', '1', '', '<pre><span style=\"font-size: small;\">江苏省参加小高考理科考试（含化学）学生</span></pre>', '<pre><span style=\"font-size: small;\">1.立足基础知识，抓纲靠本，力求做到&ldquo;面全，线通，点透&rdquo;。<br />2.学会迁移应用，通过不同情景中的应用，有效提升分析问题、解决问题的能力。<br />3.联系近几年试题的不同特点，提供计算题、分析题等多种题型的思维路径。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14380', '初三全年数学课程', '4', '', '', '&#60;pre&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;&#60;strong&#62;学习初三的你：是否也有以下&#60;/strong&#62;&#60;strong&#62;&#38;ldquo;&#60;/strong&#62;&#60;strong&#62;症状&#60;/strong&#62;&#60;strong&#62;&#38;rdquo;&#60;/strong&#62;&#60;strong&#62;：&#60;/strong&#62;&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;1.解决实际应用问题，找不到相等关系，判断最值失误。&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;2.圆的切线证明，经常找不到突破口，往往走进死胡同！&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;3.面对二次函数问题，你是否能&#38;ldquo;潇洒&#38;rdquo;面对？&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;&#60;strong&#62;德智课程帮助你：&#60;/strong&#62;&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;1.弄清实际问题背景，把有关数量关系分析透彻，特别是找出作为列方程依据的主要相等关系。&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;2.帮你寻找添加辅助线的思路，而不是凭着自己的感觉去胡乱添加。&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;3.二次函数形式的特点分析，独到而全面的例题教你如何设二次函数，能更快、更准的 求出函数解析式。&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;4.典型例题，细致讲解，方法归纳教你面对最值问题不再烦恼。&#60;/span&#62;&#60;br /&#62;&#60;span style=&#34;font-family: %value; font-size: small;&#34;&#62;5.相似的模型归纳，模型特点的详细阐述，让你面对几何图形，不再害怕。&#60;/span&#62;&#60;/pre&#62;', '/data/default/kecheng/dc0/153/6ig93vj2kclpdlco7ubqvi4vn61356586198.jpg', 'a:0:{}', 'a:0:{}', '380.00', '19', '31104000', '179', '3301', null, '', '1356514484', '1356514484', '0', '', '<pre><span style=\"font-family: %value; font-size: small;\">全国初三学生</span></pre>', '<pre><span style=\"font-family: %value; font-size: small;\">1.对所学重点知识加强记忆与理解，温故而知新！</span><br /><span style=\"font-family: %value; font-size: small;\">2.对二次函数的图像与系数之间的关系认识更加深刻，不只是停留于课本上的知识，彻底弄清二次函数的图像、性质！</span><br /><span style=\"font-family: %value; font-size: small;\">3.对相似有更深刻的认识，能从几何图形中，提炼模型，帮你更快，更准的解答几何题！</span><br /><span style=\"font-family: %value; font-size: small;\">4.理清三角函数，让你对一大堆的公式记忆不再模糊！</span><br /><span style=\"font-family: %value; font-size: small;\">5.分类讨论，数形变换，数形结合的思想面面俱到，让你的思维来个大解放！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14381', '江苏小高考理科班（含生物）', '2', '', '', '', '/data/default/kecheng/eb7/f11/u0mkq6usucku7dj4gvvb033uk01356514649.jpg', 'a:1:{i:0;s:70:\"/data/default/kecheng/9b6/687/8785k8hqvmba121d3ioqqqn8671356665648.png\";}', 'a:4:{i:1006;s:1008:\"<pre><span style=\"font-size: small;\"><strong>选考生物的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.感觉细胞的分子组成、结构及代谢，内容抽象、陌生、零碎，记不住。<br />2.涉及实验及推理、相关计算等综合的问题，感觉难度大，无法入手。<br />3.感觉人体神经调节的结构基础和调节过程名词概念多，涉及微观层面多，很抽象。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.运用实验法进行研究，体会实验法中的自变量、因变量和无关变量的含义，学会在相关实验资料中识别它们，并运用三者之间的相互关系解决实际问题。<br />2.学习记忆相关名词概念的基本策略，即理解意义才能记住、用准。<br />3.建立生态系统组成成分模型、碳循环模型，并在解决实际问题中运用，获得分析思路。</span></pre>\";i:1007;s:852:\"<pre><span style=\"font-size: small;\"><strong>选考历史的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.不能清楚地分析不同国家与地区的社会经济发展模式及影响因素。<br />2.从历史发展的角度，无法清晰分析不同政治制度对历史进程的影响。<br />3.专题间的思维跳跃性大，重难点知识把握不准，浪费过多复习精力。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.抓住主干知识，着重梳理重要史实、历史概念、历史结论、阶段特征、基本线索、发展过程等。<br />2.学会客观、辩证地分析不同国家与地区的社会经济发展模式。<br />3.构建单元专题知识体系，归纳解题技巧与规律。</span></pre>\";i:1008;s:834:\"<pre><span style=\"font-size: small;\"><strong>选考地理的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.地理概念多，易混淆，理论抽象，不好理解。<br />2.各种图像类题目众多，不能把握图像中的关键信息，影响解题。<br />3.题目综合性强，无法找到正确的解题思路和方法。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.突破图像题，提高从图像中提取信息、加工整合信息的能力。<br />2.在主案例之外，提供相似案例，引导迁移和应用，尽快形成分析问题、解决问题的能力。<br />3.考点梳理与典型例题相结合，以考点带例题，以例题学考点，面向考试，招招带劲。</span></pre>\";i:1009;s:1118:\"<pre><span style=\"font-size: small;\"><strong>选考政治的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.分配与公平的关系该如何正确处理，怎样加快经济发展方式的转变？<br />2.如何正确理解民主与专政这两个矛盾体，政府的职能对内和对外有何不同？体现在哪？<br />3.面对丰富多彩的文化生活，如何从具体的文化形式中抽出精髓&mdash;&mdash;内涵、作用、先进文化、民族精神？面对这一具体到抽象的转变过程，你能应对自如么？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.联系现实经济生活中的现象和问题，综合运用经济学理论分析现象、探求规律，整合理论知识，提高学生的解题技巧和能力。</span><span style=\"font-size: small;\"><br />2.引导学生分清不同行为主体在经济生活中的不同地位和作用。<br />3.联系现实政治生活中的热点问题，教你如何用文化的视角去分析、看待问题。</span></pre>\";}', '760.00', '38', '31104000', '35', '3302', null, '', '1356514701', '1356514701', '0', '', '<pre><span style=\"font-size: small;\">适用于江苏省参加小高考理科考试（含生物）学生</span></pre>', '<pre><span style=\"font-size: small;\">1.立足基础知识，抓纲靠本，力求做到&ldquo;面全，线通，点透&rdquo;。<br />2.学会迁移应用，通过不同情景中的应用，有效提升分析问题、解决问题的能力。<br />3.提升灵活解决实验问题的能力，综合题型做到有思路、有方法</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14382', '初三中考大礼包', '3', '把握核心考点，顺利冲刺中考', '北京名校名师会聚', '', '/data/default/kecheng/1b3/881/ltjlj1umd71b55mcpf6eeo54941358507461.jpg', 'a:0:{}', 'a:5:{i:1001;s:1859:\"<pre><span style=\"font-size: small;\"><strong>正在中考二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">现代文阅读做了一堆，是不是经常有审题不明，答题不准的情况？中考文言文课内与课外各占比例是多少，课内又有哪些是必考篇目？今年中考作文命题形式以哪种为主？材料作文？话题作文？命题作文还是半命题作文？这些作文怎么写能得高分&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本次语文中考二轮复习课程，我们完全是按照中考语文的考查内容和考查顺序为你设置的。对于中考必考的四大模块：基础知识、综合性学习、现代文阅读和写作，我们从每一模块内容的考查形式入手，归纳考点，并根据考点给你相应的解决方案。既让你提前熟悉中考卷面，又能第一时间把握最新中考动态和命题规律。告诉你中考常见的文言篇目有哪些，现代文阅读如何审题，如何根据作者的写作意图准确答题&hellip;&hellip;这些，让你中考语文复习不再困惑。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.复习巩固基础知识，掌握中考必考的基础考查类型，如重点成语运用，常见病句类型、重点诗句默写等内容。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.了解综合性学习的考查形式及出题规律，社会普遍关注的特点问题，最易成为综合性学习的命题根据。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.从读懂作者的写作意图入手，分析问题，解答题目，不再模棱两可。</span></pre>\";i:1002;s:1540:\"<pre><strong><span style=\"font-family: %value; font-size: small;\">进入二轮复习的你</span></strong></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.随着中考日益逼近，压力也随之剧增，开始陷入自我焦虑与恐慌之中。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.每天数不清的数学试卷，让你开始厌烦这种无止境的考试&rarr;讲题&rarr;考试循环之中。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.考过同一类型的题，自己还是会出错，老是出现重复性错误。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">4.在仅剩的日子里不知道自己应该如何复习备战，才能达到高效复习。</span></pre>\r\n<pre><strong><span style=\"font-family: %value; font-size: small;\">德智课程帮助你</span></strong></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.直逼中考重难点内容，挑选中考当中失分率达80%以上的内容进行专题形式的讲解。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.历年中考真题详细剖析，让你知道中考要考你什么。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.让你明确中考当中，你的丢分点，失分点会在哪。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">4.题目讲解后进行专题总结归纳，帮你指明大体解题方向，总结题型规律。</span></pre>\";i:1003;s:1154:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.语法知识复习的很好，感觉自己什么都掌握了，做题却经常出错，找不到症结所在。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.常用句型、词组、习惯用法等方面的基础知识掌握不牢，单选、完型等题型经常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.阅读、写作等综合能力考查题型，得分不稳定，忽高忽低，&ldquo;挣分儿&rdquo;题变成了&ldquo;拉分题&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.查漏补缺，识别复习薄弱点，帮助你有针对性的复习，节省宝贵时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.辨别考点陷阱，归纳易错题型，帮助你灵活性的复习，归纳应试技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.单选、完型、阅读、写作全面复习，帮助你系统性的复习，提升综合能力。</span></pre>\";i:1004;s:2132:\"<pre><span style=\"font-size: small;\"><strong>面对中考的压力，你是否会感到以下焦虑</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.面对庞杂的物理知识，不知道哪些是重点，不知道如何有效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.虽然选择题与填空题比较简单，但还是会丢分，基础知识掌握不牢！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对于力学的复杂题目，一看就懵，不知道如何分析，更不知道怎么解题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.面对众多的电学知识，电功、电功率、欧姆定律、电路故障分析，更不知道如何面对。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.挑选最重点的知识进行复习，用最短的时间，达到最好的效果。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.帮你梳理中考的要求，结合近年的中考真题，细致分析，抓住重点，牢固掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.重点讲解力学中较难的知识，专门解决综合题！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.对电学的重点内容设置了相对较多的课时，重点突破各个重点板块，并配有专题总结分析。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">通过本课程的学习，你将在以下几个方面有所突破：</span></pre>\r\n<pre><span style=\"font-size: small;\">1.对物理概念的理解更加牢固，对物理规律的把握更加熟练。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握物理问题的解题思路，触类旁通，学会各类题的解法。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>\";i:1005;s:1510:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.面对中考压力大，不知从何开始复习，如何全面掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.知识零散，涉及面广，不能系统复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时间紧，任务重，不知如何高效复习，精准把握。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结中考能力要求，科学提示方法，有效制定策略，轻松全面复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结常考点，揭秘易错点，以题带点，知识与解题精讲精析，全面突破！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.提炼精华，抓准考试命脉，做到高效、精准复习！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.明确中考考向，做到复习方向精准，科学有效备考！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.中考五大块内容全面网罗，从身边的化学物质到实验综合题全面详细解析，让你的化学学习由难变易，熟练掌握！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握各类题型的解题方法、技巧，避开考试陷阱，高效复习，轻松应考！</span></pre>\";}', '2000.00', '45', '31104000', '181', '3301', null, '', '1356573726', '1356573726', '1', '即将中考的初中学生', '<pre><span style=\"font-size: small;\">初三年级学生，二轮复习使用</span></pre>', '<pre><span style=\"font-size: small;\">1.明确中考考向，做到复习方向精准，科学有效备考！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.查漏补缺，识别复习薄弱点，帮助你有针对性的复习，节省宝贵时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\"><strong><br /></strong></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\"><strong>温馨提示：</strong></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">1.德智课程质量要求严格，经过多个严谨的制作流程完成，每一流程都要花费很多精力与时间。为保证课程质量并兼顾学习进度，课程将采用陆续更新的方式上线，每周至少上线1～2节课，敬请关注。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">2.课时列表会根据课程标准、考试说明及教材变动等做及时的更新和调整。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14383', '中考数学二轮复习课程', '3', '', '', '', '/data/default/kecheng/dc0/153/6ig93vj2kclpdlco7ubqvi4vn61356575356.jpg', 'a:0:{}', 'a:1:{i:1002;s:1504:\"<pre><strong><span style=\"font-size: small;\">进入二轮复习的你：</span></strong></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.随着中考日益逼近，压力也随之剧增，开始陷入自我焦虑与恐慌之中。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.每天数不清的数学试卷，让你开始厌烦这种无止境的考试&rarr;讲题&rarr;考试循环之中。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.考过同一类型的题，自己还是会出错，老是出现重复性错误。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">4.在仅剩的日子里不知道自己应该如何复习备战，才能达到高效复习。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">德智课程帮助你：</span></strong></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.直逼中考重难点内容，挑选中考当中失分率达80%以上的内容进行专题形式的讲解。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.历年中考真题详细剖析，让你知道中考要考你什么。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.让你明确中考当中，你的丢分点，失分点会在哪。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">4.题目讲解后进行专题总结归纳，帮你指明大体解题方向，总结题型规律。</span></pre>\";}', '260.00', '13', '31104000', '95', '3301', null, '', '1356575392', '1356575392', '0', '', '<pre><span style=\"font-family: %value; font-size: small;\">全国各版本初三学生 二轮复习使用</span></pre>', '<pre><span style=\"font-family: %value; font-size: small;\">1.掌握选择填空3大类型难题：最值问题、规律探索问题、函数图象问题。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.掌握圆的证明与计算问题、二次函数综合问题。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.熟练方程思想、分类讨论、数形结合、动静结合等思想方法的应用。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14384', '高一下学期化学预习课（基础有机化合物）', '2', '掌握核心知识，打好有机学习基础', '北京高级教师', '', '/data/default/kecheng/9b6/687/6t0lghqladukr3dnoej6r91gd11356682383.jpg', 'a:0:{}', 'a:1:{i:1005;s:1309:\"<pre><span style=\"font-size: small;\"><strong>即将学习必修2有机部分的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.有机化学是高考的重点和难点，而此部分是有机化学的入门，不清楚如何为后续更好地学习有机打好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对于有机反应方程式的书写、配平方法把握不好，方程式书写困难。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.有机物种类、官能团、反应条件繁多，学习难度大，记忆混淆。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从简单内容开始，让你逐步理解有机的内涵，为后续学习做好铺垫。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.针对有机反应方程式书写和配平讲述巧妙方法，助你快速配平。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.通过对比、归纳全面整理，系统总结，清楚了解各有机物的组成特点及性质。</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">你的精彩课堂</span></strong></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2013/01/10/5mckk2f1k1mgva6sd60qhbgn851357801564.jpg\" alt=\"\" />\";}', '40.00', '2', '31104000', '24', '3301', null, '', '1356682389', '1356682389', '0', '高一学生', '<pre><span style=\"font-size: small;\">全国各版本高一学生</span></pre>', '<pre><span style=\"font-size: small;\">1.掌握甲烷、乙烯、苯、乙醇和乙酸的组成及性质，为更好地学习后续有机反应打好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握官能团、有机反应类型、反应条件等，为深入学习有机打好基础。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟练常见有机反应、官能团间的相互转化等，为突破有机综合题做好铺垫。</span></pre>\r\n<pre><span style=\"font-size: small;\">&nbsp;</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14385', '高三艺考生复习课程', '3', '', '', '', '/data/default/kecheng/05a/991/cqcikq1qrb2cgsoclfas7cnl151356924607.jpg', 'a:0:{}', 'a:6:{i:1001;s:1638:\"<pre><span><strong>正在复习的你</strong></span><br /><span>已经进入备考复习了，你准备好了吗？是不是觉得复习没有头绪，无从下手？文章阅读容易，答题不准？写作无从下笔，得分不高？古文生涩枯燥，理解太难？复习费时费力，提分太慢？</span><br /><span><strong>德智课堂帮助你</strong></span><br /><span>1.安排系统、全面的课程，帮你制定复习计划。本次的高考一轮复习课程，从基础知识、阅读理解、写作技巧等方面着手，覆盖了语文高考的全部的复习内容，帮你全面复习，肃清&ldquo;死角&rdquo;。</span><br /><span>2.阅读讲解详细、生动，为你归纳答题技巧。在阅读复习中，本次课程按照不同文体制定了复习策略，文言文、古诗词、散文、小说等，每一种文体都结合最新高考真题进行分析，在文章的讲解中得出相应的答题方法和解题技巧，让你答题&ldquo;有法可依，有路可循&rdquo;，做到答案精准。</span><br /><span>3.作文指导实用、新颖，让你轻松获得高分。本次课程中，老师将针对一个作文题目，从素材的积累、审题、立意、构思、语言等方面进行指导，提供多种可供借鉴的写作方法和模式，既实用又不落窠臼，帮你突破写作难于下笔的瓶颈。</span><br /><span>4.文言文复习不再枯燥。在文言文复习中，老师对最常见、最重要的实词、虚词的归纳，让你脱离&ldquo;文言词海&rdquo;的困扰；对几种特定句式总结，让你轻松翻译，读懂古文。</span></pre>\";i:1002;s:1483:\"<pre><span><strong>作为艺考生的你</strong></span><br /><span>1.数学知识点多而杂，像在学新课，但复习速度又快，往往重点没落实，细节容易忽略。</span><br /><span>2.面对数学难题时不知道该如何进行思考，如何寻找解决问题的途径和方法。</span><br /><span>3.不会总结规律，做了很多题，成绩提高却不明显。</span><br /><span><strong>德智课程帮助你</strong></span><br /><span>1.提炼六大必考且占分最多的知识模块（函数与导数、三角函数、立体几何、解析几何、数列、概率与统计），突出高考的重点和热点，让你对高考数学有一个清晰的认识，知道哪里是重点，哪里是难点，从而复习时做到心中有数，有的放矢。</span><br /><span>2.理清知识脉络：针对数学知识多且杂的特点，课程中每个专题都对本模块知识进行梳理和强化，让你对本专题的知识有个清晰的认识，形成自己的知识网络。</span><br /><span>3. 提炼和总结解题策略，培养逻辑思维能力，达到会解一道题从而会解这一类问题的目的，把你从茫茫题海中解放出来，帮助你在有限的时间内高效复习，彻底打消文科学生对数学的恐惧感。<br /><strong>你的精彩课堂</strong><br /></span></pre>\r\n<pre><span><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/05/hd21t36o6lh0t7r8vl6c2scmd41354690578.jpg\" alt=\"\" /></span></pre>\";i:1003;s:1034:\"<pre><strong><span>深受&ldquo;四不&rdquo;困扰的你</span></strong><br /><span>1.不能合理利用零散时间，制定简单有效的复习方案。</span><br /><span>2.不能形成系统的知识网络，孤零零的复习一块内容。</span><br /><span>3.不能够确保做到全面复习，不是覆盖面不全就是一个知识点反复吃不透。</span><br /><span>4.不能根据重难点以及分值分布等，科学规划，精准复习。</span><br /><strong><span>德智课程帮助你</span></strong><br /><span>1.夯实整个高三复习的基础，对重点、难点知识地毯式搜索讲解，全面复习。</span><br /><span>2.根据精选的必考、占分比高、及高频考点内容点睛讲解，精准复习。</span><br /><span>3.囊括高考金牌例题，总结应试方法策略，高效复习。<br /><strong>你的精彩课堂</strong><br /></span></pre>\r\n<pre><span><img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/05/hd21t36o6lh0t7r8vl6c2scmd41354690717.jpg\" alt=\"\" /></span></pre>\";i:1007;s:999:\"<pre><strong><span>进入复习的你</span></strong><br /><span>1.不能清楚认识人类文明的发展过程，物质文明、政治文明和精神文明不能深入理解。</span><br /><span>2.知识多，内容杂，不知如何系统理解，高效掌握。</span><br /><span>3.高考考查方向把握不准，导致复习针对性不强，对于难点历史知识与现实热点的结合难以突破。</span><br /><strong><span>德智课程帮助你</span></strong><br /><span>1.从物质文明、政治文明和精神文明方面清楚认识人类文明的发展过程。</span><br /><span>2.从中国古代史到世界现代史，从基础知识到构建体系，全方位复习，牢固掌握。</span><br /><span>3.提供详细的复习策略和高考动向，轻松突破历史知识与现实热点问题的结合。<br /><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/05/hd21t36o6lh0t7r8vl6c2scmd41354690917.jpg\" alt=\"\" />\";i:1008;s:882:\"<pre><strong><span>进入复习的你</span></strong><br /><span>1.知识杂乱，不能系统化，有效学习。</span><br /><span>2.盲目练习，毫无针对性，没有显著的复习成效。</span><br /><span>3.重难点知识把握不准，导致侧重点偏转，学习效果不好。</span><br /><strong><span>德智课程帮助你</span></strong><br /><span>1.教材全面整合，形成合理的知识结构和能力结构，把握地理知识和能力的内部联系。</span><br /><span>2.有针对性地精练，从而掌握知识的迁移应用能力、综合分析能力等等。</span><br /><span>3.对地理知识点进行深入解剖，准确把握考向，做到一次复习，全年受益。<br /><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2012/12/05/hd21t36o6lh0t7r8vl6c2scmd41354691016.jpg\" alt=\"\" />\";i:1009;s:748:\"<pre><span><strong>进入复习的你</strong><br />面对252个繁多的知识点，你还在死记硬背么？<br />如何用你所学去分析国内外经济政治热点？社会时事问题？你还在背诵答题模版么？<br /><strong>德智课程帮助你</strong><br />1.以新课程的新理念为指导，帮你建立知识体系网，梳理252个知识点之间的联系。<br />2.从提取和解读信息、调动和运用知识、分析和探究问题等方面展开讲解和训练，有效提高学生应考能力。<br />3.针对复习中存在的最直接最现实的困惑与问题进行讲解和训练，尤其是对解答非选择题中学生存在的困惑和解决办法，有最直接的指导价值和意义。</span></pre>\";}', '1200.00', '60', '31104000', '108', '3303', null, '', '1356924626', '1356924626', '1', '', '适合全国艺术考试类学生', '', '3701');
INSERT INTO `kc_course_pack` VALUES ('14386', '高三英语三轮复习课程', '3', '', '', '', '/data/default/kecheng/a41/e8e/28d6nmfpfl0fccbs1u6v4gen161357524489.jpg', 'a:0:{}', 'a:1:{i:1003;s:1256:\"<pre><span style=\"font-size: small;\"><span><span><strong>走出&ldquo;高原反应&rdquo;怪圈，备战高考，你能行！</strong><br />1.思想负担越来越重，成就感越来越小，如何排除焦虑烦躁？<br />2.高考出题陷阱很多，在分分小命根儿的考场，如何能够拨云见日呢？<br />3.高考不仅是知识的核查期，更是心理的攻坚战，如何掌握应试策略呢？<br /><strong><span style=\"color: #ff0000;\">披星戴月，十年苦读，我们分秒必争！可谓十年磨剑酬壮志，白日攻读铸辉煌！</span></strong><br /><strong>德智课程帮助你</strong><br />1.针对高考常见陷阱全面分析，帮助你拿到试卷一针见血的明确考点，尽量不丢分。<br />2.针对高考应试策略一网打尽，帮助你掌握应试技巧正常甚至超常发挥，尽量得高分。<br />3.知识和心态，两手都要抓两手都要硬，帮助你提前适应考试节奏，保持最佳迎考状态。<br /><span style=\"color: #ff0000;\">温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 2月8日左右上线各科第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</span></span></span><br /></span></pre>\";}', '40.00', '2', '31104000', '49', '3301', null, '', '1357353942', '1357353942', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三复习学生</span></pre>', '<pre><span style=\"font-size: small;\">1.扫清一轮二轮复习的知识遗漏点，找准自己复习的薄弱点，做好复习的最后收尾！<br />2.掌握高考常见陷阱以及应试策略，做到会做的绝对不丢分，不会做的尽量多得分！<br />3.适应冲刺阶段复习节奏，不烦不燥胸有成竹，以最佳状态迎考！</span></pre>\r\n<p align=\"left\">&nbsp;</p>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14387', '高三理科数学三轮复习课程', '3', '', '', '', '/data/default/kecheng/a41/e8e/madd2gjako2fh94e3la7s86sm41357524719.jpg', 'a:0:{}', 'a:1:{i:1002;s:1117:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考必考点及其解题策略，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.分析选择题常用解题技巧，帮助你掌握应试技巧，快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！<br /><br /><strong>温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</strong><br /></span></pre>\";}', '100.00', '5', '31104000', '43', '3302', null, '', '1357354559', '1357354559', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.了解高考，熟悉高考，掌握必备的应试策略，快速高效答题，做到考试尽量不丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握必备解题方法，做到胸有成竹，以最佳状态迎考！</span></pre>\r\n<pre>&nbsp;</pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14388', '高三文科数学三轮复习课程', '3', '', '', '', '/data/default/kecheng/a41/e8e/madd2gjako2fh94e3la7s86sm41357524739.jpg', 'a:0:{}', 'a:1:{i:1002;s:1120:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考必考点及其解题策略，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.分析选择题的常用解题技巧，帮助你掌握应试技巧，快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！<br /><br /><strong>温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</strong><br /></span></pre>\";}', '100.00', '5', '31104000', '37', '3303', null, '', '1357354627', '1357354627', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三文科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.了解高考，熟悉高考，掌握必备的应试策略，快速高效答题，做到考试尽量不丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\"><span style=\"font-size: small;\">2.掌握必备解题方法，做到胸有成竹，以最佳状态迎考！</span></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14389', '高三物理三轮复习', '3', '', '', '', '/data/default/kecheng/a41/e8e/312algri0fr81qsn7mca4mvk411357524648.jpg', 'a:0:{}', 'a:1:{i:1004;s:817:\"<p><strong>三轮复习的你</strong><br />1.临近高考，物理复习压力特别大，感觉很多题都不会做。<br />2.已经失去复习方向，不知道该抓哪部分，完全乱了分寸。<br />3.对高考综合题，尤其是压轴题，解答思路不清晰或者写不对得分点。<strong></strong><br /><strong>德智课程帮助你</strong><br />1.集中高效复习：针对高考占分比重最大的力学和电学，精准辅导你如何进行高中最后的冲刺复习。<br />2.在整合力学和电学重要规律的基础上，通过高考真题的讲解，教你如何分析压轴题，从而稳步得高分。</p>\r\n<p><strong>温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程陆续发布，每周上线1-2节，敬请期待！</strong></p>\";}', '80.00', '4', '31104000', '52', '3302', null, '', '1357354895', '1357354895', '0', '', '<pre><span style=\"font-size: small;\">高三理科学生</span></pre>', '<span style=\"font-size: small;\">1.理清力学和电学的高考重、难点，难度一般性题目争取不丢分。</span><br /><span style=\"font-size: small;\">2.学会分析复杂的综合性题目，简化问题，精准解答，冲刺高分。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14390', '高三历史三轮复习课程', '3', '', '', '', '/data/default/kecheng/a41/e8e/madd2gjako2fh94e3la7s86sm41357524774.jpg', 'a:0:{}', 'a:1:{i:1007;s:757:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考解题思路与方法，<span style=\"font-size: small;\">了解命题趋势，掌握答题方法。</span></span></pre>\r\n<pre><span style=\"font-size: small;\">2.各种应试策略，一网打尽，助你在考场中一往无前！</span></pre>\";}', '20.00', '1', '31104000', '30', '3303', null, '', '1357354937', '1357354937', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三复习学生</span></pre>', '<pre><span style=\"font-size: small;\">1.掌握高考常见题型及解题方法，做到知彼知己。<br />2.适应冲刺阶段复习节奏，以最佳状态迎接高考！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14391', '高三生物三轮复习', '3', '', '', '', '/data/default/kecheng/a41/e8e/312algri0fr81qsn7mca4mvk411357524681.jpg', 'a:0:{}', 'a:1:{i:1006;s:682:\"<p><strong>三轮复习的你</strong><br />1.高考迫在眉睫，要复习的知识特别多，感觉抓不住重点。<br />2.很多题目看着眼熟，要么不知从哪个角度解答，要么解答出来得不到满分。<br /><strong>德智课程帮助你</strong><br />1.利用短短一节课时间来强化重点、难点、易错点等内容，并结合具体高考实例，综合提升分析解题能力。<br />2.针对近几年各类型高考题，给出针对性的应试技巧，有效提升考场的得分率。</p>\r\n<p><strong>温馨提示：老师正在努力狂刷最新考试信息和试题中，高考三轮课程将于2月上线，敬请期待！</strong></p>\";}', '20.00', '1', '31104000', '31', '3302', null, '', '1357355272', '1357355272', '0', '', '<pre><span style=\"font-size: small;\">高三理科学生</span></pre>', '<span style=\"font-size: small;\">1.使你在紧张备考中不迷失方向，能够有步骤有方法地迎战高考。</span><br /><span style=\"font-size: small;\">2.对综合性问题的解决有了自己的科学思路与答题策略，减少失分。</span>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14392', '高三语文三轮复习课程', '3', '', '', '', '/data/default/kecheng/a41/e8e/1tk3j0bcvd00tcccoet64852871357524799.jpg', 'a:0:{}', 'a:1:{i:1001;s:1515:\"<pre><span style=\"font-size: small;\"><strong>正处于三轮冲刺阶段的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">&ldquo;时间紧，任务重&rdquo;，发现知识还有遗漏？短时间内哪项知识提升快？基础知识，古诗文，现代文阅读，还是作文？如何纠错、查缺补漏，如何快速提分？突击弱项，快速提分，才是备考硬道理！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">针对三轮复习中你可能遇到的问题，我们为你做了如下安排：</span></pre>\r\n<pre><span style=\"font-size: small;\">以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>给你的复习建议：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.第三轮复习阶段，你的主要任务是：通过&ldquo;查漏补缺&rdquo;，集中突破弱项。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.不迷信模拟题，不做偏题、怪题，反复揣摩一道好题。</span></pre>\";}', '80.00', '4', '31104000', '41', '3301', null, '', '1357355926', '1357355926', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三年级备战高考的学生</span></pre>', '<pre><span style=\"font-size: small;\">1.查缺补漏，突破弱项。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在最短时间内，使作文分数得到最大提高！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14393', '高三化学三轮复习课程', '3', '提炼技巧方法，高效备考', '北京高级教师', '', '/data/default/kecheng/1b2/f16/njruua6umr99vlloth0vkagej21357357339.jpg', 'a:0:{}', 'a:1:{i:1005;s:1524:\"<pre><span style=\"font-size: small;\"><strong>进入高考冲刺阶段的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.心情毛躁，急于求成，不注重方法策略的运用，导致复习效果不佳。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.面对高考，心惊胆战，原本清楚掌握的知识由于过于紧张，发挥不正常。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span><span style=\"font-size: small;\"><br /><span style=\"color: #ff0000; font-size: medium;\"><strong><span style=\"font-size: small;\">温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</span></strong></span></span></pre>\";}', '60.00', '3', '31104000', '43', '3302', null, '', '1357357343', '1357357343', '0', '高考冲刺理科学生', '<pre><span style=\"font-size: small;\">全国各版本高三理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.难题、综合性大题，全面网罗，突破难关，轻松得分！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明确高考考向，掌握各类题型的解题方法技巧，使题目由难变易，轻松解题！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟练运用高考应试技巧，掌握备考策略，信心十足应对高考！</span></pre>\r\n<pre><span style=\"font-size: small;\">&nbsp;</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14394', '高考地理三轮复习课程', '3', '提炼答题策略，高效冲刺备考', '北京高级教师', '', '/data/default/kecheng/1b2/f16/njruua6umr99vlloth0vkagej21357357837.jpg', 'a:0:{}', 'a:1:{i:1008;s:1445:\"<pre><span style=\"font-size: small;\"><strong>进入高考冲刺阶段的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.心情毛躁，急于求成，不注重方法策略的运用，导致复习效果不佳。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.面对高考，心惊胆战，原本清楚掌握的知识由于过于紧张，发挥不正常。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span><br /><span style=\"color: #ff0000; font-size: small;\"><strong>温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</strong></span></pre>\";}', '40.00', '2', '31104000', '33', '3303', null, '', '1357357842', '1357357842', '0', '高三文科学生', '<pre><span style=\"font-size: small;\">全国各版本高三文科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.紧密联系时政热点，掌握学科技能培养综合能力，解读考纲精神领悟高考命题趋势，科学提示方法，拓宽知识面。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.明确高考考向，掌握各类题型的解题方法技巧，融会贯通，最终形成&ldquo;能力网&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.熟练运用高考应试技巧，掌握备考策略，信心十足应对高考！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14395', '高三理科三轮复习课程', '3', '', '', '', '/data/default/kecheng/1b2/f16/l9g38ji475hc1rfi0ai004r2c11357367484.png', 'a:0:{}', 'a:6:{i:1001;s:1515:\"<pre><span style=\"font-size: small;\"><strong>正处于三轮冲刺阶段的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">&ldquo;时间紧，任务重&rdquo;，发现知识还有遗漏？短时间内哪项知识提升快？基础知识，古诗文，现代文阅读，还是作文？如何纠错、查缺补漏，如何快速提分？突击弱项，快速提分，才是备考硬道理！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">针对三轮复习中你可能遇到的问题，我们为你做了如下安排：</span></pre>\r\n<pre><span style=\"font-size: small;\">以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>给你的复习建议：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.第三轮复习阶段，你的主要任务是：通过&ldquo;查漏补缺&rdquo;，集中突破弱项。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.不迷信模拟题，不做偏题、怪题，反复揣摩一道好题。</span></pre>\";i:1002;s:895:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考必考点及其解题策略，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.分析选择题的常用解题技巧，帮助你掌握应试技巧，快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！</span></pre>\";i:1003;s:1026:\"<pre><span style=\"font-size: small;\"><strong>走出&ldquo;高原反应&rdquo;怪圈，备战高考，你能行！</strong><br />1.思想负担越来越重，成就感越来越小，如何排除焦虑烦躁？<br />2.高考出题陷阱很多，在分分小命根儿的考场，如何能够拨云见日呢？<br />3.高考不仅是知识的核查期，更是心理的攻坚战，如何掌握应试策略呢？</span><span style=\"font-size: small; color: #ff0000;\"><br />披星戴月，十年苦读，我们分秒必争！可谓十年磨剑酬壮志，白日攻读铸辉煌！</span><span style=\"font-size: small;\"><strong><br />德智课程帮助你</strong><br />1.针对高考常见陷阱全面分析，帮助你拿到试卷一针见血的明确考点，尽量不丢分。<br />2.针对高考应试策略一网打尽，帮助你掌握应试技巧正常甚至超常发挥，尽量得高分。<br />3.知识和心态，两手都要抓两手都要硬，帮助你提前适应考试节奏，保持最佳迎考状态。</span></pre>\";i:1004;s:913:\"<span style=\"font-size: small;\"><strong>三轮复习的你</strong></span><br /><span style=\"font-size: small;\">1.临近高考，物理复习压力特别大，感觉很多题都不会做。</span><br /><span style=\"font-size: small;\">2.已经失去复习方向，不知道该抓哪部分，完全乱了分寸。</span><br /><span style=\"font-size: small;\">3.对高考综合题，尤其是压轴题，解答思路不清晰或者写不对得分点。<strong></strong></span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.集中高效复习：针对高考占分比重最大的力学和电学，精准辅导你如何进行高中最后的冲刺复习。</span><br /><span style=\"font-size: small;\">2.在整合力学和电学重要规律的基础上，通过高考真题的讲解，教你如何分析压轴题，从而稳步得高分。</span>\";i:1005;s:1177:\"<pre><span style=\"font-size: small;\"><strong>进入高考冲刺阶段的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.心情毛躁，急于求成，不注重方法策略的运用，导致复习效果不佳。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.面对高考，心惊胆战，原本清楚掌握的知识由于过于紧张，发挥不正常。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span></pre>\";i:1006;s:750:\"<span style=\"font-size: small;\"><strong>三轮复习的你</strong></span><br /><span style=\"font-size: small;\">1.高考迫在眉睫，要复习的知识特别多，感觉抓不住重点。</span><br /><span style=\"font-size: small;\">2.很多题目看着眼熟，要么不知从哪个角度解答，要么解答出来得不到满分。</span><br /><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span><br /><span style=\"font-size: small;\">1.利用短短一节课时间来强化重点难点、易错点等内容，并结合具体高考实例，综合提升解题能力。</span><br /><span style=\"font-size: small;\">2.针对近几年各类型高考题，给出针对性的应试技巧，有效提升考场的得分率。</span>\";}', '380.00', '19', '31104000', '99', '3302', null, '', '1357367389', '1357367389', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.扫清一轮二轮复习的知识遗漏点，找准自己复习的薄弱点，做好复习的最后收尾！<br />2.掌握高考常见陷阱以及应试策略，做到会做的绝对不丢分，不会做的尽量多得分！<br />3.适应冲刺阶段复习节奏，不烦不燥胸有成竹，以最佳状态迎考！<br /><span style=\"color: #ff0000;\">温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线各科第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</span><br /></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14396', '高三一轮二轮三轮理科联报课程', '4', '', '', '', '/data/default/kecheng/1b2/f16/n0n7qifmbc1jdbad8fkcgm6n221357367578.jpg', 'a:0:{}', 'a:6:{i:1001;s:2268:\"<pre><span style=\"font-size: small;\"><strong>正在高考复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">面对高考复习，你是不是觉得复习没有头绪，无从下手？是不是还苦苦挣扎于&ldquo;文山题海&rdquo;？知道在考场上阅读、作文，第一步要做什么吗？发现知识还有遗漏？短时间内哪项知识提升快？基础知识，古诗文，现代文阅读，还是作文？如何纠错、查缺补漏，如何快速提分？&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.安排系统、全面的课程，帮你制定复习计划。一轮复习课程，从基础知识、阅读理解、写作技巧等方面着手，覆盖了语文高考的全部的复习内容，帮你全面复习，肃清&ldquo;死角&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.抓住最重要的。二轮课程从古诗文阅读、现代文阅读和写作三个方面设置，针对高考的分值比例和同学学习的难易程度，带领大家扫清难点，直奔重点。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.设置考场情境。模拟考场情境，面对一篇文章，一个作文题目，教你第一步该做什么，第二步该如何等等，按部就班，直到最后得出答案，引导你的思维预先感知考场。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.三轮复习以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong style=\"font-size: small;\">你的精彩课堂</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><strong style=\"font-size: small;\"><img src=\"/data/default/upload/site/999/600/2390/2012/12/13/i5grhkn445siglitkftcadb3441355379314.jpg\" alt=\"\" /></strong></span></pre>\";i:1002;s:2301:\"<pre><span style=\"font-size: small;\"><strong>高三复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.知识点多而杂，像在学新课，但复习速度又快，往往重点没落实，细节容易忽略。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.面对数学问题时不知道该如何进行思考，如何寻找解决问题的途径和方法；不会总结规律，做了很多题，成绩提高却不明显。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.题海茫茫，做了很多题，遇到同类的题还是出错。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.时间紧迫，高中数学知识繁杂，不知道如何高效的复习。<br /></span></pre>\r\n<pre><span style=\"font-size: small;\">5.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考必考点及其解题策略，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.理清知识脉络：针对数学知识多且杂的特点，课程中每个专题都对本模块知识进行梳理和强化，让学生对本专题的知识有个清晰的认识，形成自己的知识网络。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.一题多解，一题多变，总结规律，教会你通过一道题掌握一类题，学会研究问题的方法，提高解题能力，从而脱离茫茫题海。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.分析选择题和填空题的常用解题技巧，帮助你掌握应试技巧，快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691105.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691125.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691141.png\" alt=\"\" />\";i:1003;s:1724:\"<pre><span style=\"font-size: small;\"><strong>进入高三复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.不能合理利用零散时间，构建系统的知识网络，制定合理的复习方案。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.句型、固定搭配、习惯用法、语法等方面的基础知识薄弱，单选经常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.主旨概括不全、文章结构不清、句子翻译不准等使阅读写作成为最大难题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.高考出题规律莫测，无法掌握应试策略与技巧，越来越焦躁不安。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.夯实整个高三复习的基础，对重点难点知识地毯式搜索讲解，全面复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.根据精选的必考、占分比高、及高频考点内容点睛讲解，精准复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.综合复习，压轴讲解，短平快的复习方式，节省宝贵时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.高考陷阱全面分析，应试策略一网打尽，帮助你保持最佳迎考状态。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/formula/b44/050/2188/2013/01/06/1j6fn2jkd20ab6katod68lvu611357465893.png\" alt=\"\" /><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/b44/050/2188/2013/01/06/1j6fn2jkd20ab6katod68lvu611357465913.png\" alt=\"\" /><br /></strong></span>\";i:1004;s:1686:\"<pre><span style=\"font-size: small;\"><strong>高三复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.老师复习节奏快，没有自己整合消化的时间，感觉很焦虑。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对综合问题思维打不开、只会套用公式，不会灵活变形求解。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对各种规律与现代生产生活实际相结合的问题，找不到思路。对运动学与功能原理结合类问题不能快速找到突破口。对带电粒子在复合场中的运动问题思路不够清晰。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.看到高考综合题恐惧，没有信心解答。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.精选例题让你掌握各种解题方法：图像法、比例法、极值法、微元法、模型法等。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在有限时间内掌握复杂运动与力学规律综合问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.找准电磁场中运动问题的规律，节约时间，事半功倍。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.应试技巧帮你克服&ldquo;压轴题恐惧&rdquo;心理，稳步解题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691426.png\" alt=\"\" /><br /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691442.png\" alt=\"\" />\";i:1005;s:2214:\"<pre><span style=\"font-size: small;\"><strong>高三复习的你</strong></span><br /><span style=\"font-size: small;\">1.时间紧，任务重，主次混淆不清，顾此失彼，复习效率低。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.知识零散，涉及面广，不能系统复习；忽视规律的总结，陷入题海，前做后忘，费时费力没有成效。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.基础知识基本掌握，可是遇到过难过深或者综合性大题有时候还是一筹莫展。高中化学单知识点掌握熟练，综合知识应用时缺少联想、延伸和扩展。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.面对高考，心惊胆战，原本清楚掌握的知识由于过于紧张，发挥不正常。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结常考点，揭秘易错点，形成知识网络，全面掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.精析解题思路和方法，总结规律、技巧、考试策略，举一反三，触类旁通。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.从最重要的综合题型出发帮你整体把握综合知识，熟练综合大题的运用。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">6.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691535.png\" alt=\"\" />\";i:1006;s:1462:\"<pre><span style=\"font-size: small;\"><strong>高三复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.感觉内容繁杂，需要记忆的知识太多，感觉需要掌握的知识很多，复习抓不住重点，不知如何复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对综合性考题往往不知从哪个角度解答。想多做一些训练题，却没有太多时间，焦头烂额。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.在巩固知识的基础上进行前后综合联系，顺利实现知识的迁移和应用。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.高度浓缩高考生物重难点精华部分，分成实验设计题、遗传变异题、生物技术题三大内容来复习，更加高效。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.从高考要求、典型例题分析、知识与方法、解题要领等方面来全方位诠释高考的命题规律及你的思维盲点，帮你逐项突破高考生物题的各大热点及难点内容。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691604.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354691622.png\" alt=\"\" />\";}', '2780.00', '139', '31104000', '203', '3302', null, '', '1357367504', '1357367504', '1', '', '<pre><span style=\"font-size: small;\">全国各版本高三理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.全面的复习计划，让知识没有遗漏。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.在最短的时间内容复习最重要的内容，省时高效。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.提前熟悉高考试题的考查形式，预知考试情境与答题策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.总结常考点，揭秘易错点，形成知识网络，全面掌握高考。</span><br /><span style=\"font-size: small;\">5.查缺补漏，突破弱项。在最短时间内，使学习成绩得到最大提高！<br /><br /><strong></strong><span style=\"color: #ff0000;\">温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线各科第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</span></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14397', '高三文科三轮复习课程', '3', '', '', '', '/data/default/kecheng/1b2/f16/l9g38ji475hc1rfi0ai004r2c11357367921.png', 'a:0:{}', 'a:6:{i:1001;s:1528:\"<pre><span style=\"font-size: small;\"><strong>正处于三轮冲刺阶段的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">&ldquo;时间紧，任务重&rdquo;，发现知识还有遗漏？短时间内哪项知识提升快？基础知识，古诗文，现代文阅读，还是作文？如何纠错、查缺补漏，如何快速提分？突击弱项，快速提分，才是硬道理！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">针对三轮复习中你可能遇到的问题，我们为你做了如下安排：</span></pre>\r\n<pre><span style=\"font-size: small;\">以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>给你的复习建议：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.第三轮复习阶段，你的主要任务是：通过&ldquo;查漏补缺&rdquo;，集中突破弱项。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.不迷信模拟题，不做偏题、怪题，反复揣摩一道好题。</span></pre>\r\n<pre>&nbsp;</pre>\";i:1002;s:895:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考必考点及其解题策略，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.分析选择题的常用解题技巧，帮助你掌握应试技巧，快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！</span></pre>\";i:1003;s:1026:\"<pre><span style=\"font-size: small;\"><strong>走出&ldquo;高原反应&rdquo;怪圈，备战高考，你能行！</strong><br />1.思想负担越来越重，成就感越来越小，如何排除焦虑烦躁？<br />2.高考出题陷阱很多，在分分小命根儿的考场，如何能够拨云见日呢？<br />3.高考不仅是知识的核查期，更是心理的攻坚战，如何掌握应试策略呢？</span><span style=\"font-size: small; color: #ff0000;\"><br />披星戴月，十年苦读，我们分秒必争！可谓十年磨剑酬壮志，白日攻读铸辉煌！</span><span style=\"font-size: small;\"><strong><br />德智课程帮助你</strong><br />1.针对高考常见陷阱全面分析，帮助你拿到试卷一针见血的明确考点，尽量不丢分。<br />2.针对高考应试策略一网打尽，帮助你掌握应试技巧正常甚至超常发挥，尽量得高分。<br />3.知识和心态，两手都要抓两手都要硬，帮助你提前适应考试节奏，保持最佳迎考状态。</span></pre>\";i:1007;s:757:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.解读高考解题思路与方法，<span style=\"font-size: small;\">了解命题趋势，掌握答题方法。</span></span></pre>\r\n<pre><span style=\"font-size: small;\">2.各种应试策略，一网打尽，助你在考场中一往无前！</span></pre>\";i:1008;s:1177:\"<pre><span style=\"font-size: small;\"><strong>进入高考冲刺阶段的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.心情毛躁，急于求成，不注重方法策略的运用，导致复习效果不佳。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.面对高考，心惊胆战，原本清楚掌握的知识由于过于紧张，发挥不正常。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span></pre>\";i:1009;s:967:\"<pre><strong><span style=\"font-size: small;\">高三的你</span></strong></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.高考战争已经打响，你是否弹药充足？</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.蓄势待发的你，是否已经找到赢取胜利的捷径？</span></pre>\r\n<pre><strong><span style=\"font-size: small;\">德智课程帮助你</span></strong></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.从经济、政治、文化三方面剖析高考政治考点，教你针对每个模块该如何作答，才能抓取得分点！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.传授考试答题策略，教你快捷有效答题，赢取考场时间！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.面对陌生题，教你魔法，化陌生为熟悉，还原成你复习过的题目，答题有保障！</span></pre>\";}', '320.00', '16', '31104000', '58', '3303', null, '', '1357367929', '1357367929', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三文科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.扫清一轮二轮复习的知识遗漏点，找准自己复习的薄弱点，做好复习的最后收尾！<br />2.掌握高考常见陷阱以及应试策略，做到会做的绝对不丢分，不会做的尽量多得分！<br />3.适应冲刺阶段复习节奏，不烦不燥胸有成竹，以最佳状态迎考！<br /><span style=\"color: #ff0000;\">温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线各科第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</span><br /></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14398', '高三二轮三轮理科联报课程', '3', '掌握技巧策略，充分备战高考', '', '', '/data/default/kecheng/1b2/f16/njruua6umr99vlloth0vkagej21357368628.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/1b2/f16/njruua6umr99vlloth0vkagej21357371618.jpg\";i:1;s:70:\"/data/default/kecheng/1b2/f16/njruua6umr99vlloth0vkagej21357371633.jpg\";i:2;s:70:\"/data/default/kecheng/1b2/f16/njruua6umr99vlloth0vkagej21357371652.jpg\";}', 'a:6:{i:1001;s:2633:\"<pre><span style=\"font-size: small;\"><strong>正在高考复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">离高考越来越近了，时间紧，任务重，这时候你还在&ldquo;文山题海&rdquo;中挣扎吗？知道在考场上阅读、作文，第一步要做什么吗？发现知识还有遗漏？短时间内哪项知识提升快？基础知识，古诗文，</span></pre>\r\n<pre><span style=\"font-size: small;\">现代文阅读，还是作文？如何纠错、查缺补漏，如何快速提分？&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.抓住最重要的。本次课程从古诗文阅读、现代文阅读和写作三个方面设置课程，针对高考的分值比例和同学学习的难易程度，带领大家扫清难点，直奔重点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.设置考场情境。模拟考场情境，面对一篇文章，一个作文题目，教你第一步该做什么，第二步该如何等等，按部就班，直到最后得出答案，引导你的思维预先感知考场。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.三轮复习以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre><strong style=\"font-size: small;\">你的学习收获</strong></pre>\r\n<pre><span style=\"font-size: small;\">1.在最短的时间内复习最重要的内容，省时高效。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.提前熟悉高考语文的考查形式，预知考试情境与答题策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.查缺补漏，突破弱项。在最短时间内，使作文分数得到最大提高！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/08r8j4el8qubd464bfdtcd8p721355221120.png\" alt=\"\" /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/08r8j4el8qubd464bfdtcd8p721355221154.png\" alt=\"\" /><img src=\"/data/default/upload/formula/54b/530/system/2012/12/11/08r8j4el8qubd464bfdtcd8p721355221190.png\" alt=\"\" /></strong></span></pre>\r\n<pre>&nbsp;</pre>\r\n<pre>&nbsp;</pre>\";i:1002;s:1543:\"<pre><span style=\"font-size: small;\"><strong>正在高考复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高三复习千头万绪，不知道该如何有效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.时间紧迫，高中数学知识繁杂，不知道如何高效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.题海茫茫，做了很多题，遇到同类的题还是出错。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.明确高考数学的重点、难点和热点，从而使复习更有针对性，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.一题多解，一题多变，总结规律，教会你通过一道题掌握一类题，学会研究问题的方法，提高解题能力，从而脱离茫茫题海。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.考试应试技巧一网打尽，帮助你在考试时快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690483.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690509.png\" alt=\"\" />\";i:1003;s:1496:\"<pre><span style=\"font-size: small;\"><strong>进入冲刺阶段的你 </strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.句型、固定搭配、习惯用法、语法等方面的基础知识薄弱，单选经常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.主旨概括不全、文章结构不清、句子翻译不准等使阅读写作成为最大难题。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.高考出题规律莫测，无法掌握应试策略与技巧，越来越焦躁不安。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.综合复习，压轴讲解，提升应试能力，全面备考。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.查漏补缺，针对考生的薄弱点，对症下药，精准复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.短平快的复习方式，节省宝贵时间，提高复习效率。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.高考陷阱全面分析，应试策略一网打尽，帮助你保持最佳迎考状态。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<span style=\"font-size: small;\"><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690573.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690592.png\" alt=\"\" /></span>\";i:1004;s:1230:\"<pre><span style=\"font-size: small;\"><strong>即将面临高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.对运动学与功能原理结合类问题不能快速找到突破口。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.对带电粒子在复合场中的运动问题思路不够清晰。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.看到高考综合题恐惧，没有信心解答。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.在有限时间内掌握复杂运动与力学规律综合问题。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.找准电磁场中运动问题的规律，节约时间，事半功倍。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.应试技巧帮你克服&ldquo;压轴题恐惧&rdquo;心理，稳步解题。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂<br /></strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690681.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690696.png\" alt=\"\" />\";i:1005;s:2005:\"<pre><span style=\"font-size: small;\"><strong>即将面临高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考日益逼近，做题模拟考不见太大成效。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.基础知识基本掌握，可是遇到过难过深或者综合性大题有时候还是一筹莫展。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.心情毛躁，急于求成，不注重方法策略的运用，导致复习效果不佳。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">5.面对高考，心惊胆战，原本清楚掌握的知识由于过于紧张，发挥不正常。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong><strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.从最重要的综合题型出发帮你整体把握综合知识，熟练综合大题的运用。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.易错点、考试陷阱、难点熟练、延伸拓展，帮你全面网罗高考备考策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/cb2/c20/2414/2013/01/05/njruua6umr99vlloth0vkagej21357369813.jpg\" alt=\"\" /></span></pre>\";i:1006;s:1189:\"<pre><span style=\"font-size: small;\"><strong>即将面临高考的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.感觉需要掌握的知识很多，复习抓不住重点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.想多做一些训练题，却没有太多时间，焦头烂额。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高度浓缩高考生物重难点精华部分，分成实验设计题、遗传变异题、生物技术题三大内容来复习，更加高效。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.从高考要求、典型例题分析、知识与方法、解题要领等方面来全方位诠释高考的命题规律及你的思维盲点，帮你逐项突破高考生物题的各大热点及难点内容。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂</strong></span></pre>\r\n<img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690856.png\" alt=\"\" /><img src=\"/data/default/upload/site/54b/530/system/2012/12/05/b4813b33i4pjcnikms0j3bg7211354690876.png\" alt=\"\" />\";}', '1160.00', '58', '31104000', '242', '3302', null, '', '1357368305', '1357368305', '1', '高三理科学生', '<pre><span style=\"font-size: small;\">全国各版本高三理科学生</span></pre>', '<pre><span style=\"font-size: small;\">1.对考查频率高的综合性问题掌握一定的方法要领，达到熟练运用。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握选择题出题规律和解题技巧，区分日常易错易混点，提高选择题得分率。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.难题、综合性大题，全面网罗，突破难关，轻松得分！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.明确高考考向，掌握各类题型的解题方法技巧，使题目由难变易，轻松解题！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.熟练运用高考应试技巧，掌握备考策略，信心十足应对高考！</span>&nbsp;</pre>\r\n<pre><span style=\"font-size: small;\">6.查缺补漏，突破弱项。在最短时间内，使作文分数得到最大提高！</span><span style=\"font-size: small;\"><br /><span style=\"color: #ff0000;\"><strong>温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线各科第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</strong></span></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14399', '高三政治三轮复习课程', '3', '', '', '', '/data/default/kecheng/a41/e8e/cmm6jsh1q2fqg4ppdlm2m4voh11357526430.jpg', 'a:0:{}', 'a:1:{i:1009;s:981:\"<pre><span style=\"font-family: %value; font-size: small;\">高三的你：</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.高考战争已经打响，你是否弹药充足？</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.蓄势待发的你，是否已经找到赢取胜利的捷径？</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">德智课程帮助你：</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">1.从经济、政治、文化三方面剖析高考政治考点，教你针对每个模块该如何作答，才能抓取得分点！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.传授考试答题策略，教你快捷有效答题，赢取考场时间！</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">3.面对陌生题，教你魔法，化陌生为熟悉，还原成你复习过的题目，答题有保障！</span></pre>\";}', '40.00', '2', '31104000', '24', '3303', null, '', '1357368728', '1357368728', '0', '', '<pre><span style=\"font-size: small;\">全国各版本高三学生</span></pre>', '<pre><span style=\"font-family: %value; font-size: small;\">1.在短短的时间里，对高考政治的考查方向更加的明确、清晰。</span></pre>\r\n<pre><span style=\"font-family: %value; font-size: small;\">2.快速提升答题能力，有效快捷答题。</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14400', '高三二轮三轮文科联报课程', '3', '二轮三轮双提升，助战文科高考！', '', '', '/data/default/kecheng/1b2/f16/96cm6i44egh20477q8re5k53951357370847.jpg', 'a:3:{i:0;s:70:\"/data/default/kecheng/a41/e8e/1tk3j0bcvd00tcccoet64852871357547248.jpg\";i:1;s:70:\"/data/default/kecheng/a41/e8e/1tk3j0bcvd00tcccoet64852871357547254.jpg\";i:2;s:70:\"/data/default/kecheng/a41/e8e/1tk3j0bcvd00tcccoet64852871357547359.jpg\";}', 'a:6:{i:1001;s:2006:\"<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.抓住最重要的。本次课程从古诗文阅读、现代文阅读和写作三个方面设置课程，针对高考的分值比例和同学学习的难易程度，带领大家扫清难点，直奔重点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.设置考场情境。模拟考场情境，面对一篇文章，一个作文题目，教你第一步该做什么，第二步该如何等等，按部就班，直到最后得出答案，引导你的思维预先感知考场。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.三轮复习以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong style=\"font-size: small;\">你的学习收获：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.在最短的时间内容复习最重要的内容，省时高效。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.提前熟悉高考语文的考查形式，预知考试情境与答题策略。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.查缺补漏，突破弱项。在最短时间内，使作文分数得到最大提高！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\"><img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357461641.jpg\" alt=\"\" /><img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357461657.jpg\" alt=\"\" /></span></pre>\";i:1002;s:1942:\"<pre><span style=\"font-size: small;\"><strong>正在高考复习的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高三复习千头万绪，不知道该如何有效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.时间紧迫，高中数学知识繁杂，不知道如何高效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.题海茫茫，做了很多题，遇到同类的题还是出错。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.明确高考数学的重点、难点和热点，从而使复习更有针对性，让你知彼知己，百战百胜！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.一题多解，一题多变，总结规律，教会你通过一道题掌握一类题，学会研究问题的方法，提高解题能力，从而脱离茫茫题海。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.精析数学思想方法，帮助你掌握数学的精髓，在考场中一往无前！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.考试应试技巧一网打尽，帮助你在考试时快速解题，节省宝贵时间！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>课程学习效果：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.了解高考，熟悉高考，掌握必备的应试策略，快速高效答题，做到考试尽量不丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.掌握数学思想方法，把握数学的灵魂，做到复习有的放矢。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357466472.jpg\" alt=\"\" />\r\n<div><img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357466482.jpg\" alt=\"\" /></div>\";i:1003;s:1474:\"<pre><span style=\"font-size: small;\"><strong>进入冲刺阶段的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.句型、固定搭配、习惯用法、语法等方面的基础知识薄弱，单选经常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.主旨概括不全、文章结构不清、句子翻译不准等使阅读写作成为最大难题。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.高考出题规律莫测，无法掌握应试策略与技巧，越来越焦躁不安。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.综合复习，压轴讲解，提升应试能力，全面备考。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.查漏补缺，针对考生的薄弱点，对症下药，精准复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.短平快的复习方式，节省宝贵时间，提高复习效率。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.高考陷阱全面分析，应试策略一网打尽，帮助你保持最佳迎考状态。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357466316.jpg\" alt=\"\" />\r\n<div><img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357466326.jpg\" alt=\"\" /></div>\";i:1007;s:1508:\"<pre><span style=\"font-size: small;\"><strong>正在备战高考的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.对&ldquo;新材料&rdquo;&ldquo;新情景&rdquo;的试题，不知从何下手突破。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.忽视关键、易错问题总结，思维陷入瓶颈，无法自拔。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.高考迫在眉睫，心理压力越来越大，如何在最后的冲刺阶段最有效的复习？</span></pre>\r\n<pre><span style=\"font-size: small;\">4.如何掌握最有效的解题策略，在最短的时间内快速答题？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.以&ldquo;新材料&rdquo;&ldquo;新情景&rdquo;的创设与运用为主，总结规律，突破高考难点。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.提供详细的复习策略和高考动向，把握历史命题规律，轻松迎战高考。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.解读高考解题思路与方法，了解命题趋势，掌握答题方法。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.各种应试策略，一网打尽，助你在考场中一往无前！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/d49/402/3501/2013/01/06/1hup1ggkudlatlueeedgo8ghk51357466898.jpg\" alt=\"\" />\";i:1008;s:1667:\"<pre><span style=\"font-size: small;\"><strong>高三地理复习的你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.内容繁杂，系统化认识不足，重难点知识把握不准。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.陷入&ldquo;题海&rdquo;，盲目练习，导致综合能力得不到提升。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.心情毛躁，急于求成，不注重方法策略的运用，导致复习效果不佳。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.把握不准高考考向，不能清楚掌握各类题型的应对策略，导致短时间内很难提升。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.对地理知识点进行深入解剖，正确讲解内涵，揭示内在联系，形成系统化、立体化的知识体系。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.巧妙运用解题思路方法、应试技巧，在夯实好基础知识之上，巩固提升能力，复习效率提高。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.总结高考常见题型，准确把握考向，制定针对策略，高效、轻松复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.深度解读学生心理，精准剖析各类题型的解题方法技巧，并给予行之有效的高考应对策略，轻松应对高考，赢得高分！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的精彩课堂：</strong></span></pre>\r\n<img src=\"/data/default/upload/site/cb2/c20/2414/2013/01/06/p9e419ptc23qntmvu8iffchjl11357466613.jpg\" alt=\"\" />\";i:1009;s:1665:\"<pre><span style=\"font-size: small;\"><strong>备战政治高考的你：</strong><strong></strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.高考战争已经打响，你是否弹药充足？面对政治高考的实效性特点，你是否紧跟时事脚步？</span></pre>\r\n<pre><span style=\"font-size: small;\">2.蓄势待发的你，是否已经找到赢取胜利的捷径？随着十八大的完美谢幕，留给你的剩下什么呢？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你：</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.紧紧围绕十八大展开，从&ldquo;未来中国&rdquo;&ldquo;人民主体&rdquo;&ldquo;深化改革&rdquo;三个角度设置课程，带你深入理解十八大精神，将时代元素与政治高考紧密结合。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.带你了解未来中国在经济建设、政治建设、文化建设、社会建设和生态文明建设方面的规划和愿景、明确如何将这些精神融入明年的政治高考中。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.结合十八大精神，在&ldquo;深化改革&rdquo;方面将围绕&ldquo;要不要改&rdquo;&mdash;&mdash;&ldquo;改什么&rdquo;&mdash;&mdash;&ldquo;怎样改&rdquo;展开讲解，以点带面，让你把这方面的高考内容吃深吃透。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.传授考试答题策略，教你快捷有效答题，赢取考场时间！</span></pre>\r\n<pre><span style=\"font-size: small;\">5.面对陌生题，教你魔法，化陌生为熟悉，还原成你复习过的题目，答题有保障！</span></pre>\";}', '920.00', '46', '31104000', '173', '3303', null, '', '1357370420', '1357370420', '1', '全国各版本备战高考的高三学生', '<pre><span style=\"font-size: small;\">全国各版本高三备考学生</span></pre>', '<pre><span style=\"font-size: small;\">1.在最短的时间内容复习最重要的内容，省时高效。 </span></pre>\r\n<pre><span style=\"font-size: small;\">2.了解高考，熟悉高考，掌握必备的应试策略，快速高效答题，做到考试尽量不丢分。 </span></pre>\r\n<pre><span style=\"font-size: small;\">3.高考陷阱全面分析，应试策略一网打尽，帮助你保持最佳迎考状态。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\"><strong>温馨提示：</strong></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">老师们正在努力狂刷最新考试信息和试题，高考三轮课程 1月31日左右上线各科第一讲，其他课程陆续发布，每周上线1～2节，敬请期待！</span></pre>\r\n<pre><span style=\"font-size: small;\"><br /></span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14401', '高三一轮二轮三轮文科联报课程', '4', '', '', '', '/data/default/kecheng/1b2/f16/8doik9bv8os0mo1at169dkjfc31357371183.jpg', 'a:0:{}', 'a:6:{i:1001;s:1940:\"<pre><span style=\"font-size: small;\"><strong>高三复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">面对高考复习，你是不是觉得复习没有头绪，无从下手？是不是还苦苦挣扎于&ldquo;文山题海&rdquo;？知道在考场上阅读、作文，第一步要做什么吗？发现知识还有遗漏？短时间内哪项知识提升快？基础知识，古诗文，现代文阅读，还是作文？如何纠错、查缺补漏，如何快速提分？</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.安排系统、全面的课程，帮你制定复习计划。一轮复习课程，从基础知识、阅读理解、写作技巧等方面着手，覆盖了语文高考全部的复习内容，帮你全面复习，肃清&ldquo;死角&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.抓住最重要的。二轮课程从古诗文阅读、现代文阅读和写作三个方面设置，针对高考的分值比例和同学学习的难易程度，带领大家扫清难点，直奔重点。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.设置考场情境。模拟考场情境，面对一篇文章，一个作文题目，教你第一步该做什么，第二步该如何等，按部就班，直到最后得出答案，引导你的思维预先感知考场。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.三轮复习以&ldquo;查缺补漏&rdquo;为原则，重点针对现代文阅读和作文两大知识点进行提升，这两部分在高考中的所占分值最多，而古诗词和作文的提分速度最快。尤其是作文，占据高考&ldquo;半壁江山&rdquo;。如果在最后冲刺阶段，让你的作文突破50甚至55分，那么你的语文高考便胜券在握了，本次课程的作文&ldquo;三准&rdquo;原则助你一臂之力！</span></pre>\r\n<pre>&nbsp;</pre>\";i:1002;s:1342:\"<strong>作为文科生的你</strong><br />1.时间紧迫，高中数学知识繁杂，作为文科生的你不知道如何高效的复习。<br />2.面对数学难题时不知道该如何进行思考，如何寻找解决问题的途径和方法。<br />3.不会总结规律，做了很多题，成绩提高却不明显。<br /><strong>德智课程帮助你</strong><br />1.提炼六大必考且占分最多的知识模块，突出高考的重点和热点，让你对高考数学有一个清晰的认识，知道哪里是重点，哪里是难点，从而复习时做到心中有数，有的放矢。<br />2.针对数学知识多且杂的特点，每个专题都对本模块知识进行梳理和强化，让你对本专题的知识有个清晰的认识，形成自己的知识网络。<br />3.提炼和总结解题策略，培养逻辑思维能力，达到会解一道题从而会解这一类问题的目的，把你从茫茫题海中解放出来，帮助你在有限的时间内高效复习。<br /><strong><strong>你的精彩课堂</strong></strong><span style=\"font-size: small;\"><strong><br /></strong></span><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463357.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463389.png\" alt=\"\" />\";i:1003;s:2148:\"<p><strong><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">高三复习的你</span></strong><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1.不能够确保做到全面复习，不是覆盖面不全就是一个知识点反复吃不透。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2.不能根据重难点以及分值分布等，科学规划，精准复习。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">3.主旨概括不全、文章结构不清、句子翻译不准等使阅读理解深感迷惑。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">4.遗漏要点、生硬翻译、表达不准确、逻辑思维混乱，写作成绩不上不下。</span><br /><strong><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">德智课程帮助你</span></strong><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">1.夯实整个高三复习的基础，对重点、难点知识地毯式搜索讲解，全面复习。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">2.根据精选的必考、占分比高、及高频考点内容点睛讲解，精准复习。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">3.强化训练文章结构分析及逻辑理解，提高阅读速度和质量。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\">4.写作不再生搬硬套，千篇一律，学会写作思路，提高抒发个人见解的能力。</span><br /><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><strong>你的精彩课堂</strong></strong></span></p>\r\n<p><span style=\"font-size: small; font-family: arial,helvetica,sans-serif;\"><strong><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463509.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463545.png\" alt=\"\" /></strong></span></p>\";i:1007;s:1072:\"<strong>高三复习的你</strong><br />1.高考考查方向把握不准，导致复习针对性不强，对于难点历史知识与现实热点的结合难以突破。<br />2.对&ldquo;新材料&rdquo;、&ldquo;新情景&rdquo;的试题，不知从何下手突破。<br />3.忽视关键、易错问题总结，思维陷入瓶颈，无法自拔。<br /><strong>德智课程帮助你</strong><br />1.从中国古代史到世界现代史，从基础知识到构建体系，全方位复习，牢固掌握。<br />2.以&ldquo;新材料&rdquo;、&ldquo;新情景&rdquo;的创设与运用为主，总结规律，突破高考难点。<br />3.提供详细的复习策略和高考动向，轻松突破历史知识与现实热点问题的结合。<br /><strong>你的精彩课堂</strong>\r\n<p><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463663.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463693.png\" alt=\"\" /></strong></span></p>\";i:1008;s:923:\"<p><strong>高三复习的你</strong><br />1.感觉内容繁杂，系统化认识不足，重难点知识把握不准。<br />2.对于高考考查方向把握不准，导致复习针对性不强，效果不佳。<strong><br /><strong>德智课程帮助你</strong></strong><br />1.对地理知识点进行深入解剖，准确把握考向，做到一次复习，全年受益。<br />2.教材全面整合，形成合理的知识结构和能力结构，把握地理知识和能力的内部联系。<br />3.制定针对策略，高端预测命题，科学提示备考。<strong><br /><strong>你的精彩课堂</strong></strong></p>\r\n<p><span style=\"font-size: small;\"><strong><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463773.png\" alt=\"\" /><img src=\"/data/default/upload/formula/076/828/1021/2013/01/06/ugbctq9r2296k984thrf37c4151357463813.png\" alt=\"\" /></strong></span></p>\";i:1009;s:830:\"<p align=\"left\"><strong>高三复习的你</strong><br />1.面对繁多的知识点，你还在死记硬背么？<strong><br /></strong>2.如何用你所学去分析国内外经济政治热点及社会时事问题？<br />3.了解十八大精神与高考政治的联系吗？<br /><strong>德智课程帮助你</strong><br />1.以新课程的新理念为指导，帮你建立知识体系网，梳理252个知识点之间的联系。<br />2.针对复习中存在的最直接最现实的困惑与问题进行讲解和训练，尤其是对解答题中学生存在的困惑和解决办法，提供最直接的指导。<br />3.围绕十八大从&ldquo;未来中国&rdquo;&ldquo;人民主体&rdquo;&ldquo;深化改革&rdquo;三个角度设置课程，带你深入理解十八大精神，将时代元素与政治高考紧密结合。</p>\";}', '2120.00', '106', '31104000', '135', '3303', null, '', '1357370967', '1357370967', '1', '', '<span style=\"font-size: small;\">全国各版本高三文科学生</span>', '<span style=\"font-size: small;\">1.制定全面科学的复习计划，各科&ldquo;全面撒网&rdquo;，让知识点没有遗漏。</span><br /><span style=\"font-size: small;\">2.提炼关键点，揭秘易错点，轻松避开题目陷阱。</span><br /><span style=\"font-size: small;\">3.模拟高考情境，熟知高考的考查内容及命题规律。</span><br /><span style=\"font-size: small;\">4.学会从题目中提取和解读信息，运用所学知识答题，能有效帮助你提高应考能力。</span><br />\r\n<pre><span style=\"font-size: small;\">温馨提示：老师们正在努力狂刷最新考试信息和试题中，高考三轮课程 1月31日左右上线各科第一讲，其他课程陆续发布，每周上线1-2节，敬请期待！</span></pre>', '3701');
INSERT INTO `kc_course_pack` VALUES ('14402', '中考二轮复习五科联报 ', '3', '把握核心考点，顺利冲刺中考', '北京名校名师会聚', '', '/data/default/kecheng/8bf/1e8/6vru3ide65u60jckoa1pqfvj031357632332.jpg', 'a:0:{}', 'a:5:{i:1001;s:1859:\"<pre><span style=\"font-size: small;\"><strong>正在中考二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">现代文阅读做了一堆，是不是经常有审题不明，答题不准的情况？中考文言文课内与课外各占比例是多少，课内又有哪些是必考篇目？今年中考作文命题形式以哪种为主？材料作文？话题作文？命题作文还是半命题作文？这些作文怎么写能得高分&hellip;&hellip;</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课堂帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">本次语文中考二轮复习课程，我们完全是按照中考语文的考查内容和考查顺序为你设置的。对于中考必考的四大模块：基础知识、综合性学习、现代文阅读和写作，我们从每一模块内容的考查形式入手，归纳考点，并根据考点给你相应的解决方案。既让你提前熟悉中考卷面，又能第一时间把握最新中考动态和命题规律。告诉你中考常见的文言篇目有哪些，现代文阅读如何审题，如何根据作者的写作意图准确答题&hellip;&hellip;这些，让你中考语文复习不再困惑。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.复习巩固基础知识，掌握中考必考的基础考查类型，如重点成语运用，常见病句类型、重点诗句默写等内容。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.了解综合性学习的考查形式及出题规律，社会普遍关注的特点问题，最易成为综合性学习的命题根据。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.从读懂作者的写作意图入手，分析问题，解答题目，不再模棱两可。</span></pre>\";i:1002;s:1330:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.随着中考日益逼近，压力也随之剧增，开始陷入自我焦虑与恐慌之中。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.每天数不清的数学试卷，让你开始厌烦这种无止境的考试&rarr;讲题&rarr;考试循环之中。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.考过同一类型的题，自己还是会出错，老是出现重复性错误。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.在仅剩的日子里不知道自己应该如何复习备战，才能达到高效复习。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.直逼中考重难点内容，挑选中考当中失分率达80%以上的内容进行专题形式的讲解。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.历年中考真题详细剖析，让你知道中考要考你什么。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.让你明确中考当中，你的丢分点，失分点会在哪。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.题目讲解后进行专题总结归纳，帮你指明大体解题方向，总结题型规律。</span></pre>\";i:1003;s:1154:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.语法知识复习的很好，感觉自己什么都掌握了，做题却经常出错，找不到症结所在。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.常用句型、词组、习惯用法等方面的基础知识掌握不牢，单选、完型等题型经常丢分。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.阅读、写作等综合能力考查题型，得分不稳定，忽高忽低，&ldquo;挣分儿&rdquo;题变成了&ldquo;拉分题&rdquo;。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.查漏补缺，识别复习薄弱点，帮助你有针对性的复习，节省宝贵时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.辨别考点陷阱，归纳易错题型，帮助你灵活性的复习，归纳应试技巧。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.单选、完型、阅读、写作全面复习，帮助你系统性的复习，提升综合能力。</span></pre>\";i:1004;s:1405:\"<pre><span style=\"font-size: small;\"><strong>面对中考的压力，你是否会感到以下焦虑</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.面对庞杂的物理知识，不知道哪些是重点，不知道如何有效的复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.虽然选择题与填空题比较简单，但还是会丢分，基础知识掌握不牢！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.对于力学的复杂题目，一看就懵，不知道如何分析，更不知道怎么解题。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.面对众多的电学知识，电功、电功率、欧姆定律、电路故障分析，更不知道如何面对。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.挑选最重点的知识进行复习，用最短的时间，达到最好的效果。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.帮你梳理中考的要求，结合近年的中考真题，细致分析，抓住重点，牢固掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.重点讲解力学中较难的知识，专门解决综合题！</span></pre>\r\n<pre><span style=\"font-size: small;\">4.对电学的重点内容设置了相对较多的课时，重点突破各个重点板块，并配有专题总结分析。</span></pre>\";i:1005;s:1510:\"<pre><span style=\"font-size: small;\"><strong>进入二轮复习的你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.面对中考压力大，不知从何开始复习，如何全面掌握。</span></pre>\r\n<pre><span style=\"font-size: small;\">2.知识零散，涉及面广，不能系统复习。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.时间紧，任务重，不知如何高效复习，精准把握。</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>德智课程帮助你</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.总结中考能力要求，科学提示方法，有效制定策略，轻松全面复习！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.总结常考点，揭秘易错点，以题带点，知识与解题精讲精析，全面突破！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.提炼精华，抓准考试命脉，做到高效、精准复习！</span></pre>\r\n<pre><span style=\"font-size: small;\"><strong>你的学习效果</strong></span></pre>\r\n<pre><span style=\"font-size: small;\">1.明确中考考向，做到复习方向精准，科学有效备考！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.中考五大块内容全面网罗，从身边的化学物质到实验综合题全面详细解析，让你的化学学习由难变易，熟练掌握！</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握各类题型的解题方法、技巧，避开考试陷阱，高效复习，轻松应考！</span></pre>\";}', '900.00', '45', '31104000', '111', '3301', null, '', '1357610689', '1357610689', '1', '即将中考的初中学生', '<pre><span style=\"font-size: small;\">全国各版本初三年级备战中考的学生</span></pre>', '<pre><span style=\"font-size: small;\">1.明确中考考向，做到复习方向精准，科学有效备考！</span></pre>\r\n<pre><span style=\"font-size: small;\">2.查漏补缺，识别复习薄弱点，帮助你有针对性的复习，节省宝贵时间。</span></pre>\r\n<pre><span style=\"font-size: small;\">3.掌握规范的答题步骤，抓住得分点，提高考试成绩。</span></pre>\r\n<pre><span style=\"font-size: small;\">4.积累大量的解题技巧，在考试中能快速解题，提高准确率。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\"><strong><br /></strong></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\"><strong>温馨提示：</strong></span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">1.德智课程质量要求严格，经过多个严谨的制作流程完成，每一流程都要花费很多精力与时间。为保证课程质量并兼顾学习进度，课程将采用陆续更新的方式上线，每周至少上线1～2节课，敬请关注。</span></pre>\r\n<pre><span style=\"font-size: small; color: #ff0000;\">2.课时列表会根据课程标准、考试说明及教材变动等做及时的更新和调整。</span></pre>', '3701');
